UPDATE locales_quest SET offerrewardtext_loc8='You\'ve certainly saved me a heap of trouble, $N! I can\'t thank you enough. It shouldn\'t be long now until we see Master Nesingwary\'s book in libraries throughout the world!' WHERE entry=338;
