UPDATE locales_quest SET offerrewardtext_loc8='$N! You know that Shimmerweed you brought me? Well I used it in a few recipes I\'ve been working on, and one of them turned out great!$B$BHere, try it out and tell me what you think...' WHERE entry=415;
