UPDATE locales_quest SET offerrewardtext_loc8='It sounds from your descriptions as if the creatures show a mindless ferocity...$B$BStay alert, $N. It would be in your best interest to learn more of the corruption. Always know your enemy.' WHERE entry=4120;
