UPDATE locales_quest SET RequestItemsText_loc8='<Bolvar watches you intently.>' WHERE entry=4185;
UPDATE locales_quest SET offerrewardtext_loc8='It could have been worse, I suppose...$B$B<Magistrate Solomon looks you over.>$B$BIt appears as if the fate of Lakeshire rests squarely on your shoulders.$B$B<You notice Magistrate Solomon blinking rapidly. He appears to be crying.>' WHERE entry=4186;
