INSERT INTO `creature_ai_texts` VALUES ('-1415', 'Dieses dämonische Wesen wird Leerwandler genannt. Seine Kraft und Belastbarkeit sind beeindruckend, was ihn ideal für Verteidigungszwecke macht. Schickt ihn aus, um Feinde anzugreifen, und benutzt ihn dann als Schild, während Ihr Eure Zauber und Fähigkeiten einsetzt, um Eurem Gegner Leben zu entziehen.', null, null, 'Dieses dämonische Wesen wird Leerwandler genannt. Seine Kraft und Belastbarkeit sind beeindruckend, was ihn ideal für Verteidigungszwecke macht. Schickt ihn aus, um Feinde anzugreifen, und benutzt ihn dann als Schild, während Ihr Eure Zauber und Fähigkeiten einsetzt, um Eurem Gegner Leben zu entziehen.', null, null, null, null, null, '0', '0', '33', '0', '5702');
INSERT INTO `creature_ai_texts` VALUES ('-1414', 'Now that you have had a chance to study imp, let us move on the next minion you will be able to summon, the voidwalker.', null, null, null, null, null, null, null, null, '0', '0', '33', '0', '5702');
INSERT INTO `creature_ai_texts` VALUES ('-1413', 'Diese üble kleine Kreatur ist ein Wichtel. Er ist klein und schwach, was ihn als lebendigen Schild beinahe unbrauchbar macht, und sein Schadenspotenzial ist bestenfalls mittelmäßig. Diese Kreatur wird am besten als Unterstützung für eine größere Gruppe eingesetzt.', null, null, 'Diese üble kleine Kreatur ist ein Wichtel. Er ist klein und schwach, was ihn als lebendigen Schild beinahe unbrauchbar macht, und sein Schadenspotenzial ist bestenfalls mittelmäßig. Diese Kreatur wird am besten als Unterstützung für eine größere Gruppe eingesetzt.', null, null, null, null, null, '0', '0', '33', '0', '5702');
INSERT INTO `creature_ai_texts` VALUES ('-1412', 'Die einfachste Kreatur zum Beschwören ist der Wichtel. Ihr solltet bereits in der Lage sein, diesen Diener hervorzubringen, aber der Vollständigkeit halber möchte ich mit ihm beginnen.', null, null, 'Die einfachste Kreatur zum Beschwören ist der Wichtel. Ihr solltet bereits in der Lage sein, diesen Diener hervorzubringen, aber der Vollständigkeit halber möchte ich mit ihm beginnen.', null, null, null, null, null, '0', '0', '33', '0', '5702');
INSERT INTO `creature_ai_texts` VALUES ('-1411', 'Eure Anwesenheit hier bedeutet wohl, dass Ihr bereit seid, das Studium des Beschwörens von Dämonenkohorten, die Euch gehorchen, zu beginnen. Wir werden mit der niedersten Kreatur beginnen, die ihr rufen könnt, und uns dann weiter vorarbeiten. Lasst uns beginnen.', null, null, 'Eure Anwesenheit hier bedeutet wohl, dass Ihr bereit seid, das Studium des Beschwörens von Dämonenkohorten, die Euch gehorchen, zu beginnen. Wir werden mit der niedersten Kreatur beginnen, die ihr rufen könnt, und uns dann weiter vorarbeiten. Lasst uns beginnen.', null, null, null, null, null, '0', '0', '33', '0', '5702');
