INSERT INTO `script_texts` VALUES ('-1000001', '%s goes into a killing frenzy!', null, null, '%s wird mörderisch wahnsinnig!', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_FRENZY_KILL');
INSERT INTO `script_texts` VALUES ('-1000002', '%s goes into a frenzy!', null, null, '%s gerät in Raserei!', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES ('-1000003', '%s becomes enraged!', null, null, '%s gerät in Raserei!', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_ENRAGED');
INSERT INTO `script_texts` VALUES ('-1000004', '%s goes into a berserker rage!', null, null, '%s geht in einen Berserkermodus!', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_GENERIC_BERSERK');
INSERT INTO `script_texts` VALUES ('-1000005', '%s goes into a frenzy!', null, null, '%s gerät in Raserei!', null, null, null, null, null, '0', '3', '0', '0', 'EMOTE_BOSS_GENERIC_FRENZY');
INSERT INTO `script_texts` VALUES ('-1000006', '%s becomes enraged!', null, null, '%s gerät in Raserei!', null, null, null, null, null, '0', '3', '0', '0', 'EMOTE_BOSS_GENERIC_ENRAGED');
INSERT INTO `script_texts` VALUES ('-1000100', 'Come, little ones. Face me!', null, null, 'Kommt, ihr Wichtel! Tretet mir gegenüber!', null, null, null, null, null, '0', '1', '0', '0', 'azuregos SAY_TELEPORT');
INSERT INTO `script_texts` VALUES ('-1000101', 'Follow me, $N. I\'ll take you to the Defias hideout. But you better protect me or I am as good as dead', null, null, 'Folgt mir, $N. Ich werde euch zum Versteck der Defias bringen. Aber Ihr beschützt mich besser, sonst bin ich so gut wie tot.', null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_START');
INSERT INTO `script_texts` VALUES ('-1000102', 'The entrance is hidden here in Moonbrook. Keep your eyes peeled for thieves. They want me dead.', null, null, 'Der Eingang ist hier in Mondbruch versteckt. Haltet Eure Augen nach Dieben offen. Sie wollen mich töten.', null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000103', 'You can go tell Stoutmantle this is where the Defias Gang is holed up, $N.', null, null, 'Ihr könnt Starkmantel erzählen, dass dies der Ort ist an dem die sich die Defias Bande aufhält, $N.', null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_END');
INSERT INTO `script_texts` VALUES ('-1000104', '$N coming in fast! Prepare to fight!', null, null, '%s kommt schnell! Bereitet Euch darauf vor, zu kämpfen!', null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000105', 'Help!', null, null, 'Hilfe!', null, null, null, null, null, '0', '0', '7', '0', 'defias traitor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000106', 'Everyone ready?', null, null, 'Alle bereit?', null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_READY');
INSERT INTO `script_texts` VALUES ('-1000107', 'Ok, Lets move out!', null, null, 'Ok, lasst uns losgehen!', null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_MOVE');
INSERT INTO `script_texts` VALUES ('-1000108', 'Prepare yourselves. Silverwing is just around the bend.', null, null, 'Bereitet euch vor. Silberschwinge ist gleich um die Ecke.', null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_PREPARE');
INSERT INTO `script_texts` VALUES ('-1000109', 'Silverwing is ours!', null, null, 'Silberschwinge ist besiegt!', null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_WIN');
INSERT INTO `script_texts` VALUES ('-1000110', 'Go report that the outpost is taken. We will remain here.', null, null, 'Geht. Meldet, dass der Außenposten eingenommen wurde. Wir werden hier bleiben.', null, null, null, null, null, '0', '0', '1', '0', 'torek SAY_END');
INSERT INTO `script_texts` VALUES ('-1000111', 'Our house is this way, through the thicket.', null, null, 'Zu unserem Haus geht es da lang, durch das Dickicht.', null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_START');
INSERT INTO `script_texts` VALUES ('-1000112', 'Help me!', null, null, 'Helft mir!', null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000113', 'My poor family. Everything has been destroyed.', null, null, 'Meine arme Familie. Alles wurde zerstört.', null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000114', 'Father! Father! You\'re alive!', null, null, 'Vater! Vater! Du lebst!', null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_END1');
INSERT INTO `script_texts` VALUES ('-1000115', 'You can thank $N for getting me back here safely, father.', null, null, 'Du kannst $N dafür danken, dass $Ger:sie; mich sicher hier her gebracht hat, Vater.', null, null, null, null, null, '0', '0', '7', '0', 'magwin SAY_END2');
INSERT INTO `script_texts` VALUES ('-1000116', '%s hugs her father.', null, null, '%s umarmt ihren Vater.', null, null, null, null, null, '0', '2', '7', '0', 'magwin EMOTE_HUG');
INSERT INTO `script_texts` VALUES ('-1000117', 'Thank you for agreeing to help. Now, let\'s get out of here $N.', null, null, 'Danke, dass Ihr helfen wollt. Jetzt nichts wie los, $N!', null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_START');
INSERT INTO `script_texts` VALUES ('-1000118', 'Over there! They\'re following us!', null, null, 'Dort drüben! Sie folgen uns!', null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1000119', 'Allow me a moment to rest. The journey taxes what little strength I have.', null, null, 'Erlaubt mir einen Moment zu rasten. Die Reise zehrt sehr an meinen ohnehin schwachen Kräften.', null, null, null, null, null, '0', '0', '1', '16', 'wounded elf SAY_ELF_RESTING');
INSERT INTO `script_texts` VALUES ('-1000120', 'Did you hear something?', null, null, 'Habt Ihr etwas gehört?', null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1000121', 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has -', null, null, 'Endlich, die Falkenwacht! Nun, wo ist mein... Oh nein! Mein Rucksack fehlt! Wo hab ich denn...', null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000122', 'You won\'t keep me from getting to Falcon Watch!', null, null, 'Ihr werdet mich nicht davon abhalten, zur Falkenwacht zu gehen!', null, null, null, null, null, '0', '0', '1', '0', 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000123', 'Ready when you are, $c.', null, null, 'Bereit wenn Ihr es seid, $C.', null, null, null, null, null, '0', '0', '0', '15', 'big will SAY_BIG_WILL_READY');
INSERT INTO `script_texts` VALUES ('-1000124', 'The Affray has begun. $n, get ready to fight!', null, null, 'Die Schlägerei hat begonnen. $N, machen Euch bereit, um zu kämpfen!', null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_BEGIN');
INSERT INTO `script_texts` VALUES ('-1000125', 'You! Enter the fray!', null, null, 'Ihr! Hinein ins Gefecht!', null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_FRAY');
INSERT INTO `script_texts` VALUES ('-1000126', 'Challenger is down!', null, null, 'Der Herausforderer ist besiegt!', null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_DOWN');
INSERT INTO `script_texts` VALUES ('-1000127', 'The Affray is over.', null, null, 'Der Kampf ist vorbei.', null, null, null, null, null, '0', '0', '0', '0', 'twiggy SAY_TWIGGY_OVER');
INSERT INTO `script_texts` VALUES ('-1000128', 'We need you to send reinforcements to Manaforge Duro, Ardonis. This is not a request, it\'s an order.', null, null, 'Wir müssen Verstärkung zur Manaschmiede Duro schicken, Ardonis. Das ist keine Bitte, sondern ein Befehl!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `script_texts` VALUES ('-1000129', 'You cannot be serious! We are severely understaffed and can barely keep this manaforge functional!', null, null, 'Das kann nicht Euer Ernst sein! Wir sind stark unterbesetzt und können diese Schmiede kaum in Betrieb halten!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `script_texts` VALUES ('-1000130', 'You will do as ordered. Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control. Failure to comply will not be tolerated!', null, null, 'Ihr werdet tun, was ich Euch befehle. Die Manaschmiede Duro wird von Manakreaturen angegriffen. Die Lage gerät außer Kontrolle. Ein Versagen wird nicht geduldet!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `script_texts` VALUES ('-1000131', 'Indeed, it is not a request.', null, null, 'In der Tat, das ist keine Bitte.', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_PATHALEON_CULATOR_IMAGE_1');
INSERT INTO `script_texts` VALUES ('-1000132', 'My lord!', null, null, 'Mein Herr!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `script_texts` VALUES ('-1000133', 'Duro will be reinforced! Ultris was a complete disaster. I will NOT have that mistake repeated!', null, null, 'Duro wird verstärkt werden! Ultris war ein komplettes Desaster. Ich werde NICHT zulassen, dass dieser Fehler sich wiederholt!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2');
INSERT INTO `script_texts` VALUES ('-1000134', 'We\'ve had too many setbacks along the way: Hellfire Citadel, Fallen Sky Ridge, Firewing Point... Prince Kael\'thas will tolerate no further delays. I will tolerate nothing other than complete success!', null, null, 'Wir haben schon zu viele Rückschläge einstecken müssen: die Höllenfeuerzitadelle, der Himmelssturzgrat, der Posten der Feuerschwingen... Prinz Kael\'thas wird keine weitere Verzögerung dulden. Ich werde nicht außer vollem Erfolg gelten lassen!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_1');
INSERT INTO `script_texts` VALUES ('-1000135', 'I am returning to Tempest Keep. See to it that I do not have reason to return!', null, null, 'Ich kehre zur Festung der Stürme zurück. Sorgt dafür, dass ich kein Grund haben zurückzukehren!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge PATHALEON_CULATOR_IMAGE_2_2');
INSERT INTO `script_texts` VALUES ('-1000136', 'Yes, my lord.', null, null, 'Ja, mein Herr!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_4 SAY_ARCANIST_ARDONIS_2');
INSERT INTO `script_texts` VALUES ('-1000137', 'See to it, Ardonis!', null, null, 'Kümmert Euch darum, Ardonis!', null, null, null, null, null, '0', '0', '0', '0', 'dawnforge COMMANDER_DAWNFORGE_5');
INSERT INTO `script_texts` VALUES ('-1000138', 'Avruu\'s magic... it still controls me. You must fight me, mortal. It\'s the only way to break the spell!', null, null, 'Avruus Magie... kontrolliert mich noch immer. Ihr müsst mich bekämpfen, Sterblicher. Nur so kann der Zauber gebrochen werden!', null, null, null, null, null, '0', '0', '0', '0', 'aeranas SAY_SUMMON');
INSERT INTO `script_texts` VALUES ('-1000139', 'Avruu\'s magic is broken! I\'m free once again!', null, null, 'Avruus Magie ist gebrochen! Ich bin wieder frei!', null, null, null, null, null, '0', '0', '0', '0', 'aeranas SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1000140', 'Let\'s go.', null, null, 'Auf geht\'s!', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_START');
INSERT INTO `script_texts` VALUES ('-1000141', '$N, let\'s use the antechamber to the right.', null, null, '$N, lasst uns den Vorraum zu unserer Rechten nehmen.', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS1');
INSERT INTO `script_texts` VALUES ('-1000142', 'I can see the light at the end of the tunnel!', null, null, 'Ich sehe schon das Licht am Ende des Tunnels!', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS2');
INSERT INTO `script_texts` VALUES ('-1000143', 'There\'s Farstrider Enclave now, $C. Not far to go... Look out! Troll ambush!!', null, null, '$C, dort drüben ist schon die Enklave der Weltenwanderer. Es ist nicht mehr weit... passt auf! Trollhinterhalt!!', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_PROGRESS3');
INSERT INTO `script_texts` VALUES ('-1000144', 'Thank you for saving my life and bringing me back to safety, $N', null, null, 'Danke, dass ihr mir das Leben gerettet und mich in Sicherheit gebracht habt, $N!', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_END1');
INSERT INTO `script_texts` VALUES ('-1000145', 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', null, null, 'Hauptmann Helios, man hat mich aus den Amanikatakomben gerettet. Melde mich zum Dienst, Sir!', null, null, null, null, null, '0', '0', '1', '0', 'lilatha SAY_END2');
INSERT INTO `script_texts` VALUES ('-1000146', 'Liatha, get someone to look at those injuries. Thank you for bringing her back safely.', null, null, 'Lilatha, jemand soll sich diese Verletzungen ansehen. Ich danke Euch, dass Ihr sie sicher zurückgebracht habt.', null, null, null, null, null, '0', '0', '1', '0', 'lilatha CAPTAIN_ANSWER');
INSERT INTO `script_texts` VALUES ('-1000147', 'I remember well the sting of defeat at the conclusion of the Third War. I have waited far too long for my revenge. Now the shadow of the Legion falls over this world. It is only a matter of time until all of your failed creation... is undone.', null, null, 'Der Stachel der Niederlage am Ende des dritten Krieges sitzt noch immer tief. Ich habe viel zu lange auf meine Rache gewartet. Nun fällt der Schatten der Legion auf diese Welt. Es ist nur eine Frage der Zeit bis Eure gesamte Schöpfung... ungeschehen gemacht wird.', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1000148', 'The Legion will conquer all!', null, null, 'Die Legion wird alles erobern!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000149', 'All mortals will perish!', null, null, 'Alle Sterblichen werden Zu Grunde gehen!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000150', 'All life must be eradicated!', null, null, 'Alles Leben muss ausgelöscht werden!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_SURPREME1');
INSERT INTO `script_texts` VALUES ('-1000151', 'I\'ll rip the flesh from your bones!', null, null, 'Ich werde das Fleisch von Euren Knochen reißen!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_SURPREME2');
INSERT INTO `script_texts` VALUES ('-1000152', 'Kirel Narak!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_KILL1');
INSERT INTO `script_texts` VALUES ('-1000153', 'Contemptible wretch!', null, null, 'Verabscheuungswürdiger Wicht!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_KILL2');
INSERT INTO `script_texts` VALUES ('-1000154', 'The universe will be remade.', null, null, 'Das Universum wird neu erschaffen.', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_KILL3');
INSERT INTO `script_texts` VALUES ('-1000155', 'The Legion... will never... fall.', null, null, 'Die Legion wird niemals fallen!', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1000156', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1000157', 'Invaders, you dangle upon the precipice of oblivion! The Burning Legion comes and with it comes your end.', null, null, 'Eindringlinge, ihr steht am Rande des Untergangs! Die Brennende Legion ist gekommen, und mit ihr euer Ende.', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_RAND1');
INSERT INTO `script_texts` VALUES ('-1000158', 'Impudent whelps, you only delay the inevitable. Where one has fallen, ten shall rise. Such is the will of Kazzak...', null, null, 'Unverschämte Würmer, ihr verzögert nur das Unvermeidliche. Wo einer gefallen ist, sollen zehn wieder auferstehen. Dies ist der Wille von Kazzak...', null, null, null, null, null, '0', '1', '0', '0', 'kazzak SAY_RAND2');
INSERT INTO `script_texts` VALUES ('-1000168', 'Who dares awaken Aquementas?', null, null, 'Wer wagt es, Aquementas zu wecken?', null, null, null, null, null, '0', '1', '0', '0', 'aquementas AGGRO_YELL_AQUE');
INSERT INTO `script_texts` VALUES ('-1000169', 'Muahahahaha! You fool! You\'ve released me from my banishment in the interstices between space and time!', null, null, 'Har har, har! Ihr Narr! Ihr habt mich aus meiner Verbannung in den Zwischenraum von Raum und Zeit befreit!', null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_1');
INSERT INTO `script_texts` VALUES ('-1000170', 'All of Draenor shall quick beneath my feet! I will destroy this world and reshape it in my image!', null, null, 'Ganz Draenor soll unter meinen Füßen beben! Ich werde diese Welt zerstören und nach meinem Bild neu formen!', null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_2');
INSERT INTO `script_texts` VALUES ('-1000171', 'Where shall I begin? I cannot bother myself with a worm such as yourself. There is a world to be conquered!', null, null, 'Womit soll ich beginnen? Ich kann mich nicht mit einem Wurm wie Euch abgeben. Es gilt eine Welt zu erobern!', null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_3');
INSERT INTO `script_texts` VALUES ('-1000172', 'No doubt the fools that banished me are long dead. I shall take wing survey my demense. Pray to whatever gods you hold dear that we do not meet again.', null, null, 'Die Narren, die mich verbannt haben, sind zweifellos schon seit langem tot. Ich sollte mich aufmachen und und meinen neuen Besitz beobachten. Betet zu welchen Göttern Ihr auch immer aufsehen mögt, dass wir uns nie wieder treffen werden.', null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_4');
INSERT INTO `script_texts` VALUES ('-1000173', 'NOOOOooooooo!', null, null, 'NEEeeeiiiinnnn', null, null, null, null, null, '0', '1', '0', '0', 'nether_drake SAY_NIHIL_INTERRUPT');
INSERT INTO `script_texts` VALUES ('-1000174', 'Good $N, you are under the spell\'s influence. I must analyze it quickly, then we can talk.', null, null, 'Gut, $N, Ihr steht unter dem Einfluss des Zeubers. Ich muss das schnell analysieren, dann können wir reden.', null, null, null, null, null, '0', '0', '7', '0', 'daranelle SAY_SPELL_INFLUENCE');
INSERT INTO `script_texts` VALUES ('-1000175', 'Thank you, mortal.', null, null, 'Vielen Dank, Sterblicher.', null, null, null, null, null, '0', '0', '11', '0', ' SAY_JUST_EATEN');
INSERT INTO `script_texts` VALUES ('-1000176', 'The last thing I remember is the ship falling and us getting into the pods. I\'ll go see how I can help. Thank you!', null, null, 'Vielen Dank, $C. Ich gehe am besten zur Absturzstelle und sehe, was ich dort tun kann. Bis zum nächsten Mal.', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL1');
INSERT INTO `script_texts` VALUES ('-1000177', '$C, Where am I? Who are you? Oh no! What happened to the ship?', null, null, 'Wo bin ich? Wer seid Ihr? Oh nein! Was ist mit dem Schiff passiert?', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL2');
INSERT INTO `script_texts` VALUES ('-1000178', '$C You saved me! I owe you a debt that I can never repay. I\'ll go see if I can help the others.', null, null, '$C, du hast mich gerettet! Ich schulde Euch etwas, das ich niemals zurückzahlen kann. Ich werde sehen, ob die Anderen Hilfe benötigen.', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL3');
INSERT INTO `script_texts` VALUES ('-1000179', 'Ugh... what is this place? Is that all that\'s left of the ship over there?', null, null, 'Aaagggh... Wo bin ich? Ist das alles, was vom Schiff übrig ist?', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HEAL4');
INSERT INTO `script_texts` VALUES ('-1000180', 'Oh, the pain...', null, null, 'Oh, diese Schmerzen...', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP1');
INSERT INTO `script_texts` VALUES ('-1000181', 'Everything hurts, Please make it stop...', null, null, 'Alles tut so weh. Lasst es aufhören, bitte!', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP2');
INSERT INTO `script_texts` VALUES ('-1000182', 'Ughhh... I hurt. Can you help me?', null, null, 'Aaagggh... Ich bin verletzt. Könnt Ihr mir helfen?', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP3');
INSERT INTO `script_texts` VALUES ('-1000183', 'I don\'t know if I can make it, please help me...', null, null, 'Ich weiß nicht, ob ich es schaffen werde. Bitte, helft mir!', null, null, null, null, null, '0', '0', '7', '0', 'draenei_survivor SAY_HELP4');
INSERT INTO `script_texts` VALUES ('-1000184', 'Yes Master, all goes along as planned.', null, null, 'Ja, Meister. Alles verläuft wie geplant.', null, null, null, null, null, '0', '0', '7', '0', 'engineer_spark SAY_TEXT');
INSERT INTO `script_texts` VALUES ('-1000185', '%s puts the shell to his ear.', null, null, '%s hält sich die Muschel an das Ohr.', null, null, null, null, null, '0', '2', '7', '0', 'engineer_spark EMOTE_SHELL');
INSERT INTO `script_texts` VALUES ('-1000186', 'Now I cut you!', null, null, 'Und jetzt werde ich Euch aufschlitzen!', null, null, null, null, null, '0', '1', '7', '0', 'engineer_spark SAY_ATTACK');
INSERT INTO `script_texts` VALUES ('-1000187', 'Thank you, dear $C, you just saved my life.', null, null, 'Danke, $Glieber:Liebe:c; $C. Ihr habt mein Leben gerettet!', null, null, null, null, null, '0', '0', '0', '0', 'npc_redemption_target SAY_HEAL');
INSERT INTO `script_texts` VALUES ('-1000188', 'Deployment sucessful. Trespassers will be neutralized.', null, null, 'Einsatzbereit. Eindringlinge werden neutralisiert.', null, null, null, null, null, '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_1');
INSERT INTO `script_texts` VALUES ('-1000189', 'Objective acquired. Initiating security routines.', null, null, 'Ziel ermittelt. Initiiere Sicherheitsroutinen.', null, null, null, null, null, '0', '0', '0', '0', 'converted_sentry SAY_CONVERTED_2');
INSERT INTO `script_texts` VALUES ('-1000190', 'In Nagrand, food hunt ogre!', null, null, 'In Nagrand, Essen jagt Oger!', null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_0');
INSERT INTO `script_texts` VALUES ('-1000191', 'You taste good with maybe a little salt and pepper.', null, null, 'Ihr schmecken gut mit etwas Salz und Pfeffer.', null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_1');
INSERT INTO `script_texts` VALUES ('-1000192', 'OK, OK! Lump give up!', null, null, 'Bitte, bitte! Brocken aufgeben!', null, null, null, null, null, '0', '0', '0', '0', ' SAY_LUMP_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1000194', 'I give up! Please don\'t kill me!', null, null, 'Ich gebe auf! Bitte tötet mich nicht!', null, null, null, null, null, '0', '0', '0', '0', 'unkor SAY_SUBMIT');
INSERT INTO `script_texts` VALUES ('-1000195', 'Thank you again, $N. I\'ll make my way to the road now. When you can, find Terenthis and let him know we escaped.', null, null, 'Ich danke Euch nochmals, $N. Ich mache mich jetzt auf den Weg zur Straße. Wenn Ihr könnt, sucht Terenthis und lasst ihn wissen, dass wir entkommen sind.', null, null, null, null, null, '0', '0', '0', '1', 'volcor SAY_ESCAPE');
INSERT INTO `script_texts` VALUES ('-1000196', 'Belore...', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'lady_sylvanas SAY_LAMENT_END');
INSERT INTO `script_texts` VALUES ('-1000197', '%s kneels down and pick up the amulet.', null, null, '%s kniet seufzend nieder und nimmt das Amulett.', null, null, null, null, null, '0', '2', '1', '16', 'lady_sylvanas EMOTE_LAMENT_END');
INSERT INTO `script_texts` VALUES ('-1000198', 'Taste blade, mongrel!', null, null, 'Niemand soll entkommen!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000199', 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', null, null, 'Bitte sagt mir, dass lhr nicht gerade das getan habt, was ich denke. Bitte sagt mir, dass ich Euch nicht wehtun muss...', null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000200', 'As if we don\'t have enough problems, you go and create more!', null, null, 'Zittert, Würmer!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_GUARD_SIL_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1000201', 'I\'m saved! Thank you, doctor!', null, null, 'Ich bin gerettet! Danke, Doktor!', null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC1');
INSERT INTO `script_texts` VALUES ('-1000202', 'HOORAY! I AM SAVED!', null, null, 'HURRA! ICH BIN GERETTET!', null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC2');
INSERT INTO `script_texts` VALUES ('-1000203', 'Sweet, sweet embrace... take me...', null, null, 'Süße, süße Umarmung... nimm mich auf...', null, null, null, null, null, '0', '0', '0', '0', 'injured_patient SAY_DOC3');
INSERT INTO `script_texts` VALUES ('-1000204', '%s looks up at you quizzically. Maybe you should inspect it?', null, null, '%s sieht fragend zu Euch auf. Vielleicht solltet Ihr der Sache auf den Grund gehen?', null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_A_HELLO');
INSERT INTO `script_texts` VALUES ('-1000205', '%s looks at you unexpectadly.', null, null, '%s sieht fragend zu Euch.', null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_H_HELLO');
INSERT INTO `script_texts` VALUES ('-1000206', '%s starts pecking at the feed.', null, null, '%s beginnt das Futter auszupicken.', null, null, null, null, null, '0', '2', '0', '0', 'cluck EMOTE_CLUCK_TEXT2');
INSERT INTO `script_texts` VALUES ('-1000207', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1000208', 'Frenzyheart kill you if you come back. You no welcome here no more!', null, null, 'Wildherz dich töten, wenn du kommen zurück. Du nicht mehr hier willkommen!', null, null, null, null, null, '0', '0', '0', '0', 'vekjik SAY_TEXTID_VEKJIK1');
INSERT INTO `script_texts` VALUES ('-1000209', 'Very well. Let\'s see what you have to show me, $N.', null, null, 'Nun gut. Lasst uns sehen, was Ihr vorzuzeigen habt, $N.', null, null, null, null, null, '0', '0', '1', '0', 'anvilward SAY_ANVIL1');
INSERT INTO `script_texts` VALUES ('-1000210', 'What manner of trick is this, $R? If you seek to ambush me, I warn you I will not go down quietly!', null, null, 'Was für eine Art Trick ist dies, $R? Ich warne Euch: wenn Ihr mich überfallen wollt, werde ich mich nach Kräften wehren.', null, null, null, null, null, '0', '0', '1', '0', 'anvilward SAY_ANVIL2');
INSERT INTO `script_texts` VALUES ('-1000211', 'Warning! %s emergency shutdown process initiated by $N. Shutdown will complete in two minutes.', null, null, 'Warnung! Notabschaltung von %s wurde duch $N ausgelöst. Notabschaltung in Zwei Minuten abgeschlossen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000212', 'Emergency shutdown will complete in one minute.', null, null, 'Notabschaltung in einer Minute abgeschlossen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_60');
INSERT INTO `script_texts` VALUES ('-1000213', 'Emergency shutdown will complete in thirty seconds.', null, null, 'Notabschaltung in dreißig Sekunden abgeschlossen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_30');
INSERT INTO `script_texts` VALUES ('-1000214', 'Emergency shutdown will complete in ten seconds.', null, null, 'Notabschaltung in zehn Sekunden abgeschlossen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_10');
INSERT INTO `script_texts` VALUES ('-1000215', 'Emergency shutdown complete.', null, null, 'Notabschaltung abgeschlossen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000216', 'Emergency shutdown aborted.', null, null, 'Abschaltung der Manaschmiede abgebrochen. Betrieb der Manaschmiede wird wieder aufgenommen.', null, null, null, null, null, '0', '2', '0', '0', 'manaforge_control EMOTE_ABORT');
INSERT INTO `script_texts` VALUES ('-1000217', 'Greetings, $N. I will guide you through the cavern. Please try and keep up.', null, null, 'Seid gegrüßt, $N. Ich werde Euch durch die Höhle führen. Versucht bitte mit mir Schritt zu halten.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_1');
INSERT INTO `script_texts` VALUES ('-1000218', 'We do not know if the Caverns of Time have always been accessible to mortals. Truly, it is impossible to tell as the Timeless One is in perpetual motion, changing our timeways as he sees fit. What you see now may very well not exist tomorrow. You may wake up and have no memory of this place.', null, null, 'Wir wissen nicht, ob die Höhlen der Zeit schon immer für Sterbliche zugänglich waren. Genau genommen ist dies nur schwer zu sagen, denn der Zeitlose ruht niemals, ständig verändert er den Fluss der Zeit nach seinem Ermessen. Was Ihr heute hier seht, ist womöglich morgen schon nicht mehr existent. Es ist nicht auszuschließen, dass Ihr urplötzlich im Gestern aufwacht, ohne jegliche Erinnerungen an diesem Ort.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_2');
INSERT INTO `script_texts` VALUES ('-1000219', 'It is strange, I know... Most mortals cannot actually comprehend what they see here, as often, what they see is not anchored within their own perception of reality.', null, null, 'Es ist seltsam, ich weiß... Die meisten Sterblichen verstehen nicht, was sie hier erblicken, und genauso oft ist das, was sie sehen, nicht mehr mit ihrer eigenen Wahrnehmung der Realität vereinbar.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_3');
INSERT INTO `script_texts` VALUES ('-1000220', 'Follow me, please.', null, null, 'Folgt mir, bitte.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_4');
INSERT INTO `script_texts` VALUES ('-1000221', 'There are only two truths to be found here: First, that time is chaotic, always in flux, and completely malleable and second, perception does not dictate reality.', null, null, 'An diesem Ort gibt es nur zwei fundamentale Wahrheiten: Erstens, dass die Zeit chaotisch ist, beständig im Fluss und veränderbar. Zweitens, entspricht die Wahrnehmung eines Lebewesens nicht unbedingt immer der Realität.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_5');
INSERT INTO `script_texts` VALUES ('-1000222', 'As custodians of time, we watch over and care for Nozdormu\'s realm. The master is away at the moment, which means that attempts are being made to dramatically alter time. The master never meddles in the affairs of mortals but instead corrects the alterations made to time by others. He is reactionary in this regard.', null, null, 'Als Bewahrer der Zeit wachen wir über Nozdormus Reich. Der Meister ist momentan nicht zugegen, was bedeutet, dass momentan Bestrebungen im Gange sind, den Verlauf der Zeit massiv zu verändern. Niemal würde der Meister sich in die Angelegenheiten der Sterblichen einmischen, vielmehr korrigiert er, duch andere verursachte, Manipulationen der Zeit.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_6');
INSERT INTO `script_texts` VALUES ('-1000223', 'For normal maintenance of time, the Keepers of Time are sufficient caretakers. We are able to deal with most ordinary disturbances. I speak of little things, such as rogue mages changing something in the past to elevate their status or wealth in the present.', null, null, 'Für die reguläre Überwachung der Zeit, sind die Hüter mehr als ausreichend qualifiziert. Wir sind in der Lage selbstständig mit den meisten der herkömmlichen Störungen fertig zu werden. Ich spreche hier von kleinen Zwischenfällen, wie abtrünnigen Magiern, die versuchen, ein Ereignis in der Vergangenheit zu ihren Gunsten zu verändern, um so ihren Reichtum oder gesellschaftlichen Stand in der Gegenwart zu verbessern.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_7');
INSERT INTO `script_texts` VALUES ('-1000224', 'These tunnels that you see are called timeways. They are infinite in number. The ones that currently exist in your reality are what the master has deemed as \'trouble spots.\' These trouble spots may differ completely in theme but they always share a cause. That is, their existence is a result of the same temporal disturbance. Remember that should you venture inside one...', null, null, 'Die Tunnel, die Ihr hier seht, sind die so genannten Pfade der Zeit. Ihre Zahl ist endlos. Diejenigen, welche momentan in Eurer Realität existieren, erchtet der Meister als \'Problemzonen\'. Die verschiedenen Problemzonen unterscheiden sich in ihrer Problematik, aber sie alle entspringen derselben Ursache. Das heißt, sie verdanken ihre Existenz ein und derselben temporalen Störung. Denkt daran, falls Ihr eine davon betreten solltet...', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_8');
INSERT INTO `script_texts` VALUES ('-1000225', 'This timeway is in great disarray! We have agents inside right now attempting to restore order. What information I have indicates that Thrall\'s freedom is in jeopardy. A malevolent organization known as the Infinite Dragonflight is trying to prevent his escape. I fear without outside assistance, all will be lost.', null, null, 'Dieser Pfad der Zeit befindet sich in größter Unordnung! Wir haben einige unserer Agenten hereingeschickt, um die Ordnung wiederherzustellen. Die mir zur Verfügung stehenden Informationen weisen darauf hin, dass Thralls Freiheit in Gefahr ist. Eine finstere Vereinigung, bekannt als der ewige Drachenschwarm, versucht seine Flucht zu verhindern. Ich befürchte, dass ohne Unterstützung von außen, alles verloren ist.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_9');
INSERT INTO `script_texts` VALUES ('-1000226', 'We have very little information on this timeway. Sa\'at has been dispatched and is currently inside. The data we have gathered from his correspondence is that the Infinite Dragonflight are once again attempting to alter time. Could it be that the opening of the Dark Portal is being targeted for sabotage? Let us hope not...', null, null, 'Über diesen Pfad der Zeit stehen uns nur wenige Informationen zur Verfügung. Momentan kümmert sich Sa\'at um diese Angelegenheit, er befindet sich im Inneren des Pfades. Soweit wir es seinen Botschaften entnehmen konnten, versucht der ewige Drachenschwarm erneut den Verlauf der Zeit zu ändern. Kann es tatsächlich möglich sein, dass sie planen, die Öffnung des Dunklen Portals zu verhindern? Ich hoffe nicht...', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_10');
INSERT INTO `script_texts` VALUES ('-1000227', 'This timeway is currently collapsing. What that may hold for the past, present and future is currently unknown...', null, null, 'Dieser Pfad der Zeit ist zusammengebrochen und hat sich neu geformt. Das Resultat war Stratholme aus früheren Tagen. Was spielt sich da drinnen bloß ab?', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_11');
INSERT INTO `script_texts` VALUES ('-1000228', 'The timeways are currently ranked in order from least catastrophic to most catastrophic. Note that they are all classified as catastrophic, meaning that any single one of these timeways collapsing would mean that your world would end. We only classify them in such a way so that the heroes and adventurers that are sent here know which timeway best suits their abilities.', null, null, 'Wir haben die Pfade der Zeit nach ihrem Katastrophengrad, vom leichtesten bis zum schwersten, eingeteilt. Beachtet bitte, dass sie alle als katastrophal für das Gefüge der Zeit eingestuft wurden. Sollte nur ein einziger von ihnen kollabieren, würde dies das Ende Eurer Welt bedeuten. Die von uns vorgenommene Einteilung soll es den eintreffenden Helden leichter machen, Pfade der Zeit zu finden, die ihre Fähigkeiten entsprechen.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_12');
INSERT INTO `script_texts` VALUES ('-1000229', 'All we know of this timeway is that it leads to Mount Hyjal. The Infinite Dragonflight have gone to great lengths to prevent our involvement. We know next to nothing, mortal. Soridormi is currently attempting to break through the timeway\'s defenses but has thus far been unsuccessful. You might be our only hope of breaking through and resolving the conflict.', null, null, 'Wir wissen nur, dass dieser Pfad der Zeit zum Berg Hyjal führt. Der ewige Drachenschwarm hat alles unternommen, um eine Einmischung unsererseits zu verhindern. Die genaue Situation ist uns unbekannt, Sterblicher. Soridormi versucht momentan die Verteidigungsmaßnamen des Pfades zu durchdringen, bisher ohne Erfolg. Ihr seid vielleicht unsere letzte Hoffnung auf einen Durchbruch und eine Lösung des Konflikts.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_13');
INSERT INTO `script_texts` VALUES ('-1000230', 'Our time is at an end $N. I would wish you luck, if such a thing existed.', null, null, 'Unsere gemeinsamen Zeit endet hier, $N. Ich würde Euch Glück wünschen, wenn so etwas tatsächlich existieren würde.', null, null, null, null, null, '0', '4', '0', '0', 'WHISPER_CUSTODIAN_14');
INSERT INTO `script_texts` VALUES ('-1000231', 'Ah, $GPriest:Priestess; you came along just in time. I appreciate it.', null, null, 'Ah, $GPriester:Priesterin;, Ihr seid gerade rechtzeitig gekommen. Ich weiß das zu schätzen.', null, null, null, null, null, '0', '0', '0', '20', 'garments SAY_COMMON_HEALED');
INSERT INTO `script_texts` VALUES ('-1000232', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those gnolls with your power to back me!', null, null, 'Danke! Ich danke Euch, $GPriester:Priesterin;. Jetzt, wo mich Eure Macht unterstützt, kann ich es mit diesen Gnollen aufnehmen!', null, null, null, null, null, '0', '0', '1', '4', 'garments SAY_DG_KEL_THANKS');
INSERT INTO `script_texts` VALUES ('-1000233', 'Farewell to you, and may shadow always protect you!', null, null, 'Lebt wohl, und möge der Schatten Euch immer beschützen!', null, null, null, null, null, '0', '0', '1', '3', 'garments SAY_DG_KEL_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000234', 'Follow me, stranger. This won\'t take long.', null, null, 'Folgt mir, Fremder. Es wird nicht lange dauern.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_SERV_0');
INSERT INTO `script_texts` VALUES ('-1000235', 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', null, null, 'Shattrath war einst die Hauptstadt der Draenei in dieser Welt. Ihr Name bedeutet \"Stadt des Lichts\".', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_1');
INSERT INTO `script_texts` VALUES ('-1000236', 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', null, null, 'Als die Brennende Legion die Orcs gegen die Draenei hetzte, brach die schlimmste Schlacht hier aus. Die Draenei haben mit Klauen und Zähnen gekämpft, aber schließlich fiel die Stadt doch.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_2');
INSERT INTO `script_texts` VALUES ('-1000237', 'The city was left in ruins and darkness... until the Sha\'tar arrived.', null, null, 'Die Stadt lag in Trümmern und in Dunkelheit... bis die Sha\'tar kamen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_3');
INSERT INTO `script_texts` VALUES ('-1000238', 'Let us go into the Lower City. I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', null, null, 'Lasst uns in das untere Viertel gehen. Ich werde Euch warnen, dass Shattrath als einer der sicheren Häfen der Scherbenwelt viele Flüchtlinge aus allen Kriegen, ob vergangene oder herrschende, angelockt hat.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_4');
INSERT INTO `script_texts` VALUES ('-1000239', 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', null, null, 'Die Sha\'tar, oder \"aus dem Licht Geborene\", sind die Naaru, die in die Scherbenwelt kamen, um die Dämonen der Brennenden Legion zu bekämpfen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_5');
INSERT INTO `script_texts` VALUES ('-1000240', 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.', null, null, 'Sie wurden von den Ruinen von Shattrath angezogen, in denen ein kleiner Rest Priesterschaft der Draenei in einem zerstörten Tempel an genau dieser Stelle seine Riten vollzog.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_6');
INSERT INTO `script_texts` VALUES ('-1000241', 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began. The ruined temple is now used as an infirmary for injured refugees.', null, null, 'Die Priesterschaft, die als Aldor bekannt ist, hat ihre Stärke schnell zurückgewonnen, als sie hörten, dass die Naaru zurückgekehrt waren, und der Aufbau der Stadt begann. Der zerstörte Tempel wird nun als Krankenlager für die verletzten Flüchtlinge genutzt.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_7');
INSERT INTO `script_texts` VALUES ('-1000242', 'It wouldn\'t be long, however, before the city came under attack once again. This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', null, null, 'Es dauerte jedoch nicht lange, bis die Stadt erneut angegriffen wurde. Diesmal kam der Angriff von Illidans Armee. Ein großes Regiment von Blutelfen wurde von Illidans Verbündetem, Kael\'thas Sonnenwanderer, geschickt, um die Stadt in Schutt und Asche zu legen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_8');
INSERT INTO `script_texts` VALUES ('-1000243', 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light. But then the unexpected happened.', null, null, 'Als das Regiment der Blutelfen diese Brücke überquerte, nahmen die Exarchen und Verteidiger der Aldor Aufstellung, um die Terrasse des Lichts zu verteidigen. Aber dann geschah das Unerwartete.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_9');
INSERT INTO `script_texts` VALUES ('-1000244', 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', null, null, 'Die Blutelfen haben ihre Waffen vor den Verteidigern der Stadt niedergelegt. Ihr Anführer, ein Blutelfältester namens Voren\'thal, stürmte auf die Terasse des Lichts und wollte mit A\'dal sprechen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_10');
INSERT INTO `script_texts` VALUES ('-1000245', 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', null, null, 'Als der Naaru auf ihm zukam, kniete Voren\'thal vor ihm nieder und sprach Folgendes: \"Ich habe Euch in einer Vision gesehen, Naaru. Die einzige Hoffnung auf das Überleben meines Volkes ruht auf Euch. Meine Gefolgsleute und ich sind hier, um Euch zu dienen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_11');
INSERT INTO `script_texts` VALUES ('-1000246', 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves. Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.', null, null, 'Der Treuebruch Voren\'thals und seiner Gefolgschaft war der größte Verlust, den Kaels Streitkräfte je hinnehmen mussten. Und es waren nicht nur Blutelfen. Viele der besten und talentiertesten Gelehrten und Magister Kael wurden von Voren\'thal beeinflusst.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_12');
INSERT INTO `script_texts` VALUES ('-1000247', 'The naaru accepted the defectors, who would become known as the Scryers; their dwelling lies in the platform above. Only those initiated with the Scryers are allowed there.', null, null, 'Die Naaru haben die Abtrünnigen, die von da an als Seher bekannt wurden, aufgenommen. Ihr Zuhause befindet sich auf der Ebene über uns. Nur diejenigen, die von den Sehern initiiert wurden, dürfen sich dort aufhalten.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_13');
INSERT INTO `script_texts` VALUES ('-1000248', 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', null, null, 'Die Aldor sind die Gefolgsleute des Lichts. Vergebung und Erlösung sind Werte, die ihnen nur allzu vertraut sind. Dennoch haben sie unter Kaels Befehl sehr kämpfen müssen, um die Taten der Blutelfen vergessen zu können.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_14');
INSERT INTO `script_texts` VALUES ('-1000249', 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru. They were not happy to share the city with their former enemies.', null, null, 'Viele Mitglieder der Priesterschaft wurden von genau den Magistern getötet, die geschworen haben die Naaru zu dienen. Sie waren nicht sehr glücklich darüber, Ihre Stadt mit ihren früheren Feinden teilen zu müssen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_15');
INSERT INTO `script_texts` VALUES ('-1000250', 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above. As a holy site, only the initiated are welcome inside.', null, null, 'Der heiligste Tempel der Aldor und die Wohnstätten um ihn herum befinden sich auf der Terrasse über uns. Da es ein heiliger Ort ist, sind nur Initiierte dort willkommen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_16');
INSERT INTO `script_texts` VALUES ('-1000251', 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', null, null, 'Die Angriffe auf Shattrath gingen weiter, aber wie Ihr seht fiel die Stadt nicht. Im Gegenzug führte der Naaru, der als Xi\'ri bekannt ist, einen erfolgreichen Einfall in das Schattenmondtal, direkt vor Illidans Haustür.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_17');
INSERT INTO `script_texts` VALUES ('-1000252', 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers. The two factions have not given up on their old feuds, though.', null, null, 'Dort begann er, mit Hilfe der Aldor und der Seher Krieg gegen Illidan zu führen. Die beiden Fraktionen haben ihre Feindschaft jedoch nicht niedergelegt.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_18');
INSERT INTO `script_texts` VALUES ('-1000253', 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal. The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', null, null, 'Ihre Feindseligkeit ist so groß, dass sie darum wetteifern, wem die Ehre zuteil werden darf, den Naaru dort zu helfen. Jeden Tag wird diese Entscheidung von A\'dal gefällt. Hier sammeln sich die Armeen, um A\'dals Segen zu erhalten, bevor sie nach Schattenmond ziehen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_19');
INSERT INTO `script_texts` VALUES ('-1000254', 'Khadgar should be ready to see you again. Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.', null, null, 'Khadgar sollte nun bereit sein, Euch wieder zu treffen. Denkt daran, dass Ihr Euch, wenn Ihr den Sha\'tar dienen wollt, wahrscheinlich mit den Aldor oder Sehern verbünden müsst. Wenn Ihr jedoch das Wohlwollen einer dieser Gruppen erarbeiten möchtet, müsst Ihr den Hass der anderen in Kauf nehmen.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_20');
INSERT INTO `script_texts` VALUES ('-1000255', 'Good luck stranger, and welcome to Shattrath City.', null, null, 'Viel Glück, Fremder, und willkommen in Shattrath.', null, null, null, null, null, '0', '4', '0', '0', 'SAY_KHAD_SERV_21');
INSERT INTO `script_texts` VALUES ('-1000256', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those murlocs with the Light on my side!', null, null, 'Danke! Ich danke Euch, $GPriester:Priesterin;. Jetzt, wo ich das Licht auf meiner Seite habe, kann ich es mit diesen Murlocs aufnehmen!', null, null, null, null, null, '0', '0', '7', '4', 'garments SAY_ROBERTS_THANKS');
INSERT INTO `script_texts` VALUES ('-1000257', 'Farewell to you, and may the Light be with you always.', null, null, 'Gehabt Euch wohl, und möge das Licht Euch immer beistehen.', null, null, null, null, null, '0', '0', '7', '3', 'garments SAY_ROBERTS_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000258', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those humans with your power to back me!', null, null, 'Danke! Ich danke Euch, $GPriester:Priesterin;. Jetzt, wo mich Eure Macht unterstützt, kann ich es mit diesen Menschen aufnehmen!', null, null, null, null, null, '0', '0', '1', '4', 'garments SAY_KORJA_THANKS');
INSERT INTO `script_texts` VALUES ('-1000259', 'Farewell to you, and may our ancestors be with you always!', null, null, 'Gehabt Euch wohl, und mögen unsere Vorfahren Euch immer beistehen!', null, null, null, null, null, '0', '0', '1', '3', 'garments SAY_KORJA_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000260', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those wendigo with the Light on my side!', null, null, 'Danke! Ich danke Euch, $GPriester:Priesterin;. Jetzt, wo ich das Licht auf meiner Seite habe, kann ich es mit diesen Wendigos aufnehmen!', null, null, null, null, null, '0', '0', '7', '4', 'garments SAY_DOLF_THANKS');
INSERT INTO `script_texts` VALUES ('-1000261', 'Farewell to you, and may the Light be with you always.', null, null, 'Gehabt Euch wohl, und möge das Licht Euch immer beistehen.', null, null, null, null, null, '0', '0', '7', '3', 'garments SAY_DOLF_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000262', 'Thank you! Thank you, $GPriest:Priestess;. Now I can take on those corrupt timberlings with Elune\'s power behind me!', null, null, 'Danke! Ich danke Euch, $GPriester:Priesterin;. Jetzt, wo Elunes Macht mich stützt, kann ich es mit diesen verderbten Bäumlingen aufnehmen!', null, null, null, null, null, '0', '0', '2', '4', 'garments SAY_SHAYA_THANKS');
INSERT INTO `script_texts` VALUES ('-1000263', 'Farewell to you, and may Elune be with you always.', null, null, 'Gehabt Euch wohl, und möge Elune immer bei Euch sein.', null, null, null, null, null, '0', '0', '2', '3', 'garments SAY_SHAYA_GOODBYE');
INSERT INTO `script_texts` VALUES ('-1000264', 'Ok, $N. Follow me to the cave where I\'ll attempt to harness the power of the rune stone into these goggles.', null, null, 'Nun gut, $N. Folgt mir zu der Höhle, Dort werde ich versuchen, die Macht des Runenstens in dieser Brille zu bannen.', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000265', 'I discovered this cave on our first day here. I believe the energy in the stone can be used to our advantage.', null, null, 'Ich habe diese Höhle an unserem ersten Tag hier entdeckt. Ich glaube, dass wir die Energien in dem Stein zu unserem Vorteil nutzen können.', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000266', 'I\'ll begin drawing energy from the stone. Your job, $N, is to defend me. This place is cursed... trust me.', null, null, 'Ich werde jetzt anfangen, Energie aus de, Stein zu ziehen. Eure Aufgabe, $N, ist es, mich zu verteidigen. Dieser Ort ist verflucht... glaubt mir.', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_3');
INSERT INTO `script_texts` VALUES ('-1000267', '%s begins tinkering with the goggles before the stone.', null, null, '%s beginnt mit der Brille vor dem Stein herumzutüfteln.', null, null, null, null, null, '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_4');
INSERT INTO `script_texts` VALUES ('-1000268', 'Help!!! Get these things off me so I can get my work done!', null, null, 'Hilfe!!! Befreit mich von diesen Dingern, damit ich meine Arbeit machen kann!', null, null, null, null, null, '0', '0', '0', '0', 'phizzlethorpe SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000269', 'Almost done! Just a little longer!', null, null, 'Fast fertig! Nur noch ein bisschen länger!', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_5');
INSERT INTO `script_texts` VALUES ('-1000270', 'I\'ve done it! I have harnessed the power of the stone into the goggles! Let\'s get out of here!', null, null, 'Ich habe es geschaft! Ich habe die Macht des Steins auf die Brille übertragen! Lasst uns von hier verschwinden!', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_6');
INSERT INTO `script_texts` VALUES ('-1000271', 'Phew! Glad to be back from that creepy cave.', null, null, 'Puh! Ich bin froh, dass ich aus dieser gruseligen Höhle zurück bin.', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_7');
INSERT INTO `script_texts` VALUES ('-1000272', '%s hands one glowing goggles over to Doctor Draxlegauge.', null, null, '%s überreicht Doktor Drechselknacks die glühende Brille.', null, null, null, null, null, '0', '2', '0', '0', 'phizzlethorpe EMOTE_PROGRESS_8');
INSERT INTO `script_texts` VALUES ('-1000273', 'Doctor Draxlegauge will give you further instructions, $N. Many thanks for your help!', null, null, 'Doktor Drechselknacks wird Euch weitere Anweisungen geben, $N. Vielen Dank für Eure Hilfe!', null, null, null, null, null, '0', '0', '0', '1', 'phizzlethorpe SAY_PROGRESS_9');
INSERT INTO `script_texts` VALUES ('-1000274', 'Time to teach you a lesson in manners, little $Gboy:girl;!', null, null, 'Zeit Euch Manieren beizubringen, $GJunge:Mädel;!', null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_START');
INSERT INTO `script_texts` VALUES ('-1000275', 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', null, null, 'Ich zähle bis \"3\", dann seid Ihr draußen, oder ich hetze die Hunde auf Euch.', null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT');
INSERT INTO `script_texts` VALUES ('-1000276', '1...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT_1');
INSERT INTO `script_texts` VALUES ('-1000277', '2...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_COUNT_2');
INSERT INTO `script_texts` VALUES ('-1000278', 'Time to meet your maker!', null, null, 'Zeit Eurem Schöpfer gegenüber zu treten!', null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_ATTACK_5');
INSERT INTO `script_texts` VALUES ('-1000279', 'Alright, we give up! Don\'t hurt us!', null, null, 'In Ordnung, wir geben auf. Tut uns nicht weh!', null, null, null, null, null, '0', '0', '0', '0', 'larry SAY_GIVEUP');
INSERT INTO `script_texts` VALUES ('-1000280', 'A shadowy, sinister presence has invaded the Emerald Dream. Its power is poised to spill over into our world, $N. We must oppose it! That\'s why I cannot accompany you in person.', null, null, 'Eine schattenhafte, bösartige Präsenz ist in den Smaragdgrünen Traum eingedrungen. Ihre Macht will sich in unsere Welt schleichen, $N. Wir müssen etwas dagegen unternehmen! Aus diesem Grund kann ich Euch nicht persönlich begleiten.', null, null, null, null, null, '0', '0', '0', '1', 'clintar SAY_START');
INSERT INTO `script_texts` VALUES ('-1000281', 'The Emerald Dream will never be yours!', null, null, 'Der Smaragdgrüne Traum wird niemals Euch gehören!', null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000282', 'Begone from this place!', null, null, 'Verlasst diesen Ort!', null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000283', 'That\'s the first relic, but there are still two more. Follow me, $N.', null, null, 'Dies ist das erste Relikt, doch es gibt noch zwei weitere. Folgt mir, $N.', null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC1');
INSERT INTO `script_texts` VALUES ('-1000284', 'I\'ve recovered the second relic. Take a moment to rest, and then we\'ll continue to the last reliquary.', null, null, 'Ich habe das zweite Relikt geborgen. Ruht Euch einen Moment aus. Dann werden wir zum letzten Relikt gehen.', null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC2');
INSERT INTO `script_texts` VALUES ('-1000285', 'We have all three of the relics, but my energy is rapidly fading. We must make our way back to Dreamwarden Lurosa! He will let you know what to do next.', null, null, 'Wir haben alle drei Relikte, aber meine Kräfte schwinden zusehends. Wir müssen es zurück zu Traumwächter Lurosa schaffen! Er wird Euch sagen können, was Ihr als nächstes zu tun habt.', null, null, null, null, null, '0', '0', '0', '0', 'clintar SAY_RELIC3');
INSERT INTO `script_texts` VALUES ('-1000286', 'Lurosa, I am entrusting the Relics of Aviana to $N, who will take them to Morthis Whisperwing. I must return completely to the Emerald Dream now. Do not let $N fail!', null, null, 'Lurosa, ich werde $N die Relikte von Aviana anvertrauen, sie wird sie zu Morthis Flügelraunen bringen. Ich muss nun vollständig in den Smaragdgrünen Traum zurückkehren. $N darf nicht versagen!', null, null, null, null, null, '0', '0', '0', '1', 'clintar SAY_END');
INSERT INTO `script_texts` VALUES ('-1000287', 'Emergency power activated! Initializing ambulanory motor! CLUCK!', null, null, 'Notfallenergie aktiviert! Ambulatorenantrieb wird gestartet! GLUCK!', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_START');
INSERT INTO `script_texts` VALUES ('-1000288', 'Physical threat detected! Evasive action! CLUCK!', null, null, 'Körperliche Bedrohung entdeckt! Ausweichmanöver! GLUCK!', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000289', 'Thread analyzed! Activating combat plan beta! CLUCK!', null, null, 'Bedrohung analysiert! Gefechtsplan Beta wird aktiviert! GLUCK!', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000290', 'CLUCK! Sensors detect spatial anomaly - danger imminent! CLUCK!', null, null, 'GLUCK! Sensoren haben räumliche Anomalie entdeckt - Gefahr im Verzug! GLUCK', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000291', 'No one challanges the Wastewander nomads - not even robotic chickens! ATTACK!', null, null, 'Niemand fordert die Wüstenläufernomaden heraus - nicht einmal Roboterhühner! ANGRIFF!', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX17_AMBUSH_REPLY');
INSERT INTO `script_texts` VALUES ('-1000292', 'Cloaking systems online! CLUCK! Engaging cloak for transport to Booty Bay!', null, null, 'Tarnsysteme online! GLUCK! Tarnung für Transport nach Beutebucht wird aktiviert!', null, null, null, null, null, '0', '0', '0', '0', 'oox SAY_OOX_END');
INSERT INTO `script_texts` VALUES ('-1000293', 'To the house! Stay close to me, no matter what! I have my gun and ammo there!', null, null, 'Zum Haus! Bleibt dicht bei mir, was auch geschieht. Ich habe meine Schusswaffe und Munition im Haus.', null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_START');
INSERT INTO `script_texts` VALUES ('-1000294', 'We showed that one!', null, null, 'Wir zeigten es ihm!', null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_1');
INSERT INTO `script_texts` VALUES ('-1000295', 'One more down!', null, null, 'Und wieder einer weniger!', null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_2');
INSERT INTO `script_texts` VALUES ('-1000296', 'We\'ve done it! We won!', null, null, 'Wir haben es geschafft! Wir haben es geschafft! Wir haben sie zurückgedrängt!', null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_DOWN_3');
INSERT INTO `script_texts` VALUES ('-1000297', 'Meet me down by the orchard--I just need to put my gun away.', null, null, 'Wir treffen uns beim Obsthain. Ich bring nur noch rasch mein Gewehr weg.', null, null, null, null, null, '0', '0', '7', '0', 'stilwell SAY_DS_PROLOGUE');
INSERT INTO `script_texts` VALUES ('-1000298', 'Alright, alright I think I can figure out how to operate this thing...', null, null, 'Schön, schön! Ich werde schon herausfinden, wie dieses Ding funktioniert...', null, null, null, null, null, '0', '0', '0', '1', 'wizzlecrank SAY_START');
INSERT INTO `script_texts` VALUES ('-1000299', 'Arrrgh! This isn\'t right!', null, null, 'Arrrgh! Das ist nicht richtig!', null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_STARTUP1');
INSERT INTO `script_texts` VALUES ('-1000300', 'Okay, I think I\'ve got it, now. Follow me, $n!', null, null, 'Gut, ich denke, jetzt habe ich es. Folgt mir, $N!', null, null, null, null, null, '0', '0', '0', '1', 'wizzlecrank SAY_STARTUP2');
INSERT INTO `script_texts` VALUES ('-1000301', 'There\'s the stolen shredder! Stop it or Lugwizzle will have our hides!', null, null, 'Das ist der gestohlene Schredder! Hört damit auf, sonst wird Lugwizz uns das Fell über die Ohren ziehen!', null, null, null, null, null, '0', '1', '0', '0', 'wizzlecrank SAY_MERCENARY');
INSERT INTO `script_texts` VALUES ('-1000302', 'Looks like we\'re out of woods, eh? Wonder what this does...', null, null, 'Sieht aus, als wären wir aus dem Gröbsten raus, was? Was das wahl macht...', null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000303', 'Come on, don\'t break down on me now!', null, null, 'Kommt schon, lass mich nicht im Stich!', null, null, null, null, null, '0', '0', '0', '1', 'wizzlecrank SAY_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000304', 'That was a close one! Well, let\'s get going, it\'s still a ways to Ratchet!', null, null, 'Das war knapp! Machen wir uns auf den Weg, es ist noch weit bis Ratchet!', null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_PROGRESS_3');
INSERT INTO `script_texts` VALUES ('-1000305', 'Hmm... I don\'t think this blinking red light is a good thing...', null, null, 'Hm... Ich glaube nicht, dass dieses rote Licht etwas Gutes bedeutet...', null, null, null, null, null, '0', '0', '0', '0', 'wizzlecrank SAY_END');
INSERT INTO `script_texts` VALUES ('-1000306', 'Let\'s get to the others, and keep an eye open for those wolves cutside...', null, null, 'Wir sollten zu den anderen gehen und die Wölfe da draußen im Auge behalten...', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_START_1');
INSERT INTO `script_texts` VALUES ('-1000307', 'Be careful, $N. Those wolves like to hide among the trees.', null, null, 'Gebt Acht, $N. Diese Wölfe halten sich oft unter den Bäumen versteckt.', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_START_2');
INSERT INTO `script_texts` VALUES ('-1000308', 'A $C attacks!', null, null, '$C greift an!', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000309', 'Beware! I am under attack!', null, null, 'Achtung! Ich werde angegriffen!', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000310', 'Oh no! A $C is upon us!', null, null, 'Vorsicht! Ein $C ist hinter uns her!', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1000311', 'We\'re almost there!', null, null, 'Wir sind fast da!', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000312', 'We made it! Thanks, $N. I couldn\'t have gotten without you.', null, null, 'Wir haben es geschafft! Danke, $N. Ohne Euch hätte ich es nicht bis hierher geschafft.', null, null, null, null, null, '0', '0', '1', '0', 'erland SAY_END');
INSERT INTO `script_texts` VALUES ('-1000313', 'It\'s good to see you again, Erland. What is your report?', null, null, 'Schön, Euch wiederzusehen, Erland. Wie lautet Euer Bericht?', null, null, null, null, null, '0', '0', '33', '1', 'erland SAY_RANE');
INSERT INTO `script_texts` VALUES ('-1000314', 'Masses of wolves are to the east, and whoever lived at Malden\'s Orchard is gone.', null, null, 'Im Osten halten sich unzählige Wölfe auf und wer auch immer bei Maldens Obsthain lebt, ist fort.', null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_RANE_REPLY');
INSERT INTO `script_texts` VALUES ('-1000315', 'If I am excused, then I\'d like to check on Quinn...', null, null, 'Wenn Ihr mich entschuldigen wollt, dann würde ich jetzt gern mal nach Quinn sehen...', null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_CHECK_NEXT');
INSERT INTO `script_texts` VALUES ('-1000316', 'Hello, Quinn. How are you faring?', null, null, 'Hallo, Quinn. Wie geht es Euch?', null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_QUINN');
INSERT INTO `script_texts` VALUES ('-1000317', 'I\'ve been better. Ivar the Foul got the better of me...', null, null, 'Es ging mir schon mal besser. Ivar der Verfaulte hat mich überwältigt...', null, null, null, null, null, '0', '0', '33', '1', 'erland SAY_QUINN_REPLY');
INSERT INTO `script_texts` VALUES ('-1000318', 'Try to take better care of yourself, Quinn. You were lucky this time.', null, null, 'Ihr müsst besser auf Euch Acht geben, Quinn. Diesmal habt Ihr Glück gehabt.', null, null, null, null, null, '0', '0', '1', '1', 'erland SAY_BYE');
INSERT INTO `script_texts` VALUES ('-1000319', 'Let the trial begin, Bloodwrath, attack!', null, null, 'Lasst die Prüfung beginnen. Rachblut, angreifen!', null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId1');
INSERT INTO `script_texts` VALUES ('-1000320', 'Champion Lightrend, make me proud!', null, null, 'Champion Lichtrend, macht mich stolz!', null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId2');
INSERT INTO `script_texts` VALUES ('-1000321', 'Show this upstart how a real Blood Knight fights, Swiftblade!', null, null, 'Zeigt diesem Emporkömmling, wie ein wahrer Blutritter kämpft, Schnellklinge!', null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId3');
INSERT INTO `script_texts` VALUES ('-1000322', 'Show $n the meaning of pain, Sunstriker!', null, null, 'Lehrt $N die Bedeutung von Schmerz, Sonnensturm!', null, null, null, null, null, '0', '1', '1', '0', 'kelerun SayId4');
INSERT INTO `script_texts` VALUES ('-1000323', 'Mist! I feared I would never see you again! Yes, I am well, do not worry for me. You must rest and recover your health.', null, null, 'Nebel! Ich fürchtete, ich würde Euch niemals wiedersehen!! Ja, mir geht es gut. Sorgt Euch nicht um mich. Ihr müsst ruhen und Euch erholen.', null, null, null, null, null, '0', '0', '7', '0', 'mist SAY_AT_HOME');
INSERT INTO `script_texts` VALUES ('-1000324', '%s growls in acknowledgement before straightening and making her way off into the forest.', null, null, '%s knurrt zustimmend, bevor sie sich streckt und wieder in Richtung Wald verschwindet.', null, null, null, null, null, '0', '2', '0', '0', 'mist EMOTE_AT_HOME');
INSERT INTO `script_texts` VALUES ('-1000325', '\"Threshwackonator First Mate unit prepared to follow\"', null, null, 'Erster-Maat-Einheit des Dreschonators bereit zum Folgen', null, null, null, null, null, '0', '2', '0', '0', 'threshwackonator EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000326', 'YARRR! Swabie, what have ye done?! He\'s gone mad! Baton him down the hatches! Hoist the mast! ARRRR! Every man for hi\'self!', null, null, 'JARRR! Swabie, was haste gemacht?! Er ist verrückt geworden! Luken dicht machen! Segel hissen! ARRRR! Jeder rette sich selbst!', null, null, null, null, null, '0', '0', '7', '0', 'threshwackonator SAY_AT_CLOSE');
INSERT INTO `script_texts` VALUES ('-1000327', 'Ok, $n, let\'s go find where I left that mysterious fossil. Follow me!', null, null, 'Nun gut, $N, dann lasst uns nachsehen, wo ich dieses rätselhafte Fossil gelassen habe. Folgt mir!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_START');
INSERT INTO `script_texts` VALUES ('-1000328', 'Now where did I put that mysterious fossil? Ah, maybe up there...', null, null, 'Wo habe ich bloß dieses rätselhafte Fossil hingelegt? Ah, vielleicht da oben...', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_1');
INSERT INTO `script_texts` VALUES ('-1000329', 'Hrm, nothing up here.', null, null, 'Hm, hier oben ist nichts.', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_RAMP1_2');
INSERT INTO `script_texts` VALUES ('-1000330', 'No mysterious fossil here... Ah, but my copy of Green Hills of Stranglethorn. What a good book!', null, null, 'Hier ist kein rätselhaftes Fossil... Oh, aber meine Ausgabe von \"Die Grünen Hügel des Schlingendorntals\". Was für ein gutes Buch!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_BOOK');
INSERT INTO `script_texts` VALUES ('-1000331', 'I bet you I left it in the tent!', null, null, 'Ich wette, ich habe es im Zelt vergessen!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_1');
INSERT INTO `script_texts` VALUES ('-1000332', 'Oh wait, that\'s Hollee\'s tent... and it\'s empty.', null, null, 'Moment, das ist Hollees Zelt... und es ist leer.', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_TENT1_2');
INSERT INTO `script_texts` VALUES ('-1000333', 'Interesting... I hadn\'t noticed this earlier...', null, null, 'Interessant... das ist mir bisher nicht aufgefallen...', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_MOSS');
INSERT INTO `script_texts` VALUES ('-1000334', '%s inspects the ancient, mossy stone.', null, null, '%s untersucht den alten, moosbedeckten Stein.', null, null, null, null, null, '0', '2', '7', '0', 'remtravel EMOTE_REM_MOSS');
INSERT INTO `script_texts` VALUES ('-1000335', 'Oh wait! I\'m supposed to be looking for that mysterious fossil!', null, null, 'Halt! Ich soll nach diesem rätselhaften Fossil suchen!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_MOSS_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000336', 'Nope. didn\'t leave the fossil back here!', null, null, 'Nein, hier hab ich das Fossil nicht gelassen!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000337', 'Ah. I remember now! I gave the mysterious fossil to Hollee! Check with her.', null, null, 'Ah, jetzt fällt es mir wieder ein! Ich habe Hollee das rätselhafte Fossil gegeben! Ich frage sie.', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_REMEMBER');
INSERT INTO `script_texts` VALUES ('-1000338', '%s goes back to work, oblivious to everything around him.', null, null, '%s geht zurück an die Arbeit und nimmt die Umgebung gar nicht wahr.', null, null, null, null, null, '0', '2', '7', '0', 'remtravel EMOTE_REM_END');
INSERT INTO `script_texts` VALUES ('-1000339', 'Something tells me this $r wants the mysterious fossil too. Help!', null, null, 'Ich habe so das Gefühl, dass d. Gesprungener Golem auch hinter diesem rätselhaften Fossil her ist. Hilfe!', null, null, null, null, null, '0', '0', '7', '0', 'remtravel SAY_REM_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000340', '%s howls in delight at the sight of his lunch!', null, null, '%s jault beim Anblick der Mahlzeit freudig auf.', null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_SEE_LUNCH');
INSERT INTO `script_texts` VALUES ('-1000341', '%s eats his lunch.', null, null, '%s verspeist seine Mahlzeit.', null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_EAT_LUNCH');
INSERT INTO `script_texts` VALUES ('-1000342', '%s thanks you with a special dance.', null, null, '%s dankt Euch mit einem besonderen Tanz.', null, null, null, null, null, '0', '2', '0', '0', 'kyle EMOTE_DANCE');
INSERT INTO `script_texts` VALUES ('-1000343', 'Is the way clear? Let\'s get out while we can, $N.', null, null, 'Ist der Weg frei? Lasst uns von hier verschwinden, solange wir noch können, $N.', null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_START');
INSERT INTO `script_texts` VALUES ('-1000344', 'Looks like we won\'t get away so easy. Get ready!', null, null, 'Es sieht so aus, als kämen wir nicht so leicht davon. Macht Euch bereit!', null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_AMBUSH1');
INSERT INTO `script_texts` VALUES ('-1000345', 'Let\'s keep moving. We\'re not safe here!', null, null, 'Bleibt in Bewegung. Hier sind wir nicht sicher!', null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000346', 'Look out, $N! Enemies ahead!', null, null, 'Achtung, $N! Feinde voraus!', null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_AMBUSH2');
INSERT INTO `script_texts` VALUES ('-1000347', 'We\'re almost to the refuge! Let\'s go.', null, null, 'Wir sind fast bei der Zufluch. Beeilung.', null, null, null, null, null, '0', '0', '0', '0', 'kayra SAY_END');
INSERT INTO `script_texts` VALUES ('-1000348', 'Ah...the wondrous sound of kodos. I love the way they make the ground shake... inspect the beast for me.', null, null, 'Ah... das wundervolle Geräusch von Kodos. Ich finde es einfach herrlich, wie sie den Erdboden zum Beben bringen... bitte untersucht das Tier für mich.', null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_1');
INSERT INTO `script_texts` VALUES ('-1000349', 'Hey, look out with that kodo! You had better inspect that beast before i give you credit!', null, null, 'He, passt mit dem Kodo da auf! Ihr solltet das Vieh besser mal gut untersuchen, bevor ich Euch irgendetwas gebe.', null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_2');
INSERT INTO `script_texts` VALUES ('-1000350', 'That kodo sure is a beauty. Wait a minute, where are my bifocals? Perhaps you should inspect the beast for me.', null, null, 'Dieses Kodo ist ja wirklich ein Prachtstück. Moment mal, wo ist meine Nahsichtbrille? Vielleicht solltet Ihr das Tier für mich genauer untersuchen.', null, null, null, null, null, '0', '0', '0', '0', 'kodo round SAY_SMEED_HOME_3');
INSERT INTO `script_texts` VALUES ('-1000351', 'You, there! Hand over that moonstone and nobody gets hurt!', null, null, 'Ihr da! Gebt mir den Mondstein, dann wird keiner verletzt!', null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_START');
INSERT INTO `script_texts` VALUES ('-1000352', '%s takes the Southfury moonstone and escapes into the river. Follow her!', null, null, '%s nimmt den Mondstein des Südstroms und flieht in den Fluss. Folgt ihr!', null, null, null, null, null, '0', '3', '0', '0', 'sprysprocket EMOTE_START');
INSERT INTO `script_texts` VALUES ('-1000353', 'Just chill!', null, null, 'Locker bleiben!', null, null, null, null, null, '0', '4', '0', '0', 'sprysprocket SAY_WHISPER_CHILL');
INSERT INTO `script_texts` VALUES ('-1000354', 'Stupid grenade picked a fine time to backfire! So much for high quality goblin engineering!', null, null, 'Die dumme Granate hat sich ja eine tolle Zeit ausgesucht, um fehl zu zünden! So viel zum Thema hochwertige Gobliningenieurskunst!', null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_GRENADE_FAIL');
INSERT INTO `script_texts` VALUES ('-1000355', 'All right, you win! I surrender! Just don\'t hurt me!', null, null, 'In Ordnung, Ihr gewinnt! Ich gebe auf! Tut mir bloß nicht weh!', null, null, null, null, null, '0', '1', '0', '0', 'sprysprocket SAY_END');
INSERT INTO `script_texts` VALUES ('-1000356', 'Okay, okay... gimme a minute to rest now. You gone and beat me up good.', null, null, 'Gut, gut... lasst mir eine Minute Zeit, mich zu erholen. Ihr habt mich ganz schön in die Mangel genommen.', null, null, null, null, null, '0', '0', '1', '14', 'calvin SAY_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000357', 'Let\'s go before they find out I\'m free!', null, null, 'Wir sollten abhauen, bevor sie feststellen, dass ich frei bin!', null, null, null, null, null, '0', '0', '0', '1', 'KAYA_SAY_START');
INSERT INTO `script_texts` VALUES ('-1000358', 'Look out! We\'re under attack!', null, null, 'Passt auf! Wir werden angegriffen!', null, null, null, null, null, '0', '0', '0', '0', 'KAYA_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000359', 'Thank you for helping me. I know my way back from here.', null, null, 'Vielen Dank für Eure Hilfe. Von hier aus kenne ich den Weg zurück.', null, null, null, null, null, '0', '0', '0', '0', 'KAYA_END');
INSERT INTO `script_texts` VALUES ('-1000360', 'The strands of LIFE have been severed! The Dreamers must be avenged!', null, null, 'Die Fäden des LEBENS wurden durchtrennt! Die Träumer müssen gerächt werden!', null, null, null, null, null, '0', '1', '0', '0', ' ysondre SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000361', 'Come forth, ye Dreamers - and claim your vengeance!', null, null, 'Tretet vor, ihr Träumer, und nehmt eure Rache!', null, null, null, null, null, '0', '1', '0', '0', ' ysondre SAY_SUMMONDRUIDS');
INSERT INTO `script_texts` VALUES ('-1000362', 'Let\'s go $N. I am ready to reach Whitereach Post.', null, null, 'Gehen wir, $N. Ich bin bereit, mich zum Weißgipfelposten aufzumachen.', null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_START');
INSERT INTO `script_texts` VALUES ('-1000363', 'Now this looks familiar. If we keep heading east, I think we can... Ahh, Wyvern on the attack!', null, null, 'Das kommt mir vertraut vor. Wenn wir nach Osten ziehen, können wir wahrscheinlich... Ahh! Flügeldrachen im Angriff!', null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_WYVERN');
INSERT INTO `script_texts` VALUES ('-1000364', 'Thanks a bunch... I can find my way back to Whitereach Post from here. Be sure to talk with Motega Firemane; perhaps you can keep him from sending me home.', null, null, 'Tausend Dank... Von hier aus finde ich den Rückweg zum Weißgipfelposten. Sprecht unbedingt mit Motega Feuermähne, vielleicht könnt Ihr ihn überreden, mich nicht nach Hause zu schicken.', null, null, null, null, null, '0', '0', '1', '0', 'paoka SAY_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000365', 'Be on guard... Arnak has some strange power over the Grimtotem... they will not be happy to see me escape.', null, null, 'Aber seht Euch vor... Arnak hat eine seltsame Macht über die Grimmtotem... meine Flucht wird sie nicht erfreuen.', null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_START');
INSERT INTO `script_texts` VALUES ('-1000366', 'Look out, the Grimtotem are upon us!', null, null, 'Vorsicht, die Grimmtotem greifen uns an!', null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_LOOK_OUT');
INSERT INTO `script_texts` VALUES ('-1000367', 'Here they come.', null, null, 'Da sind sie.', null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_HERE_COME');
INSERT INTO `script_texts` VALUES ('-1000368', 'More Grimtotems are coming this way!', null, null, 'Da kommen noch mehr Grimmtotem!', null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_MORE');
INSERT INTO `script_texts` VALUES ('-1000369', 'Finally, free at last... I must be going now, thanks for helping me escape. I can get back to Freewind Post by myself.', null, null, 'Endlich frei... Ich muss jetzt gehen. Danke, dass Ihr mir zur Flucht verholfen habt, Zum Freiwindposten finde ich allein zurück.', null, null, null, null, null, '0', '0', '1', '0', 'lakota SAY_LAKO_END');
INSERT INTO `script_texts` VALUES ('-1000370', 'Stay close, $n. I\'ll need all the help I can get to break out of here. Let\'s go!', null, null, 'Bleibt in der Nähe, $N. Ich werde jede erdenkliche Hilfe brauchen, um hier rauszukommen. Los!', null, null, null, null, null, '0', '0', '1', '1', 'gilthares SAY_GIL_START');
INSERT INTO `script_texts` VALUES ('-1000371', 'At last! Free from Northwatch Hold! I need a moment to catch my breath...', null, null, 'Endlich! Aus der Feste Nordwacht entkommen! Ich muss erst einmal Atem holen...', null, null, null, null, null, '0', '0', '1', '5', 'gilthares SAY_GIL_AT_LAST');
INSERT INTO `script_texts` VALUES ('-1000372', 'Now i feel better. Let\'s get back to Ratchet. Come on, $n.', null, null, 'Jetzt fühle ich mich besser. Gehen wir zurück nach Ratschet. Kommt schon, $N', null, null, null, null, null, '0', '0', '1', '23', 'gilthares SAY_GIL_PROCEED');
INSERT INTO `script_texts` VALUES ('-1000373', 'Looks like the Southsea Freebooters are heavily entrenched on the coast. This could get rough.', null, null, 'Sieht ganz so aus, als hätten sich die Südmeerfreibeuter an der Küste schwer verschanzt. Das könnte hart werden.', null, null, null, null, null, '0', '0', '1', '25', 'gilthares SAY_GIL_FREEBOOTERS');
INSERT INTO `script_texts` VALUES ('-1000374', 'Help! $C attacking!', null, null, 'Hilfe! $C greift uns an!', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000375', '$C heading this way fast! Time for revenge!', null, null, '$C in schnellem Anmarsch! Zeit für Rache!', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000376', '$C coming right at us!', null, null, '$C kommt direkt auf uns zu!', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1000377', 'Get this $C off of me!', null, null, 'Helft mir, $C von mir weg zu bekommen!', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_AGGRO_4');
INSERT INTO `script_texts` VALUES ('-1000378', 'Almost back to Ratchet! Let\'s keep up the pace...', null, null, 'Schon fast zurück in Ratschet! Lasst uns das Tempo halten...', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_ALMOST');
INSERT INTO `script_texts` VALUES ('-1000379', 'Ah, the sweet salt air of Ratchet.', null, null, 'Ah, die köstliche Salzluft von Ratschet.', null, null, null, null, null, '0', '0', '1', '0', 'gilthares SAY_GIL_SWEET');
INSERT INTO `script_texts` VALUES ('-1000380', 'Captain Brightsun, $N here has freed me! $N, i am certain the Captain will reward your bravery.', null, null, 'Kapitän Blendsonn $N hat mich befreit! $N, der Hauptmann wird Eure Tapferkeit sicher belohnen.', null, null, null, null, null, '0', '0', '1', '66', 'gilthares SAY_GIL_FREED');
INSERT INTO `script_texts` VALUES ('-1000381', 'I sense the tortured spirits, $n. They are this way, come quickly!', null, null, 'Ich fühle die gequälten Geister, $N. Wir müssen da entlang. Kommt schnell!', null, null, null, null, null, '0', '0', '0', '1', 'wilda SAY_WIL_START');
INSERT INTO `script_texts` VALUES ('-1000382', 'Watch out!', null, null, 'Gebt Acht!', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000383', 'Naga attackers! Defend yourself!', null, null, 'Naga greifen an! Verteidigt Euch!', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000384', 'Grant me protection $n, i must break trough their foul magic!', null, null, 'Gebt mir Schutz, $N, Ich muss ihre üble Magie durchbrechen!', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `script_texts` VALUES ('-1000385', 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', null, null, 'Die Naga der Echsennarbe sind besonders grausam gegenüber ihren Gefangenen. Es ist ein Wunder, dass ich in diesem nassen Gefängnis überhaupt so lange überlebt habe. Die Erdenmutter sei Dank.', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `script_texts` VALUES ('-1000386', 'Now we must find the exit.', null, null, 'Jetzt müssen wir den Ausgang finden.', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `script_texts` VALUES ('-1000387', 'Lady Vashj must answer for these atrocities. She must be brought to justice!', null, null, 'Lady Vashj muss für diese Gräueltaten zur Rechenschaft gezogen werden!', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `script_texts` VALUES ('-1000388', 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', null, null, 'Die Aufgewühltheit der großen Wasserwege in Azeroth und Draenor ist die direkte Folge der gequälten Wassergeister.', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `script_texts` VALUES ('-1000389', 'It shouldn\'t be much further, $n. The exit is just up ahead.', null, null, 'Es sollte nicht mehr weit sein, $N. Der Ausgang liegt direkt vor uns.', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `script_texts` VALUES ('-1000390', 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', null, null, 'Danke, $N. Bitte kehrt zu meinen Brüdern beim Altar der Verdammnis, in der Nähe der Hand von Gul\'dan, zurück, und sagt ihnen, dass Wilda in Sicherheit ist. Möge die Erdenmutter über Euch wachen...', null, null, null, null, null, '0', '0', '0', '0', 'wilda SAY_WIL_END');
INSERT INTO `script_texts` VALUES ('-1000391', 'I\'m Thirsty.', null, null, 'Ich bin so durstig.', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_THIRST');
INSERT INTO `script_texts` VALUES ('-1000392', 'Torta must be so worried.', null, null, 'Torta, muss sich schon solche Sorgen machen.', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_WORRIED');
INSERT INTO `script_texts` VALUES ('-1000393', 'Torta, my love! I have returned at long last.', null, null, 'Torta, meine Liebe! Ich bin endlich zurückgekehrt.', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_1');
INSERT INTO `script_texts` VALUES ('-1000394', 'You have any idea how long I\'ve been waiting here? And where\'s dinner? All that time gone and nothing to show for it?', null, null, 'Weißt du überhaupt, wie lange ich hier schon warte? Und wo ist das Abendessen? Nach dieser ganzen Zeit hast du nichts vorzuweisen?', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_2');
INSERT INTO `script_texts` VALUES ('-1000395', 'My dearest Torta. I have been gone for so long. Finally we are reunited. At long last our love can blossom again.', null, null, 'Meine liebste Torta, ich war so lange fort. Endlich sind wir wieder vereint und unsere Liebe kann erneut erblühen.', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_3');
INSERT INTO `script_texts` VALUES ('-1000396', 'Enough with the rambling. I am starving! Now, get your dusty shell into that ocean and bring momma some grub.', null, null, 'Wir sind jetzt lange genug umhergewandert. Ich bin am Verhungern! Also beweg deine staubige Schale ins Meer und bring Mama ein paar Larven.', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_4');
INSERT INTO `script_texts` VALUES ('-1000397', 'Yes Torta. Whatever your heart desires...', null, null, 'Ja Torta. Was immer dein Herz begehrt...', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TOOG_POST_5');
INSERT INTO `script_texts` VALUES ('-1000398', 'And try not to get lost this time...', null, null, 'Und verlauf dich dieses Mal nicht wieder...', null, null, null, null, null, '0', '0', '0', '0', 'tooga SAY_TORT_POST_6');
INSERT INTO `script_texts` VALUES ('-1000399', 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', null, null, 'Frieden ist nur ein flüchtiger Traum! Von nun an herrscht der ALPTRAUM!', null, null, null, null, null, '0', '1', '0', '0', 'taerar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000400', 'Children of Madness - I release you upon this world!', null, null, 'Kinder des Wahnsinns, ich schenke euch diese Welt!', null, null, null, null, null, '0', '1', '0', '0', 'taerar SAY_SUMMONSHADE');
INSERT INTO `script_texts` VALUES ('-1000401', 'Hope is a DISEASE of the soul! This land shall wither and die!', null, null, 'Die Hoffnung ist eine KRANKHEIT der Seele. Dieses Land wird verdorren und sterben!', null, null, null, null, null, '0', '1', '0', '0', 'emeriss SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000402', 'Taste your world\'s corruption!', null, null, 'Kostet die Verderbnis Eurer Welt!', null, null, null, null, null, '0', '1', '0', '0', 'emeriss SAY_CASTCORRUPTION');
INSERT INTO `script_texts` VALUES ('-1000403', 'Rin\'ji is free!', null, null, 'Rin\'ji ist frei!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_RIN_FREE');
INSERT INTO `script_texts` VALUES ('-1000404', 'Attack my sisters! The troll must not escape!', null, null, 'Zum Angriff, Schwestern! Der Troll darf nicht entkomen!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_RIN_BY_OUTRUNNER');
INSERT INTO `script_texts` VALUES ('-1000405', 'Rin\'ji needs help!', null, null, 'Rin\'ji braucht Hilfe!', null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_HELP_1');
INSERT INTO `script_texts` VALUES ('-1000406', 'Rin\'ji is being attacked!', null, null, 'Rin\'ji wird angegriffen!', null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_HELP_2');
INSERT INTO `script_texts` VALUES ('-1000407', 'Rin\'ji can see road now, $n. Rin\'ji knows the way home.', null, null, 'Rin\'ji kann jetzt die Straße sehen, $N. Rin\'ji kennt den Heimweg.', null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000408', 'Rin\'ji will tell you secret now... $n, should go to the Overlook Cliffs. Rin\'ji hid something on island  there', null, null, 'Rin\'ji wird Euch Geheimnis sagen... $N sollte zu den Aussichtsklippen gehen. Rin\'ji hat dort auf der Insel was versteckt.', null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_PROGRESS_1');
INSERT INTO `script_texts` VALUES ('-1000409', 'You find it, you keep it! Don\'t tell no one that Rin\'ji talked to you!', null, null, 'Ihr finden, Ihr behalten. Niemandem erzählen, dass Rin\'ji mit Euch geredet!', null, null, null, null, null, '0', '0', '1', '0', 'SAY_RIN_PROGRESS_2');
INSERT INTO `script_texts` VALUES ('-1000410', 'Here they come! Defend yourself!', null, null, 'Hier kommen sie! Verteidigt euch!', null, null, null, null, null, '0', '0', '1', '5', 'kanati SAY_KAN_START');
INSERT INTO `script_texts` VALUES ('-1000411', 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak for you back in the tower.', null, null, 'Warum kümmern wir uns nicht gleich jetzt um Euch, Hendel? Lady Prachtmeer wünscht, Euch im Turm zu sprechen.', null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `script_texts` VALUES ('-1000412', 'Please... please... Miss Proudmore. I didn\'t mean to...', null, null, 'Bitte... bitte... Lady Prachtmeer. Ich wollte doch nicht...', null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `script_texts` VALUES ('-1000413', 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', null, null, 'Ich muss mich entschuldigen, dass ich so lange gebraucht habe, um hierher zu kommen. Ich wollte, dass Lady Prachtmeer ebenfalls anwesend ist.', null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `script_texts` VALUES ('-1000414', 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', null, null, 'Wir können nur noch kurz bleiben, dann müssen wir zum Turm zurück. Wenn Ihr noch mehr mit uns besprechen wollt, könnt Ihr uns dort antreffen.', null, null, null, null, null, '0', '0', '7', '0', 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `script_texts` VALUES ('-1000415', '%s, too injured, gives up the chase.', null, null, '%s ist zu schwer verletzt und gibt die Jagd auf.', null, null, null, null, null, '0', '2', '0', '0', 'hendel EMOTE_SURRENDER');
INSERT INTO `script_texts` VALUES ('-1000416', 'Well, I\'m not sure how far I\'ll make it in this state... I\'m feeling kind of faint...', null, null, 'Ich weiß nicht, wie weit ich in dem Zustand komme... Ich fühle mich einer Ohnmacht nahe...', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_1');
INSERT INTO `script_texts` VALUES ('-1000417', 'Remember, if I faint again, the water that Spraggle gave you will revive me.', null, null, 'Vergesst nicht, wenn ich wieder ohnmächtig werde, kann mich das Wasser wiederbeleben, das Spraggel Euch gab.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_START_2');
INSERT INTO `script_texts` VALUES ('-1000418', 'The heat... I can\'t take it...', null, null, 'Die Hitze... Ich vertrage sie nicht...', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_1');
INSERT INTO `script_texts` VALUES ('-1000419', 'Maybe... you could carry me?', null, null, 'Vielleicht... könnt Ihr mich tragen?', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_2');
INSERT INTO `script_texts` VALUES ('-1000420', 'Uuuuuuggggghhhhh....', null, null, 'Aaaaaggggghhhhh...', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_3');
INSERT INTO `script_texts` VALUES ('-1000421', 'I\'m not feeling so well...', null, null, 'Mir geht es gar nicht so gut...', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_FAINT_4');
INSERT INTO `script_texts` VALUES ('-1000422', 'Where... Where am I?', null, null, 'Wo... Wo bin ich?', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_1');
INSERT INTO `script_texts` VALUES ('-1000423', 'I am feeling a little better now, thank you.', null, null, 'Danke, jetzt geht es mir etwas besser.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_2');
INSERT INTO `script_texts` VALUES ('-1000424', 'Yes, I must go on.', null, null, 'Ja, ich muss weiter.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_3');
INSERT INTO `script_texts` VALUES ('-1000425', 'How am I feeling? Quite soaked, thank you.', null, null, 'Wie ich mich fühle? Durch und durch nass.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_WAKE_4');
INSERT INTO `script_texts` VALUES ('-1000426', 'Spraggle! I didn\'t think I\'d make it back!', null, null, 'Spraggel! Ich hätte nicht gedacht, dass ich es zurückschaffen würde!', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_1');
INSERT INTO `script_texts` VALUES ('-1000427', 'Ringo! You\'re okay!', null, null, 'Ringo! Es ist nichts passiert!', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_2');
INSERT INTO `script_texts` VALUES ('-1000428', 'Oh... I\'m feeling faint...', null, null, 'Oh... Ich fühle mich der Ohnmacht nahe...', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_3');
INSERT INTO `script_texts` VALUES ('-1000429', '%s collapses onto the ground.', null, null, '%s wird ohnmächtig und fällt zu Boden.', null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_4');
INSERT INTO `script_texts` VALUES ('-1000430', '%s stands up after a short pause.', null, null, '%s erhebt sich nach einer kurzen Pause.', null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_5');
INSERT INTO `script_texts` VALUES ('-1000431', 'Ugh.', null, null, 'Aaagggh.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_RIN_END_6');
INSERT INTO `script_texts` VALUES ('-1000432', 'Ringo? Wake up! Don\'t worry, I\'ll take care of you.', null, null, 'Ringo? Aufwachen! Keine Bange, ich kümmere mich um Euch.', null, null, null, null, null, '0', '0', '0', '0', 'ringo SAY_SPR_END_7');
INSERT INTO `script_texts` VALUES ('-1000433', '%s fades away after a long pause.', null, null, '%s verblasst nach einer langen Pause.', null, null, null, null, null, '0', '2', '0', '0', 'ringo EMOTE_RIN_END_8');
INSERT INTO `script_texts` VALUES ('-1000434', 'Liladris has been waiting for me at Maestra\'s Post, so we should make haste, $N.', null, null, 'Liladris warte bei Maestras Posten schon auf mich... wir sollten und besser beeilen, $N.', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_START');
INSERT INTO `script_texts` VALUES ('-1000435', '%s looks very sleepy...', null, null, '%s sieht sehr schläfrig aus...', null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_1');
INSERT INTO `script_texts` VALUES ('-1000436', '%s suddenly falls asleep', null, null, '%s schläft plötzlich ein.', null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_2');
INSERT INTO `script_texts` VALUES ('-1000437', '%s begins to drift off...', null, null, '%s nickt langsam ein...w', null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_SLEEP_3');
INSERT INTO `script_texts` VALUES ('-1000438', 'This looks like the perfect place for a nap...', null, null, 'Das könnte der perfekte Platz sein, um ein kleines Nickerchen zu halten...', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_1');
INSERT INTO `script_texts` VALUES ('-1000439', 'Yaaaaawwwwwnnnn...', null, null, 'Gääääähhhhhnnnn...', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_2');
INSERT INTO `script_texts` VALUES ('-1000440', 'Oh, I am so tired...', null, null, 'Ich bin ja so müde...', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_3');
INSERT INTO `script_texts` VALUES ('-1000441', 'You don\'t mind if I stop here for a moment, do you?', null, null, 'Es stört Euch doch nicht, wenn ich einen Moment hier bleibe, oder?', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_SLEEP_4');
INSERT INTO `script_texts` VALUES ('-1000442', 'Be on the alert! The Blackwood furbolgs are numerous in the area...', null, null, 'Seid auf der Hut! In dieser Gegend gibt es äußerst viele Furbolgs der Schwarzfelle...', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_1');
INSERT INTO `script_texts` VALUES ('-1000443', 'It\'s quiet... Too quiet...', null, null, 'Es ist still... Zu still...', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_ALERT_2');
INSERT INTO `script_texts` VALUES ('-1000444', 'Oh, I can see Liladris from here... Tell her I\'m here, won\'t you?', null, null, 'Oh, ich kann Liladris von hier aus schon sehen... Sagt Ihr, dass ich hier bin, ja?', null, null, null, null, null, '0', '0', '0', '0', 'kerlonian SAY_KER_END');
INSERT INTO `script_texts` VALUES ('-1000445', '%s wakes up!', null, null, '%s wacht auf!', null, null, null, null, null, '0', '2', '0', '0', 'kerlonian EMOTE_KER_AWAKEN');
INSERT INTO `script_texts` VALUES ('-1000446', 'A-Me good. Good, A-Me. Follow... follow A-Me. Home. A-Me go home.', null, null, 'A-Me. Gut, A-Me. Folgen... A-Me folgen. Heim. A-Me geht heim.', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_START');
INSERT INTO `script_texts` VALUES ('-1000447', 'Good... good, A-Me. A-Me good. Home. Find home.', null, null, 'Gut... gut, A-Me. A-Me gut. Heim. Heim suchen.', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_PROGRESS');
INSERT INTO `script_texts` VALUES ('-1000448', 'A-Me home! A-Me good! Good A-Me. Home. Home. Home.', null, null, 'A-Me daheim! A-Me, gut! Gute A-Me. Daheim. Daheim. Daheim', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_END');
INSERT INTO `script_texts` VALUES ('-1000449', '$c, no hurt A-Me. A-Me good.', null, null, '%s A-Me nicht wehtun. A-Me gut.', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1000450', 'Danger. Danger! $c try hurt A-Me.', null, null, 'Gefahr. Gefahr! %s will A-Me wehtun.', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1000451', 'Bad, $c. $c, bad!', null, null, 'Böser %s. %s böse!', null, null, null, null, null, '0', '0', '0', '0', 'ame01 SAY_AME_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1000452', 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $n.', null, null, 'Mir ist ein Feuer auf der Insel dort drüben aufgefallen. Auch ein Mensch. Lasst uns nachschauen, $N.', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_START');
INSERT INTO `script_texts` VALUES ('-1000453', 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', null, null, 'Das ist Reethe. Mal sehen, was er zu sagen hat, ja?', null, null, null, null, null, '0', '0', '1', '1', 'ogron SAY_OGR_SPOT');
INSERT INTO `script_texts` VALUES ('-1000454', 'W-what do you want? Just leave me alone...', null, null, 'W...was wollt Ihr? Lasst mich einfach in Ruhe...', null, null, null, null, null, '0', '0', '0', '6', 'ogron SAY_OGR_RET_WHAT');
INSERT INTO `script_texts` VALUES ('-1000455', 'I swear. I didn\'t steal anything from you! Here, take some of my supplies, just go away!', null, null, 'Ich schwöre, ich habe Euch nichts gestohlen! Hier, nehmt ein paar meiner Vorräte, aber geht bloß fort!', null, null, null, null, null, '0', '0', '0', '27', 'ogron SAY_OGR_RET_SWEAR');
INSERT INTO `script_texts` VALUES ('-1000456', 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', null, null, 'Sagt uns einfach, was Ihr über das Gasthaus Zur süßen Ruh wisst, und ich werde Euch nicht den Schädel einschlagen.', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_REPLY_RET');
INSERT INTO `script_texts` VALUES ('-1000457', 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', null, null, 'Ich... nun, ich habe vielleicht ein oder zwei Sachen aus dem Gasthaus mitgenommen... aber was kümmert das einen Oger?', null, null, null, null, null, '0', '0', '0', '6', 'ogron SAY_OGR_RET_TAKEN');
INSERT INTO `script_texts` VALUES ('-1000458', 'Look here, if you don\'t tell me about the fire--', null, null, 'Nun, wenn Ihr mir nichts über das Feuer erzählen wollt...', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_TELL_FIRE');
INSERT INTO `script_texts` VALUES ('-1000459', 'Not one step closer, ogre!', null, null, 'Keinen Schritt näher, Oger!', null, null, null, null, null, '0', '0', '0', '27', 'ogron SAY_OGR_RET_NOCLOSER');
INSERT INTO `script_texts` VALUES ('-1000460', 'And I don\'t know anything about this fire of yours...', null, null, 'Und ich weiß absolut nichts über Euer Feuer...', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_NOFIRE');
INSERT INTO `script_texts` VALUES ('-1000461', 'What was that? Did you hear something?', null, null, 'Was war das? Habt Ihr etwas gehört?', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_HEAR');
INSERT INTO `script_texts` VALUES ('-1000462', 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', null, null, 'Paval Reethe! Endlich habe ich Euch gefunden. Verkehrt Ihr jetzt schon mit Ogern? Keine Angst, selbst Deserteuren und Verrätern wird ein wenig Gnade gewährt.', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_CAL_FOUND');
INSERT INTO `script_texts` VALUES ('-1000463', 'Private, show Lieutenant Reethe some mercy.', null, null, 'Gefreiter, zeigt gegenüber Leutnant Reethe ein wenig Mitleid.', null, null, null, null, null, '0', '0', '0', '29', 'ogron SAY_OGR_CAL_MERCY');
INSERT INTO `script_texts` VALUES ('-1000464', 'Gladly, sir.', null, null, 'Mit Vergnügen, Sire.', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_HALL_GLAD');
INSERT INTO `script_texts` VALUES ('-1000465', '%s staggers backwards as the arrow lodges itself deeply in his chest.', null, null, '%s taumelt zurück, als sich der Pfeil tief in seine Brust bohrt.', null, null, null, null, null, '0', '2', '0', '0', 'ogron EMOTE_OGR_RET_ARROW');
INSERT INTO `script_texts` VALUES ('-1000466', 'Ugh... Hallan, didn\'t think you had it in you...', null, null, 'Uh... Hallan, das hätte ich Euch gar nicht zugetraut...', null, null, null, null, null, '0', '0', '0', '34', 'ogron SAY_OGR_RET_ARROW');
INSERT INTO `script_texts` VALUES ('-1000467', 'Now, let\'s clean up the rest of the trash, men!', null, null, 'Räumen wir jetzt den Rest dieses Abfalls auf, Männer!', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_CAL_CLEANUP');
INSERT INTO `script_texts` VALUES ('-1000468', 'Damn it! You\'d better not die on me, human!', null, null, 'Verflucht! Ihr sterbt jetzt doch, Mensch!', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_NODIE');
INSERT INTO `script_texts` VALUES ('-1000469', 'Still with us, Reethe?', null, null, 'Seid Ihr noch unter uns, Reethe?', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_SURVIVE');
INSERT INTO `script_texts` VALUES ('-1000470', 'Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot...', null, null, 'Heute muss Euer Glückstag sein. Gut, ich rede. Lasst mich nur in Ruhe. Schaut, Ihr werdet mir nicht glauben, aber... oh, beim Licht, offensichtlich konnte das Mädchen schießen...', null, null, null, null, null, '0', '0', '0', '0', 'ogron SAY_OGR_RET_LUCKY');
INSERT INTO `script_texts` VALUES ('-1000471', 'By the way, thanks for watching my back.', null, null, 'Danke übrigens, dass Ihr mir den Rücken freigehalten habt.', null, null, null, null, null, '0', '0', '1', '0', 'ogron SAY_OGR_THANKS');
INSERT INTO `script_texts` VALUES ('-1000472', '1...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_1');
INSERT INTO `script_texts` VALUES ('-1000473', '2...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_2');
INSERT INTO `script_texts` VALUES ('-1000474', '3...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_3');
INSERT INTO `script_texts` VALUES ('-1000475', '4...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_4');
INSERT INTO `script_texts` VALUES ('-1000476', '5...', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'mana bomb SAY_COUNT_5');
INSERT INTO `script_texts` VALUES ('-1000477', 'Let us leave this place. I\'ve had enough of these madmen!', null, null, 'Lasst uns diesen Ort verlassen. Ich habe genug von diesen Irren!', null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_START');
INSERT INTO `script_texts` VALUES ('-1000478', 'You\'ll go nowhere, fool!', null, null, 'Ihr werdet nirgendwo hingehen, Narr!', null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_A');
INSERT INTO `script_texts` VALUES ('-1000479', 'Beware! More cultists come!', null, null, 'Seht Euch vor! Dort kommen mehr Kultisten!', null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_B');
INSERT INTO `script_texts` VALUES ('-1000480', 'You will not escape us so easily!', null, null, 'Ihr werdet uns nicht so leicht entkommen!', null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_AMBUSH_B_REPLY');
INSERT INTO `script_texts` VALUES ('-1000481', 'I can find my way from here. My friend Mekeda will reward you for your kind actions.', null, null, 'Von hier aus finde ich den Weg alleine. Mein Freund Mekeda wird Euch für Eure gute Tat belohnen.', null, null, null, null, null, '0', '0', '0', '0', 'akuno SAY_AKU_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000482', 'Look out!', null, null, 'Achtung!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_START');
INSERT INTO `script_texts` VALUES ('-1000483', 'Don\'t let them escape! Kill the strong one first!', null, null, 'Lasst sie nicht entfliehen! Tötet den Starken zuerst!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT INTO `script_texts` VALUES ('-1000484', 'More of them coming! Watch out!', null, null, 'Da kommen mehr von ihnen! Passt auf!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE');
INSERT INTO `script_texts` VALUES ('-1000485', 'Where do you think you\'re going? Kill them all!', null, null, 'Was glaubt Ihr, wo Ihr hin wollt? Tötet sie alle!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_MORE_REPLY');
INSERT INTO `script_texts` VALUES ('-1000486', 'Ride the lightning, filth!', null, null, 'Der Blitz möge Euch treffen, Abschaum!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_LIGHTNING');
INSERT INTO `script_texts` VALUES ('-1000487', 'FROST SHOCK!!!', null, null, 'FROSTSCHOCK!', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_SHOCK');
INSERT INTO `script_texts` VALUES ('-1000488', 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', null, null, 'Es ist besser, wenn wir uns jetzt aufteilen, falls sie noch mehr hinter uns her schicken. Hoffentlich schafft es einer von zurück zu Garrosh. Lebt wohl, $GFremder:Fremde;.', null, null, null, null, null, '0', '0', '0', '0', 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1000489', 'Show our guest around Shattrath, will you? Keep an eye out for pickpockets in the lower city.', null, null, 'Führt unseren Gast in Shattrath herum, ja? Gebt auf die Taschendiebe im unteren Viertel acht.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_START');
INSERT INTO `script_texts` VALUES ('-1000490', 'A newcomer to Shattrath! Make sure to drop by later. We can always use a hand with the injured.', null, null, 'Ein Neuankömmling in Shattrath! Kommt doch später einmal vorbei. Wir können immer Hilfe bei den Verletzten brauchen.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_INJURED');
INSERT INTO `script_texts` VALUES ('-1000491', 'Be mindful of what you say, this one\'s being shown around by Khadgar\'s pet.', null, null, 'Passt auf, was Ihr sagt, der hier wird von Khadgars Schoßtierchen herumgeührt.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_MIND_YOU');
INSERT INTO `script_texts` VALUES ('-1000492', 'Are you joking? I\'m a Scryer, I always watch what i say. More enemies than allies in this city, it seems.', null, null, 'Macht ihr Witze? Ich bin ein Seher, ich achte immer darauf, was ich sage. Es gibt wohl mehr Feinde als Freunde hier in dieser Stadt.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_MIND_ALWAYS');
INSERT INTO `script_texts` VALUES ('-1000493', 'Light be with you, $n. And welcome to Shattrath.', null, null, 'Möge das Licht mit Euch sein, $N. Und willkommen in Shattrath.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_ALDOR_GREET');
INSERT INTO `script_texts` VALUES ('-1000494', 'We\'re rather selective of who we befriend, $n. You think you have what it takes?', null, null, 'Wir sind sehr wählerisch, wenn es um unsere Freunde geht, $N. Denkt Ihr, Ihr habt, was es dazu braucht?', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_SCRYER_GREET');
INSERT INTO `script_texts` VALUES ('-1000495', 'Khadgar himself is showing you around? You must have made a good impression, $n.', null, null, 'Khadgar selbst führt Euch herrum? Ihr müsst einen guten Eindruck hinterlassen haben, $N.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_KHAD_HAGGARD');
INSERT INTO `script_texts` VALUES ('-1000496', '%s lifts its head into the air, as if listening for something.', null, null, '%s hebt lauschend den Kopf in die Höhe.', null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `script_texts` VALUES ('-1000497', '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', null, null, '%s lässt ein Heulen in Richtung der nördlichen Berge los und gibt Euch ein Zeichen zu folgen.', null, null, null, null, null, '0', '2', '0', '0', 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `script_texts` VALUES ('-1000498', 'Welcome, kind spirit. What has brought you to us?', null, null, 'Willkommen. Was hat Euch zu uns geführt?', null, null, null, null, null, '0', '0', '0', '0', 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `script_texts` VALUES ('-1000499', 'Puny $r wanna fight %s? Me beat you! Me boss here!', null, null, 'Mickriger $R will gegen Anführer Mok\'Morokk kämpfen? Ich Euch schlagen. Ich hier Boss!', null, null, null, null, null, '0', '0', '1', '0', 'morokk SAY_MOR_CHALLENGE');
INSERT INTO `script_texts` VALUES ('-1000500', 'Me scared! Me run now!', null, null, 'Ich Angst! Ich jetzt wegrennen!', null, null, null, null, null, '0', '1', '0', '0', 'morokk SAY_MOR_SCARED');
INSERT INTO `script_texts` VALUES ('-1000501', 'Are you sure that you are ready? If we do not have a group of your allies to aid us, we will surely fail.', null, null, 'Seid Ihr sicher, dass Ihr bereit seid? Falls uns keine Gruppe Eurer Verbündeten hilft, werden wir mit Sicherheit verlieren.', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_START1');
INSERT INTO `script_texts` VALUES ('-1000502', 'This will be a though fight, $n. Follow me closely.', null, null, 'Das wird ein Harter Kampf, $N. Bleibt nahe bei mir!', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_START2');
INSERT INTO `script_texts` VALUES ('-1000503', 'This is the brazier, $n. Put it out. Vorsha is a beast, worthy of praise from no one!', null, null, 'Das ist die Kohlenpfanne, $N. Löscht sie. Vorsha ist eine Bestie, die von niemandem Lob verdient!', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_BRAZIER');
INSERT INTO `script_texts` VALUES ('-1000504', 'Now we must wait. It won\'t be long before the naga realize what we have done.', null, null, 'Jetzt müssen wir warten. Es wird nicht lange dauern, bis die Naga erkennen, was wir getan haben.', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_BRAZIER_WAIT');
INSERT INTO `script_texts` VALUES ('-1000505', 'Be on your guard, $n!', null, null, 'Seid auf der Hut, $N!', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_ON_GUARD');
INSERT INTO `script_texts` VALUES ('-1000506', 'Perhaps we will get a moment to rest. But i will not give up until we have faced off against Vorsha!', null, null, 'Vielleicht können wir uns einen Moment lang ausruhen. Aber ich werde nicht aufgeben, bis wir Vorsha gegenübergetreten sind!', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_REST');
INSERT INTO `script_texts` VALUES ('-1000507', 'We have done it!', null, null, 'Wir haben es geschafft!', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_DONE');
INSERT INTO `script_texts` VALUES ('-1000508', 'You have my deepest gratitude. I thank you.', null, null, 'Ihr habt meine tiefe Anerkennung. Ich danke Euch.', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_GRATITUDE');
INSERT INTO `script_texts` VALUES ('-1000509', 'I am going to patrol the area for a while longer and ensure that things are truly safe.', null, null, 'Ich werde noch eine Weile in diesem Bereich patrouillieren, damit wirklich alles sicher ist.', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_PATROL');
INSERT INTO `script_texts` VALUES ('-1000510', 'Please return to Zoram\'gar and report our success to the Warsong runner.', null, null, 'Kehrt bitte nach Zoram\'gar zurück und meldet dem Läufer des Kriegshymnenklans unseren Erfolg.', null, null, null, null, null, '0', '0', '1', '0', 'muglash SAY_MUG_RETURN');
INSERT INTO `script_texts` VALUES ('-1000511', 'Aright, listen up! Form a circle around me and move out!', null, null, 'So, hört zu! Formt einen Kreis um mich, dann geht es los!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_START');
INSERT INTO `script_texts` VALUES ('-1000512', 'Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return.', null, null, 'Nun gut, $R. beschützt uns während wir arbeiten, und wir werden Euch bezahlen, wenn wir zurück sind.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_KEEP_SAFE');
INSERT INTO `script_texts` VALUES ('-1000513', 'The dig site is just north of here.', null, null, 'Die Ausgrabungsstätte ist direkt nördlich von hier.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NORTH');
INSERT INTO `script_texts` VALUES ('-1000514', 'We\'re here! Start diggin\'!', null, null, 'Wir sind da! Fangt mit dem Graben an!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_ARRIVE');
INSERT INTO `script_texts` VALUES ('-1000515', 'I think there\'s somethin\' buried here, beneath the sand!', null, null, 'Ich glaube hier ist etwas vergraben, unter dem Sand!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_BURIED');
INSERT INTO `script_texts` VALUES ('-1000516', 'Almost got it!', null, null, 'Ich habe es fast!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_ALMOST');
INSERT INTO `script_texts` VALUES ('-1000517', 'By brann\'s brittle bananas! What is it!? It... It looks like a drum.', null, null, 'Bei Branns brüchigen Bananen! Was ist das? Es sieht aus wie eine Trommel...', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DRUM');
INSERT INTO `script_texts` VALUES ('-1000518', 'Wow... a drum.', null, null, 'Toll. Eine Trommel.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DRUM_REPLY');
INSERT INTO `script_texts` VALUES ('-1000519', 'This discovery will surely rock the foundation of modern archaeology.', null, null, 'Diese Entdeckung wird die moderne Archäologie in ihren Gunsten erschüttern.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DISCOVERY');
INSERT INTO `script_texts` VALUES ('-1000520', 'Yea, great. Can we leave now? This desert is giving me hives.', null, null, 'Ja, super. Können wir jetzt gehen? In der Wüste kriege ich eine Gänsehaut...', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_DISCOVERY_REPLY');
INSERT INTO `script_texts` VALUES ('-1000521', 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', null, null, 'Seid Ihr wahnsinnig? Ihr erwartet von mir, dass ich eine Trommel zurücklasse, ohne sie zuvor zu schlagen? Nicht dieser Sohn von Eisenschmiede! Nein, mein Herr!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE');
INSERT INTO `script_texts` VALUES ('-1000522', 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', null, null, 'Das erinnert mich an die Zeit, als wir für Euch in Silithus nach Spuren von Sandgnomen suchen mussten.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY1');
INSERT INTO `script_texts` VALUES ('-1000523', 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', null, null, 'Oder als Ihr uns erzählt habt, Ihr hättet ein Heilmittel für die Seuche des 20. Jahrhundert gefunden. Was ist das überhaupt, ein 20. Jahrhundert?', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY2');
INSERT INTO `script_texts` VALUES ('-1000524', 'I don\'t think it can top the one time where he told us that he\'d heard that Artha\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Artha\'s cousin?', null, null, 'Nichts geht über das Mal, als er uns erzählt hat, unter einem Gletscher in Winterquell wäre ein Skelett des \"Cousins\" von Arthas eingefroren. Ich bitte Euch, Letoll. Ich meine, ehrlich... ein Cousin von Arthas?', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY3');
INSERT INTO `script_texts` VALUES ('-1000525', 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', null, null, 'Ich weiß nicht. Noch besser war doch die Geschichte, als er uns überzeugen wollte, wir hätten nur Hirngespinste von irgendjemandem, der uns für sein persönliches Vergnügen benutzt. Das ist beim Abendessen mit der Familie ganz toll angekommen.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_NO_LEAVE_REPLY4');
INSERT INTO `script_texts` VALUES ('-1000526', 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', null, null, 'Haltet die Klappe! Ich werde auf die Trommel schlagen, und keine Widerrede!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_SHUT');
INSERT INTO `script_texts` VALUES ('-1000527', 'Say, do you guys hear that?', null, null, 'Sagt, habt Ihr das gehört?', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_REPLY_HEAR');
INSERT INTO `script_texts` VALUES ('-1000528', 'IN YOUR FACE! I told you there was somethin\' here!', null, null, 'NA, WAS HAB ICH GESAGT? Ich sagte Euch doch, dass hier etwas ist!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_IN_YOUR_FACE');
INSERT INTO `script_texts` VALUES ('-1000529', 'Don\'t just stand there! Help him out!', null, null, 'Steht da nicht rum! Helft, sie da rauszuholen!', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_HELP_HIM');
INSERT INTO `script_texts` VALUES ('-1000530', '%s picks up the drum.', null, null, '%s hebt die Trommel auf.', null, null, null, null, null, '0', '2', '0', '0', 'letoll EMOTE_LE_PICK_UP');
INSERT INTO `script_texts` VALUES ('-1000531', 'You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', null, null, 'Ihr wart eine enorme Hilfe, $R! Lasst uns hier verschwinden, bevor mehr von diesen Dingern auftauchen! Ich lasse Zwergowitz wissen, dass Ihr Eure Aufgabe bewundernswert erfüllt habt.', null, null, null, null, null, '0', '0', '0', '0', 'letoll SAY_LE_THANKS');
INSERT INTO `script_texts` VALUES ('-1000532', 'At your command, my liege...', null, null, 'Wie Ihr befehlt, mein Herr...', null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE1');
INSERT INTO `script_texts` VALUES ('-1000533', 'As you desire, Lord Illidan.', null, null, 'Wie Ihr wünscht, Fürst Illidan.', null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE2');
INSERT INTO `script_texts` VALUES ('-1000534', 'Yes, Lord Illidan, I would sacrifice to you this magnificent physique. On this day you will fall - another victim of Torloth...', null, null, 'Für Fürst Illidan würde ich selbst diesen großartigen Körperbau opfern. An diesem Tag werdet ihr fallen... ein weiteres Opfer von Torloth...', null, null, null, null, null, '0', '0', '0', '0', 'torloth TORLOTH_DIALOGUE3');
INSERT INTO `script_texts` VALUES ('-1000535', 'Destroy them, Torloth. Let lose their blood like a river upon this hallowed ground.', null, null, 'Zerstört sie, Torloth. Lasst sie auf diesem geheiligten Boden ihr Blut in Strömen vergießen.', null, null, null, null, null, '0', '0', '0', '0', 'lordillidan ILLIDAN_DIALOGUE');
INSERT INTO `script_texts` VALUES ('-1000536', 'What manner of fool dares stand before Illidan Stormrage? Soldiers, destroy these insects!', null, null, 'Welcher Narr wagt es, sich Illidan Sturmgrimm entgegenzustellen? Soldaten, vernichtet dieses Gewürm!', null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON1');
INSERT INTO `script_texts` VALUES ('-1000537', 'You are no challenge for the Crimson Sigil. Mind breakers, end this nonsense.', null, null, 'Ihr seid keine Herausforderung für das Purpursiegel. Willenbrecher, macht diesem Unsinn ein Ende', null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON2');
INSERT INTO `script_texts` VALUES ('-1000538', 'Run while you still can. The highlords come soon...', null, null, 'Lauft, solange Ihr noch könnt. Bald kommen die Hochlords...', null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON3');
INSERT INTO `script_texts` VALUES ('-1000539', 'Torloth your master calls!', null, null, 'Torloth, Euer Meister ruft Euch!', null, null, null, null, null, '0', '1', '0', '0', 'lordillidan ILLIDAN_SUMMON4');
INSERT INTO `script_texts` VALUES ('-1000540', 'So you have defeated the Crimson Sigil. You now seek to challenge my rule? Not even Arthas could defeat me, yet you dare to even harbor such thoughts? Then I say to you, come! Come $N! The Black Temple awaits...', null, null, 'Ihr habt also das Purpursiegel besiegt. Nun wollt Ihr meine Herrschaft herausfordern? Nicht einmal Arthas konnte mich besiegen! Wie könnt Ihr es wagen, nur daran zu denken? Dann kommt doch! Kommt, $N! Der Schwarze Tempel erwartet Euch...', null, null, null, null, null, '0', '1', '0', '0', 'lordillidan EVENT_COMPLETED');
INSERT INTO `script_texts` VALUES ('-1000541', '%s jumps into the moonwell and goes underwater...', null, null, '%s springt in den Mondbrunnen und versinkt im Wasser...', null, null, null, null, null, '0', '2', '0', '0', 'kitten EMOTE_SAB_JUMP');
INSERT INTO `script_texts` VALUES ('-1000542', '%s follows $n obediertly.', null, null, '%s folg gehorsam $N.', null, null, null, null, null, '0', '2', '0', '0', 'kitten EMOTE_SAB_FOLLOW');
INSERT INTO `script_texts` VALUES ('-1000543', 'Why have you come here, outsider? You will only find pain! Our fate will be yours...', null, null, 'Warum seid Ihr hierher gekomen, Außenseiter? Ihr werdet nichts als Schmerzen finden! Unser Schicksal wird das Eure sein...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_1');
INSERT INTO `script_texts` VALUES ('-1000544', 'It was ... terrible... the demon...', null, null, 'Es war... furchtbar... der Dämon...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_2');
INSERT INTO `script_texts` VALUES ('-1000545', 'The darkness... the corruption... they came too quickly for anyone to know...', null, null, 'Die Dunkelheit... die Verderbnis... alles geschaah so schnell, niemand hat damit gerechnet...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_3');
INSERT INTO `script_texts` VALUES ('-1000546', 'The darkness will consume all... all the living...', null, null, 'Die Dunkelheit wird alles verzehren... alles, was lebt', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_4');
INSERT INTO `script_texts` VALUES ('-1000547', 'It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed...', null, null, 'Es ist zu spät für uns, Lebender. Ihr solltet mit Eurem Freund von hier verschwinden, bevor man Euch auch noch... einfordert...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_5');
INSERT INTO `script_texts` VALUES ('-1000548', 'It is too late for Jarl... its hold is too strong...', null, null, 'Es ist zu spät für Jarl... er war schon zu lange an diesem Ort...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_6');
INSERT INTO `script_texts` VALUES ('-1000549', 'Go away, whoever you are! Witch Hill is mine... mine!', null, null, 'Wer immer Ihr auch sein mögt, verschwindet! Der Hexenhügel gehört mir... mir!', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_7');
INSERT INTO `script_texts` VALUES ('-1000550', 'The manor... someone else... will soon be consumed...', null, null, 'Das Anwesen... ein anderer... wird bald verzehrt werden...', null, null, null, null, null, '0', '0', '0', '25', 'restless app SAY_RAND_8');
INSERT INTO `script_texts` VALUES ('-1000553', 'Be ready, $N. I hear the council returning. Prepare to ambush!', null, null, 'Haltet Euch bereit, $N. Ich höre den Rat zurückkehren. Macht Euch für einen Hinterhalt bereit!', null, null, null, null, null, '0', '0', '0', '0', 'deathstalker_faerleia SAY_START');
INSERT INTO `script_texts` VALUES ('-1000554', 'Well done. A blow to Arugal no doubt!', null, null, 'Gut gemacht. Ein Schlag für Arugal, kein Zweifel!', null, null, null, null, null, '0', '0', '0', '0', 'deathstalker_faerleia SAY_END');
INSERT INTO `script_texts` VALUES ('-1000561', 'My wounds are grave. Forgive my slow pace but my injuries won\'t allow me to walk any faster.', null, null, 'Meine Wunden sind tief. Vergebt mir, dass ich so langsam bin, aber ich bin zu schwer verletzt, um schneller zu gehen.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_CORPORAL_KEESHAN_1');
INSERT INTO `script_texts` VALUES ('-1000562', 'Ah, fresh air, at last! I need a moment to rest.', null, null, 'Ah, endlich frische Luft! Ich muss mich einen Moment ausruhen.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_CORPORAL_KEESHAN_2');
INSERT INTO `script_texts` VALUES ('-1000563', 'The Blackrock infestation is thick in these parts. I will do my best to keep the pace. Let\'s go!', null, null, 'Die Schwarzfelsplage hat sich in dieser Gegend stark ausgebreitet. Ich werde mein Bestes tun, um das Tempo zu halten. Los, gehen wir!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_CORPORAL_KEESHAN_3');
INSERT INTO `script_texts` VALUES ('-1000564', 'Marshal Marris, sir. Corporal Keeshan of the 12th Sabre Regiment returned from battle and reporting for duty!', null, null, 'Marschall Marris, Sire. Korporal keeshan vom 12. Säbelregiment zurück vom Kampf und meldet sich zum Dienst!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_CORPORAL_KEESHAN_4');
INSERT INTO `script_texts` VALUES ('-1000565', 'Brave adventurer, thank you for rescuing me! I am sure Marshal Marris will reward your kind deed.', null, null, '$GMutiger Abenteurer:Mutige Abenteurerin;, habt Dank für meine Rettung! Ich bin mir sicher, dass Marschall Marris Eure gute Tat belohnen wird.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_CORPORAL_KEESHAN_5');
INSERT INTO `script_texts` VALUES ('-1000566', 'Stand back! Stand clear! The infernal will need to be given a wide berth!', null, null, 'Bleibt zurück! Macht Platz! Die Höllenbestie braucht viel Raum!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_NIBY_1');
INSERT INTO `script_texts` VALUES ('-1000567', 'BOW DOWN TO THE ALMIGHTY! BOW DOWN BEFORE MY INFERNAL DESTRO... chicken?', null, null, 'KNIET NIEDER VOR DEM ALLMÄCHTIGEN! VERNEIGT EUCH VOR MEINEM UNBESIEGBAREN HÖLLEN... Huhn?', null, null, null, null, null, '0', '0', '0', '0', 'SAY_NIBY_2');
INSERT INTO `script_texts` VALUES ('-1000568', '%s rolls on the floor laughing.', null, null, '%s kugelt sich vor lachen.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_IMPSY_1');
INSERT INTO `script_texts` VALUES ('-1000569', 'Niby, you\' re an idiot.', null, null, 'Niby, du bist ein Idiot', null, null, null, null, null, '0', '0', '0', '0', 'SAY_IMPSY_1');
INSERT INTO `script_texts` VALUES ('-1000570', 'Silence, servant! Vengeance will be mine! Death to Stormwind! Death by chicken!', null, null, 'Schweige, Diener! Die Rache ist mein! Tod für Sturmwind! Tod durch Hühnchen!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_NIBY_3');
INSERT INTO `script_texts` VALUES ('-1000571', 'Help! I\'ve only one hand to defend myself with.', null, null, 'Hilfe! Ich habe nur eine Hand, um mich zu verteidigen.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_MIRAN_1');
INSERT INTO `script_texts` VALUES ('-1000572', 'Feel the power of the Dark Iron Dwarves!', null, null, 'Spürt die Macht der Dunkeleisenzwerge!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_DARK_IRON_DWARF');
INSERT INTO `script_texts` VALUES ('-1000573', 'Send them on! I\'m not afraid of some scrawny beasts!', null, null, 'Schickt sie nur her! Ich habe keine Angst vor diesen dürren Bestien!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_MIRAN_2');
INSERT INTO `script_texts` VALUES ('-1000574', 'Ah, here at last! It\'s going to feel so good to get rid of these barrels.', null, null, 'Ah, endlich hier! Ich fühle micht bestimmt viel besser, wenn ich diese Fässer los bin.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_MIRAN_3');
INSERT INTO `script_texts` VALUES ('-1000579', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1000582', 'Help! Please, You must help me!', null, null, 'Hilfe! Bitte, Ihr müsst mir helfen!', null, null, null, null, null, '0', '0', '0', '0', 'Galen - periodic say');
INSERT INTO `script_texts` VALUES ('-1000583', 'Let us leave this place.', null, null, 'Lasst uns von hier fortgehen.', null, null, null, null, null, '0', '0', '0', '0', 'Galen - quest accepted');
INSERT INTO `script_texts` VALUES ('-1000584', 'Look out! The $c attacks!', null, null, 'Passt auf! $C greift an!', null, null, null, null, null, '0', '0', '0', '0', 'Galen - aggro 1');
INSERT INTO `script_texts` VALUES ('-1000585', 'Help! I\'m under attack!', null, null, 'Hilfe! Ich werde angegriffen!', null, null, null, null, null, '0', '0', '0', '0', 'Galen - aggro 2');
INSERT INTO `script_texts` VALUES ('-1000586', 'Thank you $N. I will remember you always. You can find my strongbox in my camp, north of Stonard.', null, null, 'Danke, $N. Ich werde Euch nie vergessen! Ihr findet meine Geldkassette in meinem Lager nördlich von Steinard.', null, null, null, null, null, '0', '0', '0', '0', 'Galen - quest complete');
INSERT INTO `script_texts` VALUES ('-1000587', '%s whispers to $N the secret to opening his strongbox.', null, null, '%s flüstert $N das Geheimnis zu, wie man die Geldkassette öffnet.', null, null, null, null, null, '0', '2', '0', '0', 'Galen - emote whisper');
INSERT INTO `script_texts` VALUES ('-1000588', '%s disappears into the swamp.', null, null, '%s verschwindet im Sumpf.', null, null, null, null, null, '0', '2', '0', '0', 'Galen - emote disapper');
INSERT INTO `script_texts` VALUES ('-1000589', 'Kroshius live? Kroshius crush!', null, null, 'Kroshius leben? Kroshius zermalmen!', null, null, null, null, null, '0', '1', '0', '0', 'SAY_KROSHIUS_REVIVE');
INSERT INTO `script_texts` VALUES ('-1000590', 'Woot!', null, null, 'Jippie!', null, null, null, null, null, '0', '0', '0', '0', 'Captive Child SAY_THANKS_1');
INSERT INTO `script_texts` VALUES ('-1000591', 'I think those weird bird guys were going to eat us. Gross!', null, null, 'Ich glaube, diese seltsamen großen Leute wollten uns essen! Igitt!', null, null, null, null, null, '0', '0', '0', '0', 'Captive Child SAY_THANKS_2');
INSERT INTO `script_texts` VALUES ('-1000592', 'Yay! We\'re free!', null, null, 'Juhu, wir sind frei!.', null, null, null, null, null, '0', '0', '0', '0', 'Captive Child SAY_THANKS_3');
INSERT INTO `script_texts` VALUES ('-1000593', 'Gross!', null, null, 'Eklig!', null, null, null, null, null, '0', '0', '0', '0', 'Captive Child SAY_THANKS_4');
INSERT INTO `script_texts` VALUES ('-1000603', 'Do not test me, scurvy dog! I\'m trained in the way of the Blood Knights!', null, null, 'Stellt meine Geduld nicht auf die Probe, Ihr gemeiner Hund! Ich wurde in der Kunst der Blutritter unterrichtet!', null, null, null, null, null, '0', '0', '0', '0', 'silvermoon harry SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000604', 'I\'ll pay! I\'ll pay! Eeeek! Please don\'t hurt me!', null, null, 'Ich bezahle! Ich bezahle! Uff! Bitte tut mir nich weh!', null, null, null, null, null, '0', '0', '0', '0', 'silvermoon harry SAY_BEATEN');
INSERT INTO `script_texts` VALUES ('-1000615', 'Use my shinies...make weather good again...make undead things go away.', null, null, 'Benutzen mein Glitzer-Glitzer... mach Wetter wieder gut... mach untote Dinger wieder verschwinden.', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_1');
INSERT INTO `script_texts` VALUES ('-1000616', 'We gave shinies to shrine... we not greedy... why this happen?', null, null, 'Wir haben Schrein Glitzer-Glitzer gegeben... wir nicht gierig... warum das passieren?', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_2');
INSERT INTO `script_texts` VALUES ('-1000617', 'I do something bad? I sorry....', null, null, 'Ich was falsch gemacht? Tut mir traurig...', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_3');
INSERT INTO `script_texts` VALUES ('-1000618', 'We not do anything... to them... I no understand.', null, null, 'Wir nix getan... ihnen... Ich nix verstehen das', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_4');
INSERT INTO `script_texts` VALUES ('-1000619', 'Thank...you.', null, null, 'Danke... schön.', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_5');
INSERT INTO `script_texts` VALUES ('-1000620', 'Please take... my shinies. All done...', null, null, 'Bitte nehmen... mein Glitzer-Glitzer. Alles fertig...', null, null, null, null, null, '0', '0', '0', '0', 'mosswalker victim SAY_DIE_6');
INSERT INTO `script_texts` VALUES ('-1000621', 'All systems on-line.  Prepare yourself, we leave shortly.', null, null, 'Alle Systeme startklar. Bereitmachen, es geht in Kürze los.', null, null, null, null, null, '0', '0', '0', '0', 'maxx SAY_START');
INSERT INTO `script_texts` VALUES ('-1000622', 'Be careful in there and come back in one piece!', null, null, 'Gebt da drinnen auf Euch acht und kehrt in einem Stück zurück!', null, null, null, null, null, '0', '0', '0', '0', 'maxx SAY_ALLEY_FAREWELL');
INSERT INTO `script_texts` VALUES ('-1000623', 'Proceed.', null, null, 'Weiter.', null, null, null, null, null, '0', '0', '0', '0', 'maxx SAY_CONTINUE');
INSERT INTO `script_texts` VALUES ('-1000624', 'You\'re back!  Were you able to get all of the machines?', null, null, 'Ihr seid zurück! Konntet Ihr alle Maschinen bekommen?', null, null, null, null, null, '0', '0', '0', '0', 'maxx SAY_ALLEY_FINISH');
INSERT INTO `script_texts` VALUES ('-1000625', '%s gathers the warp chaser\'s blood.', null, null, '%s sammelt das Blut des Sphärenhetzers.', null, null, null, null, null, '0', '2', '0', '0', 'zeppit EMOTE_GATHER_BLOOD');
INSERT INTO `script_texts` VALUES ('-1000628', '%s feeds on the freshly-killed warp chaser.', null, null, '%s frisst von dem frisch erlegten Sphärenhetzer.', null, null, null, null, null, '0', '2', '0', '0', 'hungry ray EMOTE_FEED');
INSERT INTO `script_texts` VALUES ('-1000629', '<cough> <cough> Damsel in distress over here!', null, null, '<räusper> <räusper> Jungfer in Bedrängnis!', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_PERIODIC_1');
INSERT INTO `script_texts` VALUES ('-1000630', 'Hello? Help?', null, null, 'Hallo? Hilfe?', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_PERIODIC_2');
INSERT INTO `script_texts` VALUES ('-1000631', 'Don\'t leave me in here! Cause if you do I will find you!', null, null, 'Lasst mich nicht hier allein! Denn wenn Ihr das tut, werde ich Euch finden...', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_PERIODIC_3');
INSERT INTO `script_texts` VALUES ('-1000632', 'Ok, let\'s get out of here!', null, null, 'Gut, lasst uns hier verschwinden!', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_START');
INSERT INTO `script_texts` VALUES ('-1000633', 'You sure you\'re ready? Take a moment.', null, null, 'Seid Ihr sicher, dass Ihr bereit seid? Lasst Euch Zeit.', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_WAITING');
INSERT INTO `script_texts` VALUES ('-1000634', 'Alright, let\'s do this!', null, null, 'Fein, dann los!', null, null, null, null, null, '0', '0', '0', '0', 'isla starmane - SAY_ISLA_LEAVE_BUILDING');
INSERT INTO `script_texts` VALUES ('-1000644', 'Ouch! That\'s it, I quit the target business!', null, null, 'Autsch! Jetzt reicht\'s, ich bin raus aus dem Zielgeschäft', null, null, null, null, null, '0', '0', '0', '0', 'SAY_LUCKY_HIT_1');
INSERT INTO `script_texts` VALUES ('-1000645', 'My ear! You grazed my ear!', null, null, 'Mein Ohr! Ihr habt mein Ohr getroffen!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_LUCKY_HIT_2');
INSERT INTO `script_texts` VALUES ('-1000646', 'Not the \'stache! Now I\'m asymmetrical!', null, null, 'Nicht der Schnurrbart! Jetzt bin ich asymmetrisch!', null, null, null, null, null, '0', '0', '0', '5', 'SAY_LUCKY_HIT_3');
INSERT INTO `script_texts` VALUES ('-1000647', 'Good shot!', null, null, 'Netter Schuss!', null, null, null, null, null, '0', '0', '0', '4', 'SAY_LUCKY_HIT_APPLE');
INSERT INTO `script_texts` VALUES ('-1000648', 'Stop whining. You\'ve still got your luck.', null, null, 'Hört auf rumzuheulen. Ihr habt doch noch immer Glück.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_DROSTAN_GOT_LUCKY_1');
INSERT INTO `script_texts` VALUES ('-1000649', 'Bah, it\'s an improvement.', null, null, 'Bah, es ist eine Verbesserung.', null, null, null, null, null, '0', '0', '0', '11', 'SAY_DROSTAN_GOT_LUCKY_2');
INSERT INTO `script_texts` VALUES ('-1000650', 'Calm down lad, it\'s just a birdshot!', null, null, 'Ganz ruhig, Junge, es ist doch nur ein Vogelschuss!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_DROSTAN_HIT_BIRD_1');
INSERT INTO `script_texts` VALUES ('-1000651', 'The only thing hurt is your pride, lad! Buck up!', null, null, 'Das Einzige was schmerzt ist dein Stolz, Junge. Bleib munter!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_DROSTAN_HIT_BIRD_2');
INSERT INTO `script_texts` VALUES ('-1000652', 'Me so hungry! YUM!', null, null, 'Ich so hungrig! MJAM!', null, null, null, null, null, '0', '0', '0', '71', 'dragonmaw peon SAY_PEON_1');
INSERT INTO `script_texts` VALUES ('-1000653', 'Hey... me not feel so good.', null, null, 'He... mir nicht gehen so gut.', null, null, null, null, null, '0', '0', '0', '0', 'dragonmaw peon SAY_PEON_2');
INSERT INTO `script_texts` VALUES ('-1000654', 'You is bad orc... baaad... or... argh!', null, null, 'Ihr sein böser Orc... böööser... Or... argh!', null, null, null, null, null, '0', '0', '0', '0', 'dragonmaw peon SAY_PEON_3');
INSERT INTO `script_texts` VALUES ('-1000655', 'Time for eating!?', null, null, 'Zeit zum Fressen?!', null, null, null, null, null, '0', '0', '0', '71', 'dragonmaw peon SAY_PEON_4');
INSERT INTO `script_texts` VALUES ('-1000656', 'It put the mutton in the stomach!', null, null, 'Ich tun Hammel in Magen!', null, null, null, null, null, '0', '0', '0', '71', 'dragonmaw peon SAY_PEON_5');
INSERT INTO `script_texts` VALUES ('-1000666', 'I can sense the SHADOW on your hearts. There can be no rest for the wicked!', null, null, 'Ich fühle die SCHATTEN in Euren Herzen. Niemals darf das Böse Ruhe finden!', null, null, null, null, null, '0', '1', '0', '0', 'lethon SAY_LETHON_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000667', 'Your wicked souls shall feed my power!', null, null, 'Eure verdorbenen Seelen werden meine Kräfte stärken!', null, null, null, null, null, '0', '1', '0', '0', 'lethon SAY_LETHON_SHADE');
INSERT INTO `script_texts` VALUES ('-1000668', '%s releases the last of its energies into the nearby runestone, successfully reactivating it.', null, null, '%s überträgt die noch verbleibende Energie auf den nahegelegenen Runenstein, was diesen erfolgreich reaktiviert.', null, null, null, null, null, '0', '2', '0', '0', 'infused crystal SAY_DEFENSE_FINISH');
INSERT INTO `script_texts` VALUES ('-1000669', 'We will locate the origin of the Nightmare through the fragments you collected, $N. From there, we will pull Eranikus through a rift in the Dream. Steel yourself, $C. We are inviting the embodiment of the Nightmare into our world.', null, null, 'Wir werden die Quelle des Alptraums über die von Euch gesammelten Fragmente ausfindig machen, $N. Von dort aus, werden wir Eranikus durch einen Spalt im Traum in unsere Welt ziehen, Bereitet Euch vor, Jäger. Wir holen jetzt die Verkörperung des Alptraums in unsere Welt ziehen. Bereitet Euch vor, $C. Wir holen jetzt die Verkörperung des Alptraums in diese Welt.', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1000670', 'To Nighthaven! Keep your army close, champion. ', null, null, 'Nach Nachthafen! Sorgt dafür, dass Eure Armee mit Euch Schritt hält, Held!', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1000671', 'The rift will be opened there, above the Lake Elun\'ara. Prepare yourself, $N. Eranikus entry into our world will be wrought with chaos and strife.', null, null, 'Der Spalt wird dort über dem Elun\'ara-See geöffnet. Bereitet Euch vor, $N. Eranikus Eintritt in unsere Welt wird von Chaos und Unruhen begleitet sein.', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1000672', 'He will stop at nothing to get to Malfurion\'s physical manifistation. That must not happen... We must keep the beast occupied long enough for Tyrande to arrive.', null, null, 'Er wird sich von Nichts und Niemanden aufhalten lassen, um Malfurions körperliche Manifestation zu zerstören. Das darf auf keinen Fall geschehen... Wir müssen das Biest solange beschäftigen, bis Tyrande eingetroffen ist.', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1000673', 'Defend Nightaven, hero...', null, null, 'Verteidigt Nachthafen, Held...', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_INTRO_5');
INSERT INTO `script_texts` VALUES ('-1000674', '%s has entered our world', null, null, '%s hat unsere Welt betreten.', null, null, null, null, null, '0', '3', '0', '0', 'eranikus EMOTE_SUMMON_ERANIKUS');
INSERT INTO `script_texts` VALUES ('-1000675', 'Pitful predictable mortals... You know not what you have done! The master\'s will fulfilled. The Moonglade shall be destroyed and Malfurion along with it!', null, null, 'Erbärmliche, berechenbare Sterbliche... Ihr wisst nicht, was Ihr getan habt! Der Wunsch des Meisters hat sich erfüllt. Mondlichtung wird zerstört werden und Malfurion gleich mit!', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_SPAWN');
INSERT INTO `script_texts` VALUES ('-1000676', 'Fiend! Face the might of Cenarius!', null, null, 'Dämon! Spürt die macht von Cenarius!', null, null, null, null, null, '0', '1', '0', '1', 'remulos SAY_REMULOS_TAUNT_1');
INSERT INTO `script_texts` VALUES ('-1000677', '%s lets loose a sinister laugh.', null, null, '%s lacht teuflisch.', null, null, null, null, null, '0', '2', '0', '0', 'eranikus EMOTE_ERANIKUS_LAUGH');
INSERT INTO `script_texts` VALUES ('-1000678', 'You are certanly not your father, insect. Should it interest me, I would crush you with but a swipe of my claws. Turn Shan\'do Stormrage over to me and your pitiful life will be spared along with the lives of your people.', null, null, 'In der Tat seid ihr nicht Eurer Vater, Insekt. Wenn ich wollte, könnte ich Euch mit einem einzigen Schlag vernichten. Übergebt mir Shan\'do Sturmgrim und ich werde Euer erbärmliches Leben und das Leben Eurer Männer verschonen.', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_TAUNT_2');
INSERT INTO `script_texts` VALUES ('-1000679', 'Who is the predictable one, beast? Surely you did not think that we would summon you on top of Malfurion? Your redemption comes, Eranikus. You will be cleansed of this madness - this corruption.', null, null, 'Wer ist hier der Narr, Untier? Ihr habt doch nicht wirklich geglaubt, dass wir Euch in Malfurions Nähe beschwören würden? Eure Erlösung naht, Eranikus. Ihr werdet entgültig von Eurem Wahnsinn befreit werden.', null, null, null, null, null, '0', '1', '0', '1', 'remulos SAY_REMULOS_TAUNT_3');
INSERT INTO `script_texts` VALUES ('-1000680', 'My redemption? You are bold, little one. My redemption comes by the will of my god.', null, null, 'Meine Erlösung? Ihr habt Mut, kleines Wesen. Nur der Wille Gottes kann mir Erlösung verschaffen.', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_TAUNT_4');
INSERT INTO `script_texts` VALUES ('-1000681', '%s roars furiously.', null, null, '%s brüllt vor Wut.', null, null, null, null, null, '0', '2', '0', '0', 'eranikus EMOTE_ERANIKUS_ATTACK');
INSERT INTO `script_texts` VALUES ('-1000682', 'Hurry, $N! We must find protective cover!', null, null, 'Beeilt Euch, $C! Wir müssen Schutz finden!', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_DEFEND_1');
INSERT INTO `script_texts` VALUES ('-1000683', 'Please, champion, protect our people.', null, null, 'Bitte oh Held, beschützt Eure Gefolgsleute.', null, null, null, null, null, '0', '0', '0', '1', 'remulos SAY_REMULOS_DEFEND_2');
INSERT INTO `script_texts` VALUES ('-1000684', 'Rise, servants of the Nightmare! Rise and destroy this world! Let there be no survivors...', null, null, 'Erhebt Euch, ihr Diener des Alptraums! Zeigt Euch und zerstört diese Welt! Niemand soll überleben...', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_SHADOWS');
INSERT INTO `script_texts` VALUES ('-1000685', 'We will battle these fiends, together! Nighthaven\'s Defenders are also among us. They will fight to the death if asked. Now, quickly, we must drive these aberations back to the Nightmare. Destroy them all!', null, null, 'Wer werden diese Dämonen gemeinsam bekämpfen! Die Verteidiger von Nachthafen stehen uns zur Seite, wenn es sein muss, bis in den Tod. Schnell jetzt! Wir müssen diese Kreaturen in den Alptraum zurücktreiben. Vernichtet sie alle!', null, null, null, null, null, '0', '0', '0', '1', 'remulos SAY_REMULOS_DEFEND_3');
INSERT INTO `script_texts` VALUES ('-1000686', 'Where is your savior? How long can you hold out against my attacks?', null, null, 'Wo ist Euer Retter? Wie lange könnt Ihr meinen Angriffen standhalten?', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_ATTACK_1');
INSERT INTO `script_texts` VALUES ('-1000687', 'Defeated my minions? Then face me, mortals!', null, null, 'Ihr habt meine Wächter besiegt? Nun werdet Ihr gegen mich bestehen müssen, Sterbliche!', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_ATTACK_2');
INSERT INTO `script_texts` VALUES ('-1000688', 'Remulos, look how easy they fall before me? You can stop this, fool. Turn the druid over to me and it will all be over...', null, null, 'Remulos, seht wie schnell sie durch meine Hand fallen! Ihr könnt all dem ein Ende bereiten. Übergebt mir den Druiden und alles wird vorbei sein...', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_ATTACK_3');
INSERT INTO `script_texts` VALUES ('-1000689', 'Elune, hear my prayers. Grant us serenity! Watch over our fallen...', null, null, 'Elune, erhöre meine Gebete. Verleihe uns Klarheit! Behüte unsere Gefallen...', null, null, null, null, null, '0', '1', '0', '0', 'tyrande SAY_TYRANDE_APPEAR');
INSERT INTO `script_texts` VALUES ('-1000690', 'Tend to the injuries of the wounded, sisters!', null, null, 'Kümmert euch um die Verwundeten, Schwestern!', null, null, null, null, null, '0', '0', '0', '0', 'tyrande SAY_TYRANDE_HEAL');
INSERT INTO `script_texts` VALUES ('-1000691', 'Seek absolution, Eranikus. All will be forgiven...', null, null, 'Sucht die Erlösung, Eranikus. Alles wird vergeben werden...', null, null, null, null, null, '0', '1', '0', '0', 'tyrande SAY_TYRANDE_FORGIVEN_1');
INSERT INTO `script_texts` VALUES ('-1000692', 'You will be forgiven, Eranikus. Elune will always love you. Break free of the bonds that command you!', null, null, 'Ihr werdet Vergebung erfahren. Eranikus. Elune wird Euch immer lieben. Befreit Euch von den Fesseln, die sich Euer bemächtigt haben.', null, null, null, null, null, '0', '1', '0', '0', 'tyrande SAY_TYRANDE_FORGIVEN_2');
INSERT INTO `script_texts` VALUES ('-1000693', 'The grasp of the Old Gods is unmoving. He is consumed by their dark thoughts... I... I... I cannot... cannot channel much longer... Elune aide me.', null, null, 'Die Umklammerung der alten Götter ist unlösbar. Er wird von ihren dunklen Gedanken verzehrt... ich... Ich kann nicht... Kann nicht länger durchhalten... Elune steh mir bei...', null, null, null, null, null, '0', '0', '0', '0', 'tyrande SAY_TYRANDE_FORGIVEN_3');
INSERT INTO `script_texts` VALUES ('-1000694', 'IT BURNS! THE PAIN.. SEARING...', null, null, 'ES BRENNT! DER SCHMERZ... SENGEND...', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_DEFEAT_1');
INSERT INTO `script_texts` VALUES ('-1000695', 'WHY? Why did this happen to... to me? Where were you Tyrande? Where were you when I fell from the grace of Elune?', null, null, 'Wieso? Wie konnte all das mit mir geschehen? Wo wart Ihr, Tyrande? Wo wart Ihr, als ich vor Elune in Ungnade fiel?', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_DEFEAT_2');
INSERT INTO `script_texts` VALUES ('-1000696', 'I... I feel... I feel the touch of Elune upon my being once more... She smiles upon me... Yes... I...', null, null, 'Ich... Ich kann Elunes Berührung erneut auf meiner Seele spüren... Sie lächelt mich an... Ja... ich...', null, null, null, null, null, '0', '1', '0', '0', 'eranikus SAY_ERANIKUS_DEFEAT_3');
INSERT INTO `script_texts` VALUES ('-1000697', '%s is wholly consumed by the Light of Elune. Tranquility sets in over the Moonglade', null, null, '%s wird vollständig vom Licht der Elune verzehrt. Ruhe setzt über der Mondlichtung ein', null, null, null, null, null, '0', '2', '0', '0', 'eranikus EMOTE_ERANIKUS_REDEEM');
INSERT INTO `script_texts` VALUES ('-1000698', '%s falls to one knee.', null, null, '%s beugt das Knie.', null, null, null, null, null, '0', '2', '0', '0', 'tyrande EMOTE_TYRANDE_KNEEL');
INSERT INTO `script_texts` VALUES ('-1000699', 'Praise be to Elune... Eranikus is redeemed.', null, null, 'Gepriesen sei Elune... Eranikus ist erlöst.', null, null, null, null, null, '0', '1', '0', '0', 'tyrande SAY_TYRANDE_REDEEMED');
INSERT INTO `script_texts` VALUES ('-1000700', 'For so long, I was lost... The Nightmare\'s corruption had consumed me... And now, you... all of you.. you have saved me. Released me from its grasp.', null, null, 'So lange war ich verloren... Die Verderbnis des Alptraums hatte sich meiner bemächtigt... Und jetzt habt Ihr... Ihr alle... Ihr alle habt mich erlöst. Mich aus meiner Umklammerung befreit.', null, null, null, null, null, '0', '0', '0', '0', 'eranikus SAY_REDEEMED_1');
INSERT INTO `script_texts` VALUES ('-1000701', 'But... Malfurion, Cenarius, Ysera... They still fight. They need me. I must return to the Dream at once.', null, null, 'Aber... Malfurion, Cenarius, Ysera... Sie kämpfen weiter. Sie brauchen mich. Ich muss mich sofort zurück in den Traum begeben.', null, null, null, null, null, '0', '0', '0', '0', 'eranikus SAY_REDEEMED_2');
INSERT INTO `script_texts` VALUES ('-1000702', 'My lady, I am unworthy of your prayer. Truly, you are an angel of light. Please, assist me in returning to the barrow den so that I may return to the Dream. I like Malfurion, also have a love awaiting me... I must return to her... to protect her...', null, null, 'Ich bin Eurer Gebete nicht würdig. Ihr seid wahrhaft ein Engel des Lichts. Bitte lasst mich Eure Unterstützung zuteil werden, auf dass ich zum Grabhügel zurückkehren und erneut in den Traum gelangen kann. Auch ich, wie Malfurion, habe eine Liebste, die mich erwartet... ich muss zu ihr zurückkehren... Ich muss sie beschützen...', null, null, null, null, null, '0', '0', '0', '0', 'eranikus SAY_REDEEMED_3');
INSERT INTO `script_texts` VALUES ('-1000703', 'And heroes... I hold that which you seek. May it once more see the evil dissolved. Remulos, see to it that our champion receives the shard of the Green Flight.', null, null, 'Und Helden... Ich habe das, wonach es Euch begehrt. Auf dass es erneut das Böse vernichten möge. Remulos, kümmere dich darum, dass unser Champion den Splitter des grünen Schwarms erhält.', null, null, null, null, null, '0', '0', '0', '0', 'eranikus SAY_REDEEMED_4');
INSERT INTO `script_texts` VALUES ('-1000704', 'It will be done, Eranikus. Be well, ancient one.', null, null, 'Es wird geschehen, Eranikus. Lebt wohl, oh Weiser!', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_OUTRO_1');
INSERT INTO `script_texts` VALUES ('-1000705', 'Let us leave Nighthave, hero. Seek me out at the grove.', null, null, 'Lasst uns Nachthafen verlassen, Held. Sucht mich im Hain auf.', null, null, null, null, null, '0', '0', '0', '0', 'remulos SAY_REMULOS_OUTRO_2');
INSERT INTO `script_texts` VALUES ('-1000706', 'Your world shall suffer an unmerciful end. The Nightmare comes for you!', null, null, 'Eure Welt soll ein unbarmherziges Ende erleiden. Der Alptraum erwartet Euch!', null, null, null, null, null, '0', '0', '0', '0', 'eranikus SAY_ERANIKUS_KILL');
INSERT INTO `script_texts` VALUES ('-1000707', 'This blue light... It\'s strange. What do you think it means?', null, null, 'Dieses blaue Licht... Es ist seltsam. Was glaubt Ihr, könnte es bedeuten?', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_ENTER_OWL_THICKET');
INSERT INTO `script_texts` VALUES ('-1000708', 'We\'ve found it!', null, null, 'Wir haben es gefunden!', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_1');
INSERT INTO `script_texts` VALUES ('-1000709', 'Please, light this while I am channeling', null, null, 'Bitte zündet dies an, während ich kanalisiere.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_2');
INSERT INTO `script_texts` VALUES ('-1000710', 'This is the place. Let\'s light it.', null, null, 'Hier ist es. Dann lassen wir mal ein Licht aufgehen.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_REACH_TORCH_3');
INSERT INTO `script_texts` VALUES ('-1000711', 'Let\'s find the next one.', null, null, 'Suchen wir das nächste.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_1');
INSERT INTO `script_texts` VALUES ('-1000712', 'We must continue on now.', null, null, 'Gut, gehen wir!', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_AFTER_TORCH_2');
INSERT INTO `script_texts` VALUES ('-1000713', 'It is time for the final step; we must activate the altar.', null, null, 'Es ist Zeit für den letzten Schritt. Wir müssen den Altar aktivieren.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_1');
INSERT INTO `script_texts` VALUES ('-1000714', 'I will read the words carved into the stone, and you must find a way to light it.', null, null, 'Ich werde die Wörter vorlesen, die in den Stein gemeißelt sind, und Ihr müsst einen Weg finden, ihn zu entzünden.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_REACH_ALTAR_2');
INSERT INTO `script_texts` VALUES ('-1000715', 'The altar is glowing! We have done it!', null, null, 'Der Altar leuchtet! Wir haben es geschafft!', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_1');
INSERT INTO `script_texts` VALUES ('-1000716', 'What is happening? Look!', null, null, 'Was geschieht hier? Seht!', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_2');
INSERT INTO `script_texts` VALUES ('-1000717', 'It has been many years...', null, null, 'Es ist do viele Jahre her...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_3');
INSERT INTO `script_texts` VALUES ('-1000718', 'Who has disturbed the altar of the goddess?', null, null, 'Wer wagt es, den Altar der Göttin zu stören?', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_4');
INSERT INTO `script_texts` VALUES ('-1000719', 'Please, priestesses, forgive us for our intrusion. We do not wish any harm here.', null, null, 'Bitte, vergebt uns unser Eindringen, Priesterinnen. Wir wollen niemandem Schaden zuzufügen.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_5');
INSERT INTO `script_texts` VALUES ('-1000720', 'We only wish to know why the wildkin guard this area...', null, null, 'Wir wollen lediglich wissen, warum die Wildekin diese Gegend bewachen...', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_RANSHALLA_ALTAR_6');
INSERT INTO `script_texts` VALUES ('-1000721', 'Enu thora\'serador. This is a sacred place.', null, null, 'Enu thora\'serador. Dies ist ein hochheiliger Ort.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_7');
INSERT INTO `script_texts` VALUES ('-1000722', 'We will show you...', null, null, 'Wir werden es Euch zeigen...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_8');
INSERT INTO `script_texts` VALUES ('-1000723', 'Look above you; thara dormil dorah...', null, null, 'Seht nach oben; thara dormil dorah...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_9');
INSERT INTO `script_texts` VALUES ('-1000724', 'This gem once allowed direct communication with Elune, herself.', null, null, 'Dieser Edelstein ermöglichte einst die direkte Kommunikation mit Elune selbst.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_10');
INSERT INTO `script_texts` VALUES ('-1000725', 'Through the gem, Elune channeled her infinite wisdom...', null, null, 'Durch diesen Edelstein kanalisierte Elune ihre unendliche Weisheit...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_11');
INSERT INTO `script_texts` VALUES ('-1000726', 'Realizing that the gem needed to be protected, we turned to the goddess herself.', null, null, 'Als wir erkannten, dass der Edelstein beschützt werden musste, wandten wir uns an die Göttin persönlich.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_12');
INSERT INTO `script_texts` VALUES ('-1000727', 'Soon after, we began to have visions of a creature... A creature with the feathers of an owl, but the will and might of a bear...', null, null, 'Bald darauf begannen wir, Visionen von einer Kreatur zu haben... Einer Kreatur mit den Federn einer Eule, aber der Willenskraft und der Stärke eines Bären...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_13');
INSERT INTO `script_texts` VALUES ('-1000728', 'It was on that day that the wildkin were given to us. Fierce guardians, the goddess assigned the wildkin to protect all of her sacred places.', null, null, 'An jenem Tag wurden uns die Wildekin gegeben. Die Göttin berief die Wildekin, diese grimmigen Wächter, dazu, all ihre heiligen Orte zu beschützen.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_14');
INSERT INTO `script_texts` VALUES ('-1000729', 'Anu\'dorini Talah, Ru shallora enudoril.', null, null, 'Anu\'dorini Talah, Ru shallora enudoril', null, null, null, null, null, '0', '0', '0', '0', 'Voice of Elune SAY_VOICE_ALTAR_15');
INSERT INTO `script_texts` VALUES ('-1000730', 'But now, many years later, the wildkin have grown more feral, and without the guidance of the goddess, they are confused...', null, null, 'Doch nun, viele Jahre später, sind die Wildekin nicht mehr so friedfertig wie früher, und ohne die Führung der Göttin sind sie verwirrt...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_16');
INSERT INTO `script_texts` VALUES ('-1000731', 'Without a purpose, they wander... But many find their way back to the sacred areas that they once were sworn to protect.', null, null, 'Sie wandern herum ohne jedes Ziel, ohne jeden Zweck... Doch viele verschlägt es zurück zu den heiligen Gebieten, von denen sie einst geschworen hatten, sie zu beschützen.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_17');
INSERT INTO `script_texts` VALUES ('-1000732', 'Wildkin are inherently magical; this power was bestowed upon them by the goddess.', null, null, 'Wildekin sind von Natur aus magisch, diese Macht wurde ihnen von der Göttin verliehen.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_18');
INSERT INTO `script_texts` VALUES ('-1000733', 'Know that wherever you might find them in the world, they are protecting something of importance, as they were entrusted to do so long ago.', null, null, 'Eines müsst Ihr wissen: Ganz egal, wo auf der Welt Ihr auf sie stoßt, sie bewachen etwas Wichtiges, wie es ihnen vor langer Zeit anbefohlen wurde.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_19');
INSERT INTO `script_texts` VALUES ('-1000734', 'Please, remember what we have shown you...', null, null, 'Bitte erinnert Euch daran, was wir Euch gezeigt haben...', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_20');
INSERT INTO `script_texts` VALUES ('-1000735', 'Farewell.', null, null, 'Lebt wohl.', null, null, null, null, null, '0', '0', '0', '0', 'Priestess of Elune SAY_PRIESTESS_ALTAR_21');
INSERT INTO `script_texts` VALUES ('-1000736', 'Thank you for you help, $n. I wish you well in your adventures.', null, null, 'Danke für Eure Hilfe, $N. Ich wünsche Euch viel Glück bei Euren Abenteuern.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_QUEST_END_1');
INSERT INTO `script_texts` VALUES ('-1000737', 'I want to stay here and reflect on what we have seen. Please see Erelas and tell him what we have learned.', null, null, 'Ich möchte hier bleiben und darüber nachdenken, was wir gesehen haben. Bitte sucht Erelas auf und sagt ihm, was wir erfahren haben.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_QUEST_END_2');
INSERT INTO `script_texts` VALUES ('-1000738', '%s begins chanting a strange spell...', null, null, '%s beginnt, einen seltsamen Zauber zu singen...', null, null, null, null, null, '0', '2', '0', '0', 'Ranshalla EMOTE_CHANT_SPELL');
INSERT INTO `script_texts` VALUES ('-1000739', 'Remember, I need your help to properly channel. I will ask you to aid me several times in our path, so please be ready.', null, null, 'Und vergesst nicht, ich brauche Eure Hilfe, damit ich richtig kanalisieren kann. Auf unserem Weg werde ich Euch mehrmals bitten, mir zu helfen, also seid bitte bereit.', null, null, null, null, null, '0', '0', '0', '0', 'Ranshalla SAY_QUEST_START');
INSERT INTO `script_texts` VALUES ('-1000740', 'We must act quickly or shall be lost!', null, null, 'Wir müssen schnell handeln oder wir sind verloren!', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ANACHRONOS_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1000741', 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place our precious barrier, dragon.', null, null, 'Meine Streitkräfte können die Verteidigung der Qiraji nicht überwinden. Wir werden nicht nah genug herankommen, um unsere wertvolle Barriere zu platzieren, Drache.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_FANDRAL_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1000742', 'There is a way...', null, null, 'Es gibt einen Weg...', null, null, null, null, null, '0', '0', '0', '22', 'SAY_MERITHRA_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1000743', '%s nods knowingly.', null, null, '%s nickt wissend.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_ARYGOS_NOD');
INSERT INTO `script_texts` VALUES ('-1000744', 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf.Absolution cannot be had through misguided vengeance.', null, null, 'Jawohl, Fandral, erinnere dich an diese Worte: Lass deinen Glauben nicht von deiner Trauer leiten. Diese Gedanken, die du hältst... dunkle Orte, an die du gehst, Nachtelf. Vergebung kann nicht durch fehlgeleitete Rache erreicht werden.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_CAELESTRASZ_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1000745', '%s glances at her compatriots.', null, null, '%s wirft einen flüchtigen Blick auf ihre Verbündeten.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_MERITHRA_GLANCE');
INSERT INTO `script_texts` VALUES ('-1000746', 'We will push him back, Anachronos. This is wow. Uphold your end of this task. Let not your hands falter as you seal our fates behind the barrier.', null, null, 'Wir werden ihn zurückdrängen, Anachronos. Das ist ein Versprechen. Haltet Euren Teil dieser Aufgabe ein. Lasst Eure Hände nicht schwanken, wenn Ihr unser Schicksal hinter der Barriere versiegelt.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_MERITHRA_INTRO_5');
INSERT INTO `script_texts` VALUES ('-1000747', 'Succumb to the endless dream, little ones. Let it comsume you!', null, null, 'Ergebt euch dem endlosen Traum, ihr Wichtel. Lasst euch von ihm betören!', null, null, null, null, null, '0', '1', '0', '22', 'SAY_MERITHRA_ATTACK_1');
INSERT INTO `script_texts` VALUES ('-1000748', 'Anachronos, the diversion will give you an the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', null, null, 'Anachronos, das Ablenkungsmanöver gibt Euch und dem jungen Druiden genug Zeit, um das Tor zu versiegeln. Zögert nicht. Jetzt lasst uns sehen, wie sie mit chaotischer Magie umgehen.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ARYGOS_ATTACK_2');
INSERT INTO `script_texts` VALUES ('-1000749', 'Let them feelt the wrath of the blue flight! May Malygos protect me!', null, null, 'Lass sie den Zorn des blauen Drachenschwarms spüren! Möge Malygos mich beschützen!', null, null, null, null, null, '0', '1', '0', '22', 'SAY_ARYGOS_ATTACK_3');
INSERT INTO `script_texts` VALUES ('-1000750', 'Do not forget sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', null, null, 'Vergesst nicht die Opfer die an diesem Tag erbracht wurden, Nachtelf. Wir alle haben durch diese Bestien sehr gelitten.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_CAELESTRASZ_ATTACK_4');
INSERT INTO `script_texts` VALUES ('-1000751', 'Alexstrasza, give me the resolve to drive your enemies back.', null, null, 'Alexstrasza, gebt mir die Kraft, Eure Feinde zurückzudrängen.', null, null, null, null, null, '0', '1', '0', '22', 'SAY_CAELESTRASZ_ATTACK_5');
INSERT INTO `script_texts` VALUES ('-1000752', 'NOW,STAGHELM! WE GO NOW! Prepare your magic!', null, null, 'JETZT, HIRSCHHAUPT! WIR GEHEN JETZT! Bereite deine Magie vor!', null, null, null, null, null, '0', '0', '0', '22', 'SAY_ANACHRONOS_SEAL_1');
INSERT INTO `script_texts` VALUES ('-1000753', 'It is done, dragon. Lead the way!', null, null, 'Es ist vollbracht, Drache. Weist den Weg!', null, null, null, null, null, '0', '0', '0', '25', 'SAY_FANDRAL_SEAL_2');
INSERT INTO `script_texts` VALUES ('-1000754', 'Stay close.', null, null, 'Bleibt dicht bei mir.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_ANACHRONOS_SEAL_3');
INSERT INTO `script_texts` VALUES ('-1000755', 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', null, null, 'Der Sand der Zeit verharrt für einen kurzen Moment! Ich werde jetzt die Barriere beschwören.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_ANACHRONOS_SEAL_4');
INSERT INTO `script_texts` VALUES ('-1000756', 'FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', null, null, 'WIRKT DEN ZAUBER, HIRSCHHAUPT! ICH KANN DIE GLYPHEN DES ZAUBERSCHUTZES NICHT LÄNGER HALTEN! BESCHWÖRT DIE WURZELN!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_ANACHRONOS_SEAL_5');
INSERT INTO `script_texts` VALUES ('-1000757', 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', null, null, 'Ihr Uralten, leitet meine Hand... Erwacht aus Eurem tiefen Schlaf! ERWACHT UND VERSIEGELT DIESEN VERFLUCHTEN ORT!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_FANDRAL_SEAL_6');
INSERT INTO `script_texts` VALUES ('-1000758', '%s falls to one knee - exhausted.', null, null, '%s fällt völlig erschöpft auf die Knie.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_FANDRAL_EXHAUSTED');
INSERT INTO `script_texts` VALUES ('-1000759', 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', null, null, 'Es... es ist vorbei, Lord Hirschhaupt. Wir sind siegreich. Doch die Kosten für diesen Sieg waren hoch.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ANACHRONOS_EPILOGUE_1');
INSERT INTO `script_texts` VALUES ('-1000760', 'There is but one duty that remains', null, null, 'Es bleibt nur eine Aufgabe übrig.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ANACHRONOS_EPILOGUE_2');
INSERT INTO `script_texts` VALUES ('-1000761', 'Before I leave this place, I make one final offering for you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the scepter of the shifting sands on the sacred gong. The magic holding the barrier together will dissipate an the horrors of the Ahn\'Qiraj will be unleashed upon the world once more.', null, null, 'Bevor ich diesen Ort verlasse, mache ich Euch ein letztes Geschenk, Lord Hirschhaupt. Sollte es einmal notwendig sein, in diese verfluchte Festung einzutreten, benutzt das Szepter der Sandstürme auf dem heiligen Gong. Die Magie, die diese Barriere zusammenhält, wird sich auflösen und die Schrecken der Ahn\'Qiraj werden wieder auf die Welt losgelassen.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ANACHRONOS_EPILOGUE_3');
INSERT INTO `script_texts` VALUES ('-1000762', '%s hands the Scepter of the Shifting Sands to $N.', null, null, '%s überreicht $N das Szepter der Sandstürme.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_ANACHRONOS_SCEPTER');
INSERT INTO `script_texts` VALUES ('-1000763', 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', null, null, 'Nach all\' dem Leid, das mein Volk mit ansehen und erdulden musste, erwartet Ihr von mir, dass ich eine weitere Bürde auf mich nehme, Drache? Ihr müsst wahrlich verrückt sein.', null, null, null, null, null, '0', '0', '0', '1', 'SAY_FANDRAL_EPILOGUE_4');
INSERT INTO `script_texts` VALUES ('-1000764', 'I want nothing to do with Silithus, the Qiraji and least of all, any damed dragons!', null, null, 'Ich will nichts mit Silithus, den Qiraji und am allerwenigsten mit verdammten Drachen zu tun haben!', null, null, null, null, null, '0', '0', '0', '1', 'SAY_FANDRAL_EPILOGUE_5');
INSERT INTO `script_texts` VALUES ('-1000765', '%s hurls the Scepter of the Shifting Sands into the barrier, shattering it.', null, null, '%s schleudert das Szepter der Sandstürme in die Barriere und es zerbirst.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_FANDRAL_SHATTER');
INSERT INTO `script_texts` VALUES ('-1000766', 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', null, null, 'Lord Hirschhaupt, wo wollt Ihr hin? Ihr würdet unser Bündnis aus Stolz zerschlagen?', null, null, null, null, null, '0', '0', '0', '1', 'SAY_ANACHRONOS_EPILOGUE_6');
INSERT INTO `script_texts` VALUES ('-1000767', 'My son\'s soul will find no comfort in this hollow victory, dragon! I will have him back. Though it takes a millenia. I WILL have my son back!', null, null, 'Die Seele meines Sohnes wird keinen Trost in diesem unbedeutenden Sieg finden, Drache! Ich werde ihn zurückbekommen. Auch wenn es Jahrtausende dauert. Ich WERDE meinen Sohn zurückbekommen!', null, null, null, null, null, '0', '0', '0', '1', 'SAY_FANDRAL_EPILOGUE_7');
INSERT INTO `script_texts` VALUES ('-1000768', '%s shakes his head in disappointment.', null, null, '%s schüttelt voller Enttäuschung seinen Kopf.', null, null, null, null, null, '0', '2', '0', '25', 'EMOTE_ANACHRONOS_DISPPOINTED');
INSERT INTO `script_texts` VALUES ('-1000769', '%s kneels down to pickup the fragments of the shattered scepter.', null, null, '%s kniet nieder, um die Fragmente des zerbrochenen Szepters aufzunehmen.', null, null, null, null, null, '0', '2', '0', '0', 'EMOTE_ANACHRONOS_PICKUP');
INSERT INTO `script_texts` VALUES ('-1000770', 'And now you know all that there is to know, mortal', null, null, 'Und jetzt weißt du alles, was es zu wissen gibt Sterblicher.', null, null, null, null, null, '0', '0', '0', '0', 'SAY_ANACHRONOS_EPILOGUE_8');
INSERT INTO `script_texts` VALUES ('-1000771', 'Let\'s go $N!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_QUEST_START');
INSERT INTO `script_texts` VALUES ('-1000772', 'It looks like we\'re in trouble. Look lively, here they come!', null, null, 'Sieht aus, als hätten wir Schwierigkeiten. Ein bisschen mehr Schwung bitte, da kommen sie!', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_FIRST_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000773', 'Assassins from that cult you found... Let\'s get moving before someone else finds us out here.', null, null, 'Auftragsmörder von diesem Kult, den Ihr gefunden habt... Wir sollten uns in Bewegung setzen, bevor uns hier noch jemand anders aufspürt.', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_FIRST_AMBUSH_END');
INSERT INTO `script_texts` VALUES ('-1000774', 'Hold! I sense an evil presence... Undead!', null, null, 'Halt! Ich spüre etwas Böses... Untote!', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_SECOND_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000775', 'A $C! Slaying him would please the master. Attack!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'Forsaken Scout SAY_SCOUT_SECOND_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000776', 'They\'re coming out of the woodwork today. Let\'s keep moving or we may find more things that want me dead.', null, null, 'Sie kommen heute ans Licht. Lasst uns weitergehen, sonst finden wir vielleicht noch mehr Dinge, die meinen Tod wollen.', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_SECOND_AMBUSH_END');
INSERT INTO `script_texts` VALUES ('-1000777', 'These three again?', null, null, 'Die drei schon wieder?', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_FINAL_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000778', 'Not quite so sure of yourself without the Purifier, hm?', null, null, 'Ohne den Läuterer seid Ihr nicht ganz so selbstsicher, wie?', null, null, null, null, null, '0', '0', '0', '0', 'Balizar the Umbrage SAY_BALIZAR_FINAL_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000779', 'I\'ll finish you off for good this time!', null, null, 'Dieses Mal mache ich Euch endgültig fertig!', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_FINAL_AMBUSH_ATTACK');
INSERT INTO `script_texts` VALUES ('-1000780', 'Well done! I should be fine on my own from here. Remember to talk to Delgren when you return to Maestra\'s Post in Ashenvale.', null, null, 'Gut gemacht! Jetzt müsste ich allein zurechtkommen. Denkt daran und sprecht mit Delgren, wenn Ihr wieder in Maestras Posten im Eschental seid.', null, null, null, null, null, '0', '0', '0', '0', 'Feero Ironhand SAY_QUEST_END');
INSERT INTO `script_texts` VALUES ('-1000784', 'Thanks $N. Now let\'s get out of here!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'melizza SAY_MELIZZA_START');
INSERT INTO `script_texts` VALUES ('-1000785', 'We made it! Thanks again! I\'m going to run ahead!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'melizza SAY_MELIZZA_FINISH');
INSERT INTO `script_texts` VALUES ('-1000786', 'Hey Hornizz! I\'m back! And there are some people behind me who helped me out of a jam.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'melizza SAY_MELIZZA_1');
INSERT INTO `script_texts` VALUES ('-1000787', 'We\'re going to have to scratch the Maraudines off our list. Too hard to work with...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'melizza SAY_MELIZZA_2');
INSERT INTO `script_texts` VALUES ('-1000788', 'Well, I\'m off to the Gelkis. They\'re not as dumb as the Maraudines, but they\'re more reasonable.', null, null, null, null, null, null, null, null, '0', '0', '0', '3', 'melizza SAY_MELIZZA_3');
INSERT INTO `script_texts` VALUES ('-1000789', 'Well, now or never I suppose. Remember, once we get to the road safety, return to Terenthis to let him know we escaped.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_START');
INSERT INTO `script_texts` VALUES ('-1000790', 'We made it, My friend. Remember to find Terenthis and let him know we\'re safe. Thank you again.', null, null, 'Wir haben es geschafft. Denkt daran, Terenthis aufzusuchen und ihm zu sagen, dass wir in Sicherheit sind. Nochmals vielen Dank.', null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_END');
INSERT INTO `script_texts` VALUES ('-1000791', 'Here they come.', null, null, 'Da sind sie.', null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_FIRST_AMBUSH');
INSERT INTO `script_texts` VALUES ('-1000792', 'We can overcome these foul creatures.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000793', 'We shall earn our deaths at the very least!', null, null, 'Wir verdienen uns zumindest unseren Tod!', null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1000794', 'Don\'t give up! Fight, to the death!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'volcor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1000795', 'OK boss, I get back to tree hitting.', null, null, 'OK, Boss, ich wieder schlagen Bäume.', null, null, null, null, null, '0', '0', '0', '0', 'lazy peon SAY_AWAKE_1');
INSERT INTO `script_texts` VALUES ('-1000796', 'Sleepy... so sleepy...', null, null, 'Müde... So müde...', null, null, null, null, null, '0', '0', '0', '0', 'lazy peon SAY_AWAKE_2');
INSERT INTO `script_texts` VALUES ('-1000797', '%s squawks and heads toward Veil Shalas. Hurry and follow!', null, null, '%s krächzt und macht sich auf in Richtung Shalasversteck. Beeilt Euch, zu folgen!', null, null, null, null, null, '0', '2', '0', '0', 'skywing SAY_SKYWING_START');
INSERT INTO `script_texts` VALUES ('-1000798', '%s pauses briefly before the tree and then heads inside.', null, null, '%s wartet kurz vor dem Baum und begibt sich dann hinein.', null, null, null, null, null, '0', '2', '0', '0', 'skywing SAY_SKYWING_TREE_DOWN');
INSERT INTO `script_texts` VALUES ('-1000799', '%s seems to be looking for something. He wants you to follow.', null, null, '%s scheint nach etwas zu suchen, und möchte, dass Ihr folgt.', null, null, null, null, null, '0', '2', '0', '0', 'skywing SAY_SKYWING_TREE_UP');
INSERT INTO `script_texts` VALUES ('-1000800', '%s flies to the platform below! You\'d better jump if you want to keep up. Hurry!', null, null, '%s fliegt nach unten zur Plattform! Ihr solltet springen, wenn Ihr den Anschluss nicht verlieren wollt. Beeilt Euch!', null, null, null, null, null, '0', '2', '0', '0', 'skywing SAY_SKYWING_JUMP');
INSERT INTO `script_texts` VALUES ('-1000801', '%s bellows a loud squawk!', null, null, '%s krächzt laut!', null, null, null, null, null, '0', '2', '0', '0', 'skywing SAY_SKYWING_SUMMON');
INSERT INTO `script_texts` VALUES ('-1000802', 'Free at last from that horrible curse! Thank you! Please send word to Rilak the Redeemed that I am okay. My mission lies in Skettis. Terokk must be defeated!', null, null, 'Endlich wurde ich von diesem schrecklichen Fluch befreit! Vielen Dank! Bitte berichtet Rilak dem Erlösten, dass es mir gut geht. Meine Mission führt nach Skettis. Terokk muss besiegt werden!', null, null, null, null, null, '0', '0', '0', '0', 'skywing SAY_SKYWING_END');
INSERT INTO `script_texts` VALUES ('-1000803', 'You do not fight alone, %n! Together, we will banish this spawn of hellfire!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Oronok SAY_ORONOK_TOGETHER');
INSERT INTO `script_texts` VALUES ('-1000804', 'We will fight when you are ready.', null, null, 'Wir werden kämpfen, wenn Ihr bereit seid.', null, null, null, null, null, '0', '0', '0', '0', 'Oronok SAY_ORONOK_READY');
INSERT INTO `script_texts` VALUES ('-1000805', 'We will set the elements free of your grasp by force!', null, null, 'Wir werden die Elemente mit Gewalt aus Eurem Griff befreien!', null, null, null, null, null, '0', '1', '0', '0', 'Oronok SAY_ORONOK_ELEMENTS');
INSERT INTO `script_texts` VALUES ('-1000806', 'What say the elements, Torlok? I only hear silence.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'Oronok SAY_ORONOK_EPILOGUE_1');
INSERT INTO `script_texts` VALUES ('-1000807', 'I hear what you hear, brother. Look behind you...', null, null, 'Ich höre was Ihr hört, Bruder. Schaut hinter Euch...', null, null, null, null, null, '0', '0', '0', '1', 'Torlok SAY_TORLOK_EPILOGUE_2');
INSERT INTO `script_texts` VALUES ('-1000808', 'They are redeemed! Then we have won?', null, null, 'Sie sind erlöst! Haben wir gewonnen?', null, null, null, null, null, '0', '0', '0', '1', 'Oronok SAY_ORONOK_EPILOGUE_3');
INSERT INTO `script_texts` VALUES ('-1000809', 'It is now as it should be, shaman. You have done well.', null, null, 'Alles ist nun, wie es sein sollte, Schamane. Gute Arbeit.', null, null, null, null, null, '0', '0', '0', '0', 'Spirit of Earth SAY_EARTH_EPILOGUE_4');
INSERT INTO `script_texts` VALUES ('-1000810', 'Yes... Well enough for the elements that are here, but the cipher is known to another... The spirits of fire are in turmoil... If this force is not stopped, the world where these mortals came from will cease.', null, null, 'Ja... Gut für die Elemente hier, aber die Litanei ist jemandem anderem bekannt... Die Geister sind in Aufruhr... Wenn diese Macht nicht aufgehalten wird, ist die Welt der Sterblichen bedroht.', null, null, null, null, null, '0', '0', '0', '0', 'Spirit of Fire SAY_FIRE_EPILOGUE_5');
INSERT INTO `script_texts` VALUES ('-1000811', 'Farewell, mortals... The earthmender knows what fire feels...', null, null, 'Lebt wohl, Sterbliche... Der Erdheiler kennt das Gefühl des Feuers...', null, null, null, null, null, '0', '0', '0', '0', 'Spirit of Earth SAY_EARTH_EPILOGUE_6');
INSERT INTO `script_texts` VALUES ('-1000812', 'We leave, Torlok. I have only one request...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'Oronok SAY_ORONOK_EPILOGUE_7');
INSERT INTO `script_texts` VALUES ('-1000813', 'The Torn-heart men give their weapons to Earthmender Torlok.', null, null, 'Die Männer des Herzeleid geben Erdheiler Torlok ihre Waffen.', null, null, null, null, null, '0', '2', '0', '0', 'Torlok EMOTE_GIVE_WEAPONS');
INSERT INTO `script_texts` VALUES ('-1000814', 'Give these to the heroes that made this possible.', null, null, 'Gebt sie denen, die dies möglich gemacht haben.', null, null, null, null, null, '0', '0', '0', '1', 'Oronok SAY_ORONOK_EPILOGUE_8');
INSERT INTO `script_texts` VALUES ('-1000815', 'Be healed!', null, null, 'Seid geheilt!', null, null, null, null, null, '0', '1', '0', '0', 'Eris Havenfire SAY_PHASE_HEAL');
INSERT INTO `script_texts` VALUES ('-1000816', 'We are saved! The peasants have escaped the Scourge!', null, null, 'Wir sind gerettet! Die Arbeiter konnten der Geißel entkommen!', null, null, null, null, null, '0', '1', '0', '0', 'Eris Havenfire SAY_EVENT_END');
INSERT INTO `script_texts` VALUES ('-1000817', 'I have failed once more...', null, null, 'Wieder einmal habe ich versagt...', null, null, null, null, null, '0', '1', '0', '0', 'Eris Havenfire SAY_EVENT_FAIL_1');
INSERT INTO `script_texts` VALUES ('-1000818', 'I now return to whence I came, only to find myself here once more to relive the same epic tragedy.', null, null, 'Ich kehre nun dorthin zurück, woher ich kam, nur um hier ein weiteres Mal die gleiche dramatische Tragödie zu durchleben.', null, null, null, null, null, '0', '0', '0', '0', 'Eris Havenfire SAY_EVENT_FAIL_2');
INSERT INTO `script_texts` VALUES ('-1000819', 'The Scourge are upon us! Run! Run for your lives!', null, null, 'Die Geißel ist da! Rennt! Rennt um euer Leben!', null, null, null, null, null, '0', '1', '0', '0', 'Peasant SAY_PEASANT_APPEAR_1');
INSERT INTO `script_texts` VALUES ('-1000820', 'Please help us! The Prince has gone mad!', null, null, 'Bitte helft uns! Der Prinz ist wahnsinnig geworden!', null, null, null, null, null, '0', '1', '0', '0', 'Peasant SAY_PEASANT_APPEAR_2');
INSERT INTO `script_texts` VALUES ('-1000821', 'Seek sanctuary in Hearthglen! It is our only hope!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'Peasant SAY_PEASANT_APPEAR_3');
INSERT INTO `script_texts` VALUES ('-1000822', 'The signal has been sent. He should be arriving shortly.', null, null, 'Man hat Bescheid gegeben. Er sollte in Kürze hier sein.', null, null, null, null, null, '0', '0', '0', '1', 'squire rowe SAY_SIGNAL_SENT');
INSERT INTO `script_texts` VALUES ('-1000823', 'Yawww!', null, null, 'Jaaaaa!', null, null, null, null, null, '0', '0', '0', '35', 'reginald windsor SAY_DISMOUNT');
INSERT INTO `script_texts` VALUES ('-1000824', 'I knew you would come, $N. It is good to see you again, friend.', null, null, 'Ich wusste, Ihr würdet kommen, $N. Schön, Euch wiederzusehen.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_WELCOME');
INSERT INTO `script_texts` VALUES ('-1000825', 'On guard, friend. The lady dragon will not give in without a fight.', null, null, 'Seid vorsichtig. Onyxia wird nicht kampflos aufgeben.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_QUEST_ACCEPT');
INSERT INTO `script_texts` VALUES ('-1000826', 'As was fated a lifetime ago in Karazhan, monster - I come - and with me I bring justice.', null, null, 'Wie es vor langer Zeit in Karazhan vorherbestimmt wurde, Monster - ich komme - und ich bringe Gerechtigkeit.', null, null, null, null, null, '0', '6', '0', '22', 'reginald windsor SAY_GET_READY');
INSERT INTO `script_texts` VALUES ('-1000827', 'Seize him! Seize the worthless criminal and his allies!', null, null, 'Ergreift ihn! Ergreift den wertlosen Verbrecher und seine Verbündeten!', null, null, null, null, null, '0', '6', '0', '0', 'prestor SAY_GONNA_DIE');
INSERT INTO `script_texts` VALUES ('-1000828', 'Reginald, you know that I cannot let you pass.', null, null, 'Reginald, Ihr wisst, dass ich Euch nicht vorbeilassen kann.', null, null, null, null, null, '0', '0', '0', '1', 'jonathan SAY_DIALOG_1');
INSERT INTO `script_texts` VALUES ('-1000829', 'You must do what you think is right, Marcus. We served together under Turalyon. He made us both the men that we are today. Did he err with me? Do you truly believe my intent is to cause harm to our alliance? Would I shame our heroes?', null, null, 'Ihr müsst das tun, was Ihr für richtig haltet, Marcus. Wir haben zusammen unter Turalyon gedient. Er hat uns beide zu den Männern gemacht, die wir heute sind. Hat er sich bei mir geirrt? Glaubt Ihr wirklich, dass ich unserer Allianz schaden würde? Dass ich unsere Helden beschämen würde?', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_DIALOG_2');
INSERT INTO `script_texts` VALUES ('-1000830', 'Holding me here is not the right decision, Marcus.', null, null, 'Mich hier festzuhalten, ist nicht die richtige Entscheidung, Marcus.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_DIALOG_3');
INSERT INTO `script_texts` VALUES ('-1000831', '%s appears lost in contemplation.', null, null, '%s scheint in Gedanken versunken zu sein.', null, null, null, null, null, '0', '2', '0', '0', 'jonathan EMOTE_CONTEMPLATION');
INSERT INTO `script_texts` VALUES ('-1000832', 'I am ashamed, old friend. I know not what I do anymore. It is not you that would dare bring shame to the heroes of legend - it is I. It is I and the rest of these corrupt politicians. They fill our lives with empty promises, unending lies.', null, null, 'Ich bin beschämt. Ich weiß nicht mehr, was ich tue. Nicht Ihr bringt Schande über die Helden der Legende, sondern ich. Ich und der Rest dieser verdorbenen Politiker. Sie füllen unser Leben mit leeren Versprechungen, nicht endenden Lügen.', null, null, null, null, null, '0', '0', '0', '1', 'jonathan SAY_DIALOG_4');
INSERT INTO `script_texts` VALUES ('-1000833', 'We shame our ancestors. We shame those lost to us... forgive me, Reginald.', null, null, 'Wir beschämen unsere Vorfahren. Wir beschämen die, die wir verloren haben... vergebt mir, Reginald.', null, null, null, null, null, '0', '0', '0', '1', 'jonathan SAY_DIALOG_5');
INSERT INTO `script_texts` VALUES ('-1000834', 'Dear friend, you honor them with your vigilant watch. You are steadfast in your allegiance. I do not doubt for a moment that you would not give as great a sacrifice for your people as any of the heroes you stand under.', null, null, 'Ihr ehrt sie mit Eurer aufmerksamen Wache. Ihr seid standhaft in Eurer Loyalität. Ich bezweifle keinen Moment, dass Ihr nicht ein genauso großes Opfer für Eure Leute bringen würdet, wie die Helden, denen Ihr untersteht.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_DIALOG_6');
INSERT INTO `script_texts` VALUES ('-1000835', 'Now, it is time to bring her reign to an end, Marcus. Stand down, friend.', null, null, 'Jetzt ist es an der Zeit, ihrer Herrschaft ein Ende zu bereiten, Marcus. Tretet zurück, alter Freund.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_DIALOG_7');
INSERT INTO `script_texts` VALUES ('-1000836', 'Stand down! Can you not see that heroes walk among us?', null, null, 'Zurück! Könnt Ihr nicht sehen, dass Helden unter uns weilen?', null, null, null, null, null, '0', '0', '0', '5', 'jonathan SAY_DIALOG_8');
INSERT INTO `script_texts` VALUES ('-1000837', 'Move aside! Let them pass!', null, null, 'Zur Seite! Lasst sie durch!', null, null, null, null, null, '0', '0', '0', '5', 'jonathan SAY_DIALOG_9');
INSERT INTO `script_texts` VALUES ('-1000838', 'Reginald Windsor is not to be harmed! He shall pass through untouched!', null, null, 'Reginald Windsor darf nichts geschehen! Er soll unberührt passieren!', null, null, null, null, null, '0', '1', '0', '22', 'jonathan SAY_DIALOG_10');
INSERT INTO `script_texts` VALUES ('-1000839', 'Go, Reginald. May the light guide your hand.', null, null, 'Geht, Reginald. Möge das Licht Eure Hand leiten.', null, null, null, null, null, '0', '0', '0', '1', 'jonathan SAY_DIALOG_11');
INSERT INTO `script_texts` VALUES ('-1000840', 'Thank you, old friend. You have done the right thing.', null, null, 'Ich danke Euch. Ihr habt das Richtige getan.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_DIALOG_12');
INSERT INTO `script_texts` VALUES ('-1000841', 'Follow me, friends. To Stormwind Keep!', null, null, 'Folgt mir, Freunde. Zur Burg Sturmwind!', null, null, null, null, null, '0', '0', '0', '0', 'reginald windsor SAY_DIALOG_13');
INSERT INTO `script_texts` VALUES ('-1000842', 'Light be with you, sir.', null, null, 'Das Licht sei mit Euch.', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_1');
INSERT INTO `script_texts` VALUES ('-1000843', 'We are but dirt beneath your feet, sir.', null, null, 'Wir sind nur Staub unter Euren Füßen.', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_2');
INSERT INTO `script_texts` VALUES ('-1000844', '...nerves of thorium.', null, null, '... Nerven aus Thorium.', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_3');
INSERT INTO `script_texts` VALUES ('-1000845', 'Make way!', null, null, 'Macht Platz!', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_4');
INSERT INTO `script_texts` VALUES ('-1000846', 'A living legend...', null, null, null, null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_5');
INSERT INTO `script_texts` VALUES ('-1000847', 'A moment I shall remember for always.', null, null, 'Ein Moment, an den ich mich auf ewig erinnern werde.', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_6');
INSERT INTO `script_texts` VALUES ('-1000848', 'You are an inspiration to us all, sir.', null, null, 'Ihr seid eine Inspiration für uns alle.', null, null, null, null, null, '0', '0', '0', '66', 'guard SAY_7');
INSERT INTO `script_texts` VALUES ('-1000849', 'Be brave, friends. The reptile will thrash wildly. It is an act of desperation. When you are ready, give me the word.', null, null, 'Seid mutig, Freunde. Das Reptil wird sich sehr wehren. Es ist ein Akt der Verzweiflung. Wenn Ihr bereit seid, gebt mir Bescheid.', null, null, null, null, null, '0', '0', '0', '25', 'reginald windsor SAY_BEFORE_KEEP');
INSERT INTO `script_texts` VALUES ('-1000850', 'Onward!', null, null, 'VORWÄRTS!', null, null, null, null, null, '0', '0', '0', '5', 'reginald windsor SAY_GO_TO_KEEP');
INSERT INTO `script_texts` VALUES ('-1000851', 'Majesty, run while you still can. She is not what you think her to be...', null, null, 'Majestät, flieht, solange Ihr noch könnt. Sie ist nicht, wofür Ihr sie haltet...', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_IN_KEEP_1');
INSERT INTO `script_texts` VALUES ('-1000852', 'To the safe hall, your majesty.', null, null, 'Zur sicheren Halle, Eure Majestät.', null, null, null, null, null, '0', '0', '0', '1', 'bolvar SAY_IN_KEEP_2');
INSERT INTO `script_texts` VALUES ('-1000853', 'The masquerade is over, Lady Prestor. Or should I call you by your true name... Onyxia...', null, null, 'Die Maskerade ist vorüber, Lady Prestor. Oder soll ich Euch mit Eurem richtigen Namen ansprechen... Onyxia...', null, null, null, null, null, '0', '0', '0', '25', 'reginald windsor SAY_IN_KEEP_3');
INSERT INTO `script_texts` VALUES ('-1000854', '%s laughs.', null, null, '%s lacht.', null, null, null, null, null, '0', '2', '0', '11', 'prestor EMOTE_IN_KEEP_LAUGH');
INSERT INTO `script_texts` VALUES ('-1000855', 'You will be incarcerated and tried for treason, Windsor. I shall watch with glee as they hand down a guilty verdict and sentence you to death by hanging...', null, null, 'Ihr werdet eingekerkert und man wird Euch wegen Verrats den Prozess machen, Windsor. Ich werde freudig zusehen, wie sie Euch für schuldig befinden und Euch zum Tode durch Hängen verurteilen...', null, null, null, null, null, '0', '0', '0', '1', 'prestor SAY_IN_KEEP_4');
INSERT INTO `script_texts` VALUES ('-1000856', 'And as your limp body dangles from the rafters, I shall take pleasure in knowing that a mad man has been put to death. After all, what proof do you have? Did you expect to come in here and point your fingers at royalty and leave unscathed?', null, null, null, null, null, null, null, null, '0', '0', '0', '6', 'prestor SAY_IN_KEEP_5');
INSERT INTO `script_texts` VALUES ('-1000857', 'You will not escape your fate, Onyxia. It has been prophesied - a vision resonating from the great halls of Karazhan. It ends now...', null, null, 'Ihr werdet Eurem Schicksal nicht entkommen, Onyxia. Es wurde vorhergesagt - eine Vision, die in den großen Hallen von Karazhan widerhallt. Das alles endet nun...', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_IN_KEEP_6');
INSERT INTO `script_texts` VALUES ('-1000858', '%s reaches into his pack and pulls out the encoded tablets...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'reginald windsor EMOTE_IN_KEEP_REACH');
INSERT INTO `script_texts` VALUES ('-1000859', 'The Dark Irons thought these tablets to be encoded. This is not any form of coding, it is the tongue of ancient dragon.', null, null, 'Die Dunkeleisenzwerge dachten, diese Schrifttafeln wären verschlüsselt. Das hier ist keine Form der Verschlüsselung, es ist die Sprache der uralten Großdrachen.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_IN_KEEP_7');
INSERT INTO `script_texts` VALUES ('-1000860', 'Listen, dragon. Let the truth resonate throughout these halls.', null, null, 'Hört zu, Drache. Lasst die Wahrheit durch diese Hallen tönen.', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_IN_KEEP_8');
INSERT INTO `script_texts` VALUES ('-1000861', '%s reads from the tablets. Unknown, unheard sounds flow through your consciousness', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'reginald windsor EMOTE_IN_KEEP_READ');
INSERT INTO `script_texts` VALUES ('-1000862', '%s gasps.', null, null, '%s schnappt nach Luft.', null, null, null, null, null, '0', '2', '0', '0', 'bolvar EMOTE_IN_KEEP_GASP');
INSERT INTO `script_texts` VALUES ('-1000863', 'Curious... Windsor, in this vision, did you survive? I only ask because one thing that I can and will assure is your death. Here and now.', null, null, 'Seltsam... Windsor, habt Ihr in dieser Vision überlebt? Ich frage nur deshalb, weil ich für Euren Tod sorgen will und werde. Hier und jetzt.', null, null, null, null, null, '0', '0', '0', '1', 'onyxia SAY_IN_KEEP_9');
INSERT INTO `script_texts` VALUES ('-1000864', 'Dragon filth! Guards! Guards! Seize this monster!', null, null, 'Drachenabschaum! Wachen! Wachen! Ergreift dieses Monster!', null, null, null, null, null, '0', '1', '0', '22', 'bolvar SAY_IN_KEEP_1');
INSERT INTO `script_texts` VALUES ('-1000865', 'Yesss... Guards, come to your lord\'s aid!', null, null, 'Ja... Wachen, kommt Eurem Lord zu Hilfe!', null, null, null, null, null, '0', '0', '0', '1', 'onyxia SAY_IN_KEEP_10');
INSERT INTO `script_texts` VALUES ('-1000866', 'DO NOT LET HER ESCAPE!', null, null, 'LASST SIE NICHT ENTKOMMEN!', null, null, null, null, null, '0', '0', '0', '1', 'reginald windsor SAY_IN_KEEP_11');
INSERT INTO `script_texts` VALUES ('-1000867', 'Was this fabled, Windsor? If it was death that you came for then the prophecy has been fulfilled. May your consciousness rot in the Twisting Nether. Finish the rest of these meddlesome insects, children. Bolvar, you have been a pleasureable puppet.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'onyxia SAY_IN_KEEP_12');
INSERT INTO `script_texts` VALUES ('-1000868', 'You have failed him, mortalsss... Farewell!', null, null, 'Ihr habt versssagt, sssterbliche Wesssssssen... Lebt wohl!', null, null, null, null, null, '0', '1', '0', '0', 'onyxia SAY_IN_KEEP_12');
INSERT INTO `script_texts` VALUES ('-1000869', 'Reginald... I... I am sorry.', null, null, 'Reginald... Ich... Es tut mir leid.', null, null, null, null, null, '0', '0', '0', '0', 'bolvar SAY_IN_KEEP_13');
INSERT INTO `script_texts` VALUES ('-1000870', 'Bol... Bolvar... the medallion... use...', null, null, 'Bol... Bolvar... das Medaillon... benutzen...', null, null, null, null, null, '0', '0', '0', '0', 'reginald windsor SAY_IN_KEEP_14');
INSERT INTO `script_texts` VALUES ('-1000871', '%s dies.', null, null, '%s stirbt.', null, null, null, null, null, '0', '2', '0', '0', 'reginald windsor EMOTE_IN_KEEP_DIE');
INSERT INTO `script_texts` VALUES ('-1000872', '%s hisses', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'reginald windsor EMOTE_GUARD_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1000873', 'I know the way, insect. There is no need to prod me as if I were cattle.', null, null, 'Ich kenne den Weg, Insekt. Mich anzutreiben wie ein Stück Vieh, das ist wahrlich nicht nötig.', null, null, null, null, null, '0', '0', '0', '1', 'grark SAY_START');
INSERT INTO `script_texts` VALUES ('-1000874', 'Surely you do not think that you will get away with this incursion. They will come for me and you shall pay for your insolence.', null, null, 'Ihr glaubt doch nicht wirklich, dass Ihr damit ungestraft davonkommt. Sie werden mich befreien und Ihr werdet für Eure Dreistigkeit bezahlen.', null, null, null, null, null, '0', '0', '0', '1', 'grark SAY_PAY');
INSERT INTO `script_texts` VALUES ('-1000875', 'RUN THEM THROUGH BROTHERS!', null, null, 'MACHT SIE NIEDER, BRÜDER!', null, null, null, null, null, '0', '0', '0', '5', 'grark SAY_FIRST_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000876', 'I doubt you will be so lucky the next time you encounter my brethren.', null, null, 'Wenn Ihr das nächste Mal auf meine Brüder trefft, werdet Ihr nicht solches Glück haben.', null, null, null, null, null, '0', '0', '0', '1', 'grark SAY_FIRST_AMBUSH_END');
INSERT INTO `script_texts` VALUES ('-1000877', 'They come for you, fool!', null, null, 'Sie werden Euch erwischen, Dummkopf!', null, null, null, null, null, '0', '0', '0', '5', 'grark SAY_SEC_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000878', 'What do you think you accomplish from this, fool? Even now, the Blackrock armies make preparations to destroy your world.', null, null, 'Was wollt Ihr damit eigentlich erreichen, Dummkopf? In diesem Moment bereiten sich die Armeen des Schwarzfelsklans bereits darauf vor, Eure Welt zu vernichten.', null, null, null, null, null, '0', '0', '0', '1', 'grark SAY_SEC_AMBUSH_END');
INSERT INTO `script_texts` VALUES ('-1000879', 'On darkest wing they fly. Prepare to meet your end!', null, null, 'Auf dunkelsten Schwingen fliegen sie. Bereitet Euch auf Euer Ende vor!', null, null, null, null, null, '0', '0', '0', '5', 'grark SAY_THIRD_AMBUSH_START');
INSERT INTO `script_texts` VALUES ('-1000880', 'The worst is yet to come!', null, null, 'Das Schlimmste steht uns noch bevor!', null, null, null, null, null, '0', '0', '0', '1', 'grark SAY_THIRD_AMBUSH_END');
INSERT INTO `script_texts` VALUES ('-1000881', '%s laughs.', null, null, '%s lacht.', null, null, null, null, null, '0', '2', '0', '11', 'grark EMOTE_LAUGH');
INSERT INTO `script_texts` VALUES ('-1000882', 'Time to make your final stand, Insect.', null, null, 'Zeit für Euer letztes Gefecht, Insekt.', null, null, null, null, null, '0', '0', '0', '0', 'grark SAY_LAST_STAND');
INSERT INTO `script_texts` VALUES ('-1000883', 'Kneel, Grark', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'lexlort SAY_LEXLORT_1');
INSERT INTO `script_texts` VALUES ('-1000884', 'Grark Lorkrub, you have been charged and found guilty of treason against Horde. How you plead is unimportant. High Executioner Nuzrak, step forward.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'lexlort SAY_LEXLORT_2');
INSERT INTO `script_texts` VALUES ('-1000885', '%s raises his massive axe over Grark.', null, null, '%s hebt seine riesige Axt über Grark.', null, null, null, null, null, '0', '2', '0', '27', 'nuzark EMOTE_RAISE_AXE');
INSERT INTO `script_texts` VALUES ('-1000886', '%s raises his hand and then lowers it.', null, null, '%s hebt die Hand und lässt sie wieder sinken.', null, null, null, null, null, '0', '2', '0', '0', 'lexlort EMOTE_LOWER_HAND');
INSERT INTO `script_texts` VALUES ('-1000887', 'End him...', null, null, 'Macht ihm ein Ende...', null, null, null, null, null, '0', '0', '0', '0', 'lexlort SAY_LEXLORT_3');
INSERT INTO `script_texts` VALUES ('-1000888', 'You, soldier, report back to Kargath at once!', null, null, 'Ihr da, Soldat, macht Kargath unverzüglich Meldung!', null, null, null, null, null, '0', '0', '0', '1', 'lexlort SAY_LEXLORT_4');
INSERT INTO `script_texts` VALUES ('-1000889', '%s submits.', null, null, '%s unterwirft sich.', null, null, null, null, null, '0', '2', '0', '0', 'grark EMOTE_SUBMIT');
INSERT INTO `script_texts` VALUES ('-1000890', 'You have come to play? Then let us play!', null, null, 'Ihr seid zum Spielen gekommen? Dann lasst uns spielen!', null, null, null, null, null, '0', '0', '0', '0', 'grark SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000891', 'Beware $N! Look to the west!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000892', 'A defender has fallen!', null, null, 'Ein Verteidiger ist gefallen!', null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000893', 'Kolkar invaders are retreating!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000894', 'Kolkar are the strongest!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000895', 'For the Horde!', null, null, 'Für die Horde!', null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000896', 'Defend the bunkers!', null, null, 'Verteidigt die Bunker!', null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000897', 'Our foes will fall!', null, null, 'Unsere Gegner werden fallen!', null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000898', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000899', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000900', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000901', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000902', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000903', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000904', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1000905', 'Ok, let\'s go!!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'therylune SAY_THERYLUNE_START');
INSERT INTO `script_texts` VALUES ('-1000906', 'I can make it the rest of the way. $N. THANKS!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'therylune SAY_THERYLUNE_START');
INSERT INTO `script_texts` VALUES ('-1000948', 'Well then, let\'s get this started. The longer we\'re here, the more damage the undead could be doing back in Hilsbrad.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_START');
INSERT INTO `script_texts` VALUES ('-1000949', 'All right, this is where we really have to be on our paws. Be ready!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_REACH_BOTTOM');
INSERT INTO `script_texts` VALUES ('-1000950', 'Attack me if you will, but you won\'t stop me from getting back to Quae.', null, null, 'Greift mich an, wenn Ihr wollt, aber Ihr werdet mich nicht davon abhalten, zu Quae zurückzukehren.', null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_AGGRO_KINELORY');
INSERT INTO `script_texts` VALUES ('-1000951', 'You have my word that I shall find a use for your body after I\'ve killed you, Kinelory.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'jorell SAY_AGGRO_JORELL');
INSERT INTO `script_texts` VALUES ('-1000952', 'Watch my rear! I\'ll see what I can find in all this junk...', null, null, 'Gebt mir Deckung! Ich schaue mal, was ich zwischen all diesem Gerümpel finden kann...', null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_WATCH_BACK');
INSERT INTO `script_texts` VALUES ('-1000953', '%s begins rummaging through the apothecary\'s belongings.', null, null, '%s fängt an, die Habseligkeiten des Apothekers zu durchwühlen.', null, null, null, null, null, '0', '2', '0', '0', 'kinelory EMOTE_BELONGINGS');
INSERT INTO `script_texts` VALUES ('-1000954', 'I bet Quae\'ll think this is important. She\'s pretty knowledgeable about these things--no expert, but knowledgable.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_DATA_FOUND');
INSERT INTO `script_texts` VALUES ('-1000955', 'Okay, let\'s get out of here quick quick! Try and keep up. I\'m going to make a break for it.', null, null, 'Okay, dann mal raus hier, und zwar schnell! Nehmt die Beine in die Hand, ich werde jetzt losrennen.', null, null, null, null, null, '0', '0', '0', '0', 'kinelory SAY_ESCAPE');
INSERT INTO `script_texts` VALUES ('-1000958', 'You must protect me from monsters, who are living in this forest!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'stinky ignatz SAY_STINKY_BEGIN');
INSERT INTO `script_texts` VALUES ('-1000959', 'This part of forest are very danger for us. We must be a careful!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'stinky ignatz SAY_STINKY_FIRST_STOP');
INSERT INTO `script_texts` VALUES ('-1000960', 'Kill two monsters, who stay near Bogbean plant and then I gather a bogbean.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'stinky ignatz SAY_STINKY_2_MONSTERS');
INSERT INTO `script_texts` VALUES ('-1000961', 'I am gathering a bogbean. It takes some time.', null, null, null, null, null, null, null, null, '0', '0', '0', '69', 'stinky ignatz SAY_STINKY_GATHERING');
INSERT INTO `script_texts` VALUES ('-1000977', 'Oh, it\'s on now! But you thought I\'d be alone too, huh?!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tapoke slim jahn SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1000978', 'Okay, okay! No need to get all violent. I\'ll talk. I\'ll talk!', null, null, 'Gut, gut! Kein Grund, gleich Gewalt anzuwenden. Ich rede. Ich rede!', null, null, null, null, null, '0', '0', '0', '20', 'tapoke slim jahn SAY_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1000979', 'Whoa! This is way more than what I bargained for, you\'re on your own, Slim!', null, null, 'Boah! So hatten wir nicht gewettet. Nee, nee, jetzt seid Ihr auf Euch allein gestellt, Slim!', null, null, null, null, null, '0', '0', '0', '0', 'slim\'s friend SAY_FRIEND_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1000980', 'I have a few notes from the job back at my place. I\'ll get them and then meet you back in the inn.', null, null, 'Ich habe daheim ein paar Notizen von der Arbeit. Ich hole sie und wir treffen uns im Gasthaus.', null, null, null, null, null, '0', '0', '0', '1', 'tapoke slim jahn SAY_NOTES');
INSERT INTO `script_texts` VALUES ('-1000993', 'It\'s on! $N, meet my fists. Fists, say hello to $N.', null, null, 'Los geht\'s. $N, das sind meine Fäuste. Fäuste, sagt hallo zu $N.', null, null, null, null, null, '0', '0', '0', '0', 'dorius stonetender SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1000994', 'I\'m about to open a can on this $N.', null, null, 'Das wollte ich gerade feiern, $N.', null, null, null, null, null, '0', '0', '0', '0', 'dorius stonetender SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1033000', 'Follow me and I\'ll open the courtyard door for you.', null, null, 'Folgt mir und ich werde die Tür zum Hof für Euch öffnen.', null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_FREE_AS');
INSERT INTO `script_texts` VALUES ('-1033001', 'I have just the spell to get this door open. Too bad the cell doors weren\'t locked so haphazardly.', null, null, 'Ich habe nur den richtigen Zauber, um diese Tür zu öffnen. Zu schade, dass die Zellen nicht so halbherzig verschlossen waren.', null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_OPEN_DOOR_AS');
INSERT INTO `script_texts` VALUES ('-1033002', 'There it is! Wide open. Good luck to you conquering what lies beyond. I must report back to the Kirin Tor at once!', null, null, 'Geschafft! Sie steht weit offen. Viel Glück beim Kampf mit dem, was dahinter liegt. Ich muss den Kirin Tor sofort Bericht erstatten!', null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_POST_DOOR_AS');
INSERT INTO `script_texts` VALUES ('-1033003', 'Free from this wretched cell at last! Let me show you to the courtyard....', null, null, 'Endlich raus aus dieser jämmerlichen Zelle! Lasst mich Euch den Weg zum Hof zeigen...', null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_FREE_AD');
INSERT INTO `script_texts` VALUES ('-1033004', 'You are indeed courageous for wanting to brave the horrors that lie beyond this door.', null, null, 'Ihr seid wirklich mutig, dass Ihr den Schrecken, die hinter dieser Tür liegen, die Stirn bieten wollt.', null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_OPEN_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033005', 'There we go!', null, null, 'Jetzt geht es los!', null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_POST1_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033006', 'Good luck with Arugal. I must hurry back to Hadrec now.', null, null, 'Viel Glück mit Arugal. Jetzt muss ich schnell zu Hadrec zurück.', null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_POST2_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033007', 'About time someone killed the wretch.', null, null, 'Es wird langsam Zeit, dass jemand diese Kreatur tötet.', null, null, null, null, null, '0', '0', '1', '1', 'prisoner adamant SAY_BOSS_DIE_AD');
INSERT INTO `script_texts` VALUES ('-1033008', 'For once I agree with you... scum.', null, null, 'Ich muss Euch ausnahmsweise zustimmen... Abschaum.', null, null, null, null, null, '0', '0', '7', '1', 'prisoner ashcrombe SAY_BOSS_DIE_AS');
INSERT INTO `script_texts` VALUES ('-1033009', 'I have changed my mind loyal servants, you do not need to bring the prisoner all the way to my study, I will deal with him here and now.', null, null, 'Ich habe es mir überlegt, meine treuen Diener, ihr müsst den Gefangenen nicht in mein Arbeitszimmer bringen, ich kümmere mich gleich hier um ihn.', null, null, null, null, null, '0', '0', '0', '1', 'arugal SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1033010', 'Vincent!  You and your pathetic ilk will find no more success in routing my sons and I than those beggardly remnants of the Kirin Tor.', null, null, 'Vincent! Ihr und Euer jämmerlicher Haufen werden meine Sähne und mich so wenig überwinden können wie die kläglichen Überreste der Kirin Tor.', null, null, null, null, null, '0', '0', '0', '0', 'arugal SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1033011', 'If you will not serve my Master with your sword and knowledge of his enemies...', null, null, 'Wenn Ihr meinem Meister nicht mit Eurem Schwert und Eurem Wissen seiner Feinde dienlich seid...', null, null, null, null, null, '0', '0', '0', '1', 'arugal SAY_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1033012', 'Your moldering remains will serve ME as a testament to what happens when one is foolish enough to trespass in my domain!\n', null, null, 'Eure schwelenden Überreste werden MIR als Beweis dafür dienen, was passiert, wenn man töricht genug ist, sich in mein Reich zu wagen!', null, null, null, null, null, '0', '0', '0', '0', 'arugal SAY_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1033013', 'Who dares interfere with the Sons of Arugal?', null, null, 'Wer wagt es, die Söne von Arugal zu stören? Das ist Euer Untergang Fremdlinge!', null, null, null, null, null, '0', '1', '0', '0', 'boss_arugal YELL_FENRUS');
INSERT INTO `script_texts` VALUES ('-1033014', '%s vanishes.', null, null, '%s verschwindet.', null, null, null, null, null, '0', '2', '0', '0', 'prisoner ashcrombe EMOTE_VANISH_AS');
INSERT INTO `script_texts` VALUES ('-1033015', '%s fumbles with the rusty lock on the courtyard door.', null, null, '%s fingert an dem rostigen Schloss der Tür zum Hof herum.', null, null, null, null, null, '0', '2', '0', '133', 'prisoner adamant EMOTE_UNLOCK_DOOR_AD');
INSERT INTO `script_texts` VALUES ('-1033016', 'Arrrgh!', null, null, 'Arghhh!', null, null, null, null, null, '0', '0', '0', '0', 'deathstalker vincent SAY_VINCENT_DIE');
INSERT INTO `script_texts` VALUES ('-1033017', 'You, too, shall serve!', null, null, 'Auch Ihr sollt dienen!', null, null, null, null, null, '5793', '1', '0', '0', 'boss_arugal YELL_AGGRO');
INSERT INTO `script_texts` VALUES ('-1033018', 'Another Falls!', null, null, 'Noch einer fällt!', null, null, null, null, null, '5795', '1', '0', '0', 'boss_arugal YELL_KILLED_PLAYER');
INSERT INTO `script_texts` VALUES ('-1033019', 'Release your rage!', null, null, 'Entfesselt Eure Wut!', null, null, null, null, null, '5797', '1', '0', '0', 'boss_arugal YELL_COMBAT');
INSERT INTO `script_texts` VALUES ('-1036000', 'You there! Check out that noise.', null, null, 'Ihr da, untersucht dieses Geräusch!', null, null, null, null, null, '5775', '6', '7', '0', 'smite INST_SAY_ALARM1');
INSERT INTO `script_texts` VALUES ('-1036001', 'We\'re under attack!  A vast, ye swabs! Repel the invaders!', null, null, 'Wir werden angegriffen! Kommt, ihr Hunde! Wehrt die Eindringlinge ab!', null, null, null, null, null, '5777', '6', '7', '0', 'smite INST_SAY_ALARM2');
INSERT INTO `script_texts` VALUES ('-1036002', 'You land lubbers are tougher than I thought! I\'ll have to improvise!', null, null, 'Ihr Landratten seid zäher, als ich dachte! Ich muss wohl improvisieren!', null, null, null, null, null, '5778', '0', '0', '21', 'smite SAY_PHASE_2');
INSERT INTO `script_texts` VALUES ('-1036003', 'D\'ah! Now you\'re making me angry!', null, null, 'D\'ah! Jetzt macht Ihr mich wütend!', null, null, null, null, null, '5779', '0', '0', '15', 'smite SAY_PHASE_3');
INSERT INTO `script_texts` VALUES ('-1043000', 'At last! Naralex can be awakened! Come aid me, brave adventurers!', null, null, 'Endlich! Naralex kann erweckt werden! Kommt und helft mir, ihr tapferen Abenteurer', null, null, null, null, null, '0', '6', '0', '0', 'Disciple of Naralex - SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1043001', 'I must make the necessary preparations before the awakening ritual can begin. You must protect me!', null, null, 'Ich muss die notwendigen Vorbereitungen treffen, bevor das Erweckungsritual beginnen kann. Ihr müsst mich beschützen!', null, null, null, null, null, '0', '0', '0', '0', 'SAY_PREPARE');
INSERT INTO `script_texts` VALUES ('-1043002', 'These caverns were once a temple of promise for regrowth in the Barrens. Now, they are the halls of nightmares.', null, null, 'Diese Höhlen waren einst ein Tempel, der eine Wiederbegrünung des Ödlands versprach. Jetzt sind sie ein einziger Alptraum.', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_FIRST_CORNER');
INSERT INTO `script_texts` VALUES ('-1043003', 'Come. We must continue. There is much to be done before we can pull Naralex from his nightmare.', null, null, 'Kommt. Wir müssen weitermachen. Es muss noch viel getan werden, bevor wir Naralex aus seinem Alptraum erwecken können.', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_CONTINUE');
INSERT INTO `script_texts` VALUES ('-1043004', 'Within this circle of fire I must cast the spell to banish the spirits of the slain Fanglords.', null, null, 'Innerhalb dieses Feuerkreises muss ich den Zauber wirken, der die Geister der getöteten Giftzahnlords verbannt', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_CIRCLE_BANISH');
INSERT INTO `script_texts` VALUES ('-1043005', 'The caverns have been purified. To Naralex\'s chamber we go!', null, null, 'Die Höhlen wurden geläutert. Auf zu Naralex\' Kammer!', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_PURIFIED');
INSERT INTO `script_texts` VALUES ('-1043006', 'Beyond this corridor, Naralex lies in fitful sleep. Let us go awaken him before it is too late.', null, null, 'Am Ende dieses Ganges liegt Naralex in unruhigem Schlaf. Wir sollten ihn wecken, bevor es zu spät ist.', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_NARALEX_CHAMBER');
INSERT INTO `script_texts` VALUES ('-1043007', 'Protect me brave souls as I delve into the Emerald Dream to rescue Naralex and put an end to this corruption!', null, null, 'Beschützt mich, tapfere Seelen, während ich in den Smaragdgrünen Traum eintauche, um Naralex zu retten und dieser Verderbnis ein Ende zu setzen!', null, null, null, null, null, '0', '1', '0', '0', 'Disciple of Naralex - SAY_BEGIN_RITUAL');
INSERT INTO `script_texts` VALUES ('-1043008', '%s begins to perform the awakening ritual on Naralex.', null, null, '%s beginnt, das Erweckungsritual an Naralex auszuführen.', null, null, null, null, null, '0', '2', '0', '0', 'Disciple of Naralex - EMOTE_RITUAL_BEGIN');
INSERT INTO `script_texts` VALUES ('-1043009', '%s tosses fitfully in troubled sleep.', null, null, '%s wirft sich in unruhigem Schlaf hin und her', null, null, null, null, null, '0', '2', '0', '0', 'Naralex - EMOTE_NARALEX_AWAKE');
INSERT INTO `script_texts` VALUES ('-1043010', '%s writhes in agony. The Disciple seems to be breaking through.', null, null, '%s windet sich vor Schmerzen. Der Jünger scheint sich einen Weg zu bahnen.', null, null, null, null, null, '0', '2', '0', '0', 'Naralex - EMOTE_BREAK_THROUGH');
INSERT INTO `script_texts` VALUES ('-1043011', '%s dreams up a horrendous vision. Something stirs beneath the murky waters.', null, null, '%s hat eine entsetzliche Vision. Irgendetwas bewegt sich in dem trüben Wasser.', null, null, null, null, null, '0', '2', '0', '0', 'Naralex - EMOTE_VISION');
INSERT INTO `script_texts` VALUES ('-1043012', 'This $N is a minion from Naralex\'s nightmare no doubt!.', null, null, '$N ist zweifellos ein Diener aus Naralex\' Alptraum!', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_MUTANUS');
INSERT INTO `script_texts` VALUES ('-1043013', 'I AM AWAKE, AT LAST!', null, null, 'Endlich bin ich wach!', null, null, null, null, null, '5789', '1', '0', '0', 'Naralex - SAY_NARALEX_AWAKE');
INSERT INTO `script_texts` VALUES ('-1043014', 'At last! Naralex awakes from the nightmare.', null, null, 'Endlich! Naralex erwacht aus dem Alptraum.', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_AWAKE');
INSERT INTO `script_texts` VALUES ('-1043015', 'Ah, to be pulled from the dreaded nightmare! I thank you, my loyal Disciple, along with your brave companions.', null, null, 'Ah, wie gut, aus dem schrecklichen Alptraum erwacht zu sein! Ich danke Euch, mein treuer Jünger, und Euren tapferen Gefährten ebenso.', null, null, null, null, null, '0', '0', '0', '0', 'Naralex - SAY_NARALEX_THANKYOU');
INSERT INTO `script_texts` VALUES ('-1043016', 'We must go and gather with the other Disciplies. There is much work to be done before I can make another attempt to restore the Barrens. Farewell, brave souls!', null, null, 'Wir müssen uns mit den anderen Jüngern sammeln. Es gibt viel zu tun, bevor ich einen erneuten Versuch unternehmen kann, das Brachland wiederherzustellen. Gehabt Euch wohl, tapfere Seelen!', null, null, null, null, null, '0', '0', '0', '0', 'Naralex - SAY_FAREWELL');
INSERT INTO `script_texts` VALUES ('-1043017', 'Attacked! Help get this $N off of me!', null, null, 'D. $N greift mich an! Helft mir, es loszuwerden!', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1043018', 'Help!', null, null, 'Hilfe!', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1043019', 'Deal with this $N! I need to prepare to awake Naralex!', null, null, 'Da, d. $N! Kümmert Euch darum! Ich muss mich darauf vorbereiten, Naralex zu erwecken!', null, null, null, null, null, '0', '0', '0', '0', 'Disciple of Naralex - SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1047000', 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', null, null, 'Juhu! Endlich hier raus. Es wird aber nicht einfach werden. Haltet Eure Augen nach Ärger offen.', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_READY');
INSERT INTO `script_texts` VALUES ('-1047001', 'Up there is where Charlga Razorflank resides. Blasted old crone.', null, null, 'Dort oben residiert Charlga Klingenflanke. Die verdammte Greisin.', null, null, null, null, null, '0', '0', '0', '25', 'willix SAY_1');
INSERT INTO `script_texts` VALUES ('-1047002', 'There\'s blueleaf tuber in this trench! It\'s like gold waiting to be mined I tell you!', null, null, 'In diesem Graben gibt es Blaulaubknollen! Als würde das Gold nur darauf warten, ausgegraben zu werden, ich sag\'s Euch!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_2');
INSERT INTO `script_texts` VALUES ('-1047003', 'There could be danger around every corner here.', null, null, 'Hier könnten Gefahren hinter jeder Ecke lauern.', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_3');
INSERT INTO `script_texts` VALUES ('-1047004', 'I don\'t see how these foul animals live in this place... sheesh it smells!', null, null, 'Ich verstehe nicht, wie diese widerwärtigen Tiere an solch einem Ort leben können... puh, wie das hier stinkt!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_4');
INSERT INTO `script_texts` VALUES ('-1047005', 'I think I see a way for us to get out of this big twisted mess of a bramble.', null, null, 'Ich denke, ich sehe einen Weg, wie wir aus diesem verfluchten Dornengewirr herauskommen.', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_5');
INSERT INTO `script_texts` VALUES ('-1047006', 'Glad to be out of that wretched trench. Not much nicer up here though!', null, null, 'Ich bin froh, dass wir wieder aus diesem verflixten Graben raus sind. Doch hier oben ist es auch nicht viel besser!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_6');
INSERT INTO `script_texts` VALUES ('-1047007', 'Finally! I\'ll be glad to get out of this place.', null, null, 'Endlich! Ich bin froh, dass ich endlich hier \'raus komme.', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_7');
INSERT INTO `script_texts` VALUES ('-1047008', 'I think I\'ll rest a moment and catch my breath before heading back to Ratchet. Thanks for all the help!', null, null, 'Ich werde mich lieber einen Moment ausruhen und wieder zu Atem kommen, bevor ich nach Ratschet zurückkehre. Vielen Dank für Eure Hilfe!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_END');
INSERT INTO `script_texts` VALUES ('-1047009', '$N heading this way fast! To arms!', null, null, '$N kommt schnell näher! Zu den Waffen!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1047010', 'Eek! $N coming right at us!', null, null, 'Iiik! $N kommt direkt auf uns zu!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1047011', 'Egads! $N on me!', null, null, 'Fürwahr! $N hat\'s auf mich abgesehen!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1047012', 'Help! Get this $N off of me!', null, null, 'Hilfe! Haltet mir diesen $N fern!', null, null, null, null, null, '0', '0', '0', '0', 'willix SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES ('-1070000', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1070001', 'Who dares awaken Archaedas? Who dares the wrath of the makers!', null, null, 'Wer wagt es, Archaedas zu wecken? Wer riskiert den Zorn der Schöpfer?', null, null, null, null, null, '5855', '1', '0', '0', 'archaedas SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1070002', 'Awake ye servants, defend the discs!', null, null, 'Erwacht, ihr Diener! Verteidigt die Scheiben!', null, null, null, null, null, '5856', '1', '0', '0', 'archaedas SAY_AWAKE_GUARDIANS');
INSERT INTO `script_texts` VALUES ('-1070003', 'To my side, brothers. For the makers!', null, null, 'Zu mir, Brüder! Für die Schöpfer!', null, null, null, null, null, '5857', '1', '0', '0', 'archaedas SAY_AWAKE_WARDERS');
INSERT INTO `script_texts` VALUES ('-1070004', 'Reckless mortal.', null, null, 'Rücksichtslose Sterbliche.', null, null, null, null, null, '5858', '1', '0', '0', 'archaedas SAY_UNIT_SLAIN');
INSERT INTO `script_texts` VALUES ('-1070005', '%s breaks free from his stone slumber!', null, null, '%s befreit sich aus seinem Steinschlummer!', null, null, null, null, null, '0', '2', '0', '0', 'archaedas EMOTE_BREAK_FREE');
INSERT INTO `script_texts` VALUES ('-1090000', 'With your help, I can evaluate these tunnels.', null, null, 'Mit Eurer Hilfe kann ich diese Tunnel berechnen.', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_START');
INSERT INTO `script_texts` VALUES ('-1090001', 'Let\'s see if we can find out where these Troggs are coming from.... and put a stop to the invasion!', null, null, 'Mal sehen, ob ich herausfinden kann, woher diese Troggs kommen... und wie man die Invasion aufhalten kann!', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1090002', 'Such devastation... what a horrible mess...', null, null, 'Welch eine Zerstörung! Was für ein fürchterliches Durcheinander!', null, null, null, null, null, '0', '0', '0', '5', 'emi shortfuse SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1090003', 'It\'s quiet here....', null, null, 'Es ist still hier...', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1090004', '...too quiet.', null, null, '... zu still.', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1090005', 'Look! Over there at the tunnel wall!', null, null, 'Seht! Dort drüben an der Tunnelwand!', null, null, null, null, null, '0', '0', '0', '25', 'emi shortfuse SAY_LOOK_1');
INSERT INTO `script_texts` VALUES ('-1090006', 'Trogg incursion! Defend me while I blast the hole closed!', null, null, 'Dort sind Troggs durchgebrochen! Verteidigt mich, während ich das Loch zum Einsturz bringe!', null, null, null, null, null, '0', '0', '0', '5', 'emi shortfuse SAY_HEAR_1');
INSERT INTO `script_texts` VALUES ('-1090007', 'Get this, $n off of me!', null, null, 'Schafft mir $N vom Hals!', null, null, null, null, null, '0', '0', '0', '0', 'emi shortfuse SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1090008', 'I don\'t think one charge is going to cut it. Keep fending them off!', null, null, 'Ich glaube nicht, dass eine Sprengladung reicht. Drängt sie weiter zurück!', null, null, null, null, null, '0', '0', '0', '0', 'emi shortfuse SAY_CHARGE_1');
INSERT INTO `script_texts` VALUES ('-1090009', 'The charges are set. Get back before they blow!', null, null, 'Die Sprengladungen sind scharf! Kommt zurück, ehe sie hochgehen!', null, null, null, null, null, '0', '0', '0', '5', 'emi shortfuse SAY_CHARGE_2');
INSERT INTO `script_texts` VALUES ('-1090010', 'Incoming blast in 10 seconds!', null, null, 'Sprengung in 10 Sekunden!', null, null, null, null, null, '0', '1', '0', '5', 'emi shortfuse SAY_BLOW_1_10');
INSERT INTO `script_texts` VALUES ('-1090011', 'Incoming blast in 5 seconds. Clear the tunnel! Stay back!', null, null, 'Sprengung in 5 Sekunden. Raus aus dem Tunnel! Bleibt zurück!', null, null, null, null, null, '0', '1', '0', '5', 'emi shortfuse SAY_BLOW_1_5');
INSERT INTO `script_texts` VALUES ('-1090012', 'FIRE IN THE HOLE!', null, null, 'ACHTUNG!', null, null, null, null, null, '0', '1', '0', '25', 'emi shortfuse SAY_BLOW_1');
INSERT INTO `script_texts` VALUES ('-1090013', 'Well done! Without your help I would have never been able to thwart that wave of troggs.', null, null, 'Gut gemacht! Ohne Eure Hilfe hätte ich diese Welle von Troggs nie zurückwerfen können.', null, null, null, null, null, '0', '0', '0', '4', 'emi shortfuse SAY_FINISH_1');
INSERT INTO `script_texts` VALUES ('-1090014', 'Did you hear something?', null, null, 'Habt Ihr etwas gehört?', null, null, null, null, null, '0', '0', '0', '6', 'emi shortfuse SAY_LOOK_2');
INSERT INTO `script_texts` VALUES ('-1090015', 'I heard something over there.', null, null, 'Ich habe dort drüben etwas gehört.', null, null, null, null, null, '0', '0', '0', '25', 'emi shortfuse SAY_HEAR_2');
INSERT INTO `script_texts` VALUES ('-1090016', 'More troggs! Ward them off as I prepare the explosives!', null, null, 'Noch mehr Troggs! Wehrt sie ab, während ich den Sprengstoff vorbereite!', null, null, null, null, null, '0', '0', '0', '0', 'emi shortfuse SAY_CHARGE_3');
INSERT INTO `script_texts` VALUES ('-1090017', 'The final charge is set. Stand back!', null, null, 'Die letzte Ladung ist angebracht! Tretet zurück!', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_CHARGE_4');
INSERT INTO `script_texts` VALUES ('-1090018', '10 seconds to blast! Stand back!!!', null, null, '10 Sekunden bis zur Explosion! Tretet zurück!!!', null, null, null, null, null, '0', '1', '0', '5', 'emi shortfuse SAY_BLOW_2_10');
INSERT INTO `script_texts` VALUES ('-1090019', '5 seconds until detonation!!!!!', null, null, '5 Sekunden bis zur Detonation!', null, null, null, null, null, '0', '1', '0', '5', 'emi shortfuse SAY_BLOW_2_5');
INSERT INTO `script_texts` VALUES ('-1090020', 'Nice work! I\'ll set off the charges to prevent any more troggs from making it to the surface.', null, null, 'Saubere Arbeit! Ich löse die Sprengladungen aus, damit nicht noch mehr Troggs an die Oberfläche gelangen können.', null, null, null, null, null, '0', '0', '0', '1', 'emi shortfuse SAY_BLOW_SOON');
INSERT INTO `script_texts` VALUES ('-1090021', 'FIRE IN THE HOLE!', null, null, 'ACHTUNG!', null, null, null, null, null, '0', '1', '0', '0', 'emi shortfuse SAY_BLOW_2');
INSERT INTO `script_texts` VALUES ('-1090022', 'Superb! Because of your help, my people stand a chance of re-taking our beloved city. Three cheers to you!', null, null, 'Ausgezeichnet! Dank Eurer Hilfe haben wir eine Chance, unsere geliebte Stadt zurückzuerobern. Ein dreifaches Hurra für Euch!', null, null, null, null, null, '0', '0', '0', '0', 'emi shortfuse SAY_FINISH_2');
INSERT INTO `script_texts` VALUES ('-1090023', 'We come from below! You can never stop us!', null, null, 'Wir kommen aus der Tiefe! Ihr könnt uns niemals aufhalten!', null, null, null, null, null, '0', '1', '0', '1', 'grubbis SAY_GRUBBIS_SPAWN');
INSERT INTO `script_texts` VALUES ('-1090024', 'Usurpers! Gnomeregan is mine!', null, null, 'Thronräuber! Gnomeregan ist mein!', null, null, null, null, null, '5807', '1', '0', '0', 'thermaplugg SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1090025', 'My machines are the future! They\'ll destroy you all!', null, null, 'Meine Maschinen sind die Zukunft! Sie vernichten euch alle!', null, null, null, null, null, '5808', '1', '0', '0', 'thermaplugg  SAY_PHASE');
INSERT INTO `script_texts` VALUES ('-1090026', 'Explosions! MORE explosions! I\'ve got to have more explosions!', null, null, 'Explosionen! MEHR Explosionen! Ich brauche mehr Explosionen!', null, null, null, null, null, '5809', '1', '0', '0', 'thermaplugg  SAY_BOMB');
INSERT INTO `script_texts` VALUES ('-1090027', '...and stay dead! He got served', null, null, '... und bleib tot! Er bekam ', null, null, null, null, null, '5810', '1', '0', '0', 'thermaplugg  SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1090028', '$n attacking! Help!', null, null, 'Hilfe!', null, null, null, null, null, '0', '0', '0', '0', 'emi shortfuse SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1109000', 'The walls of the chamber tremble. Something is happening...', null, null, 'Die Wände des Raumes zittern. Irgendetwas geschieht hier...', null, null, null, null, null, '0', '2', '0', '0', 'malfurion stormrage EMOTE_MALFURION');
INSERT INTO `script_texts` VALUES ('-1109001', 'Be steadfast, champion. I know why it is that you are here and I know what it is that you seek. Eranikus will not give up the shard freely. He has been twisted... twisted by the same force that you seek to destroy.', null, null, 'Seid standhaft, $GHeld:Heldin; Ich weiß, warum Ihr hierher gekommen seid und ich weiß, wonach Ihr sucht. Eranikus wird den Splitter nicht freiwillig aufgeben. Er wurde korrumpiert... Korrupiert von genau der Macht, die Ihr zu zerstören versucht.', null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MALFURION1');
INSERT INTO `script_texts` VALUES ('-1109002', 'Are you really surprised? Is it hard to believe that the power of an Old God could reach even inside the Dream? It is true - Eranikus, Tyrant of the Dream, wages a battle against us all. The Nightmare follows in his wake of destruction.', null, null, 'Seid Ihr überrascht? Fällt es so schwer zu glauben, dass die Macht eines alten Gottes selbst das Innere des Traums erreichen könnte? Es ist wahr, Eranikus, er Tyrann des Traumes, schlägt eine Schlacht gegen uns alle. Ein Alptraum folgt seiner Spur der Zerstörung.', null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MALFURION2');
INSERT INTO `script_texts` VALUES ('-1109003', 'Understand this, Eranikus wants nothing more than to be brought to Azeroth from the Dream. Once he is out, he will stop at nothing to destroy my physical manifestation. This, however, is the only way in which you could recover the scepter shard.', null, null, 'Ihr müsst Euch über eines im klaren sein, Eranikus begehrt nichts mehr, als aus dem Traum zurück nach Azeroth zu gelangen. Wenn er dies einmal vollbracht hat, wird er mit allen Mitteln versuchen meine physische Manifestation zu vernichten. Allerdings ist dies der einzige Weg, den Szeptersplitter wiederzubeschaffen.', null, null, null, null, null, '0', '0', '0', '0', 'malfurion stormrge SAY_MAFLURION3');
INSERT INTO `script_texts` VALUES ('-1109004', 'You will bring him back into this world, champion.', null, null, 'Ihr werdet ihn zurück in diese Welt führen, Held', null, null, null, null, null, '0', '0', '0', '0', 'malfurion Stormrge SAY_MALFURION4');
INSERT INTO `script_texts` VALUES ('-1109005', 'The shield be down! Rise up Atal\'ai! Rise up!', null, null, 'Der Schild ist unten! Erhebt euch, Atal\'ai! Erhebt euch!', null, null, null, null, null, '5861', '6', '0', '0', 'jammalan SAY_JAMMALAN_INTRO');
INSERT INTO `script_texts` VALUES ('-1109006', 'HAKKAR LIVES!', null, null, 'HAKKAR LEBT!', null, null, null, null, null, '5870', '1', '0', '0', 'avatar SAY_AVATAR_BRAZIER_1');
INSERT INTO `script_texts` VALUES ('-1109007', 'I TASTE THE BLOOD OF LIFE!', null, null, 'ICH KOSTE DAS BLUT DES LEBENS!', null, null, null, null, null, '5868', '1', '0', '0', 'avatar SAY_AVATAR_BRAZIER_2');
INSERT INTO `script_texts` VALUES ('-1109008', 'I DRAW CLOSER TO YOUR WORLD!', null, null, null, null, null, null, null, null, '5867', '1', '0', '0', 'avatar SAY_AVATAR_BRAZIER_3');
INSERT INTO `script_texts` VALUES ('-1109009', 'I AM NEAR!', null, null, 'ICH BIN NAHE!', null, null, null, null, null, '5869', '1', '0', '0', 'avatar SAY_AVATAR_BRAZIER_4');
INSERT INTO `script_texts` VALUES ('-1109010', 'I AM HERE!', null, null, 'Ich BIN hier!', null, null, null, null, null, '0', '1', '0', '0', 'avatar SAY_AVATAR_SPAWN');
