INSERT INTO `script_texts` VALUES ('-1129005', 'All right, stay close. These fiends will jump right out of the shadows at you if you let your guard down.', null, null, 'Also gut, bleibt dicht zusammen. Wenn Ihr auch nur einen Moment unaufmerksam seid, wird Euch der Feind geradewegs aus dem Schatten anspringen.', null, null, null, null, null, '0', '0', '0', '0', 'belnistrasz SAY_READY');
INSERT INTO `script_texts` VALUES ('-1129006', 'Okay, here we go. It\'s going to take about five minutes to shut this thing down through the ritual. Once I start, keep the vermin off of me or it will be the end of us all!', null, null, 'Na schön, los geht\'s. Es wird etwa fünf Minuten dauern, das Ding mit einem Ritual zu verschließen. Haltet mir das Gezücht vom Leib, wenn ich erst einmal angefangen habe, oder es ist unser aller Ende!', null, null, null, null, null, '0', '0', '0', '0', 'belnistrasz SAY_START_RIT');
INSERT INTO `script_texts` VALUES ('-1129007', 'You\'ll rue the day you crossed me, $N', null, null, 'Ihr werdet den Tag bereuen, wo Ihr mir über den Weg gelaufen seid, $N.', null, null, null, null, null, '0', '0', '0', '0', 'belnistrasz SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1129008', 'Incoming $N - look sharp, friends!', null, null, 'Da kommt $N - Haltet die Augen offen, Freunde!', null, null, null, null, null, '0', '0', '0', '0', 'belnistrasz SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1129009', 'Three minutes left -- I can feel the energy starting to build! Keep up the solid defense!', null, null, 'Noch drei Minuten... ich spüre, wie sich die Energie aufbaut! Haltet die Abwehr aufrecht!', null, null, null, null, null, '0', '1', '0', '0', 'belnistrasz SAY_3_MIN');
INSERT INTO `script_texts` VALUES ('-1129010', 'Just two minutes to go! We\'re half way there, but don\'t let your guard down!', null, null, 'Noch zwei Minuten! Wir haben es schon zur Hälfte geschafft, aber Ihr dürft jetzt nicht nachlassen!', null, null, null, null, null, '0', '1', '0', '0', 'belnistrasz SAY_2_MIN');
INSERT INTO `script_texts` VALUES ('-1129011', 'One more minute! Hold on now, the ritual is about to take hold!', null, null, 'Nur noch eine Minute! Haltet durch, das Ritual ist gleich vollbracht!', null, null, null, null, null, '0', '1', '0', '0', 'belnistrasz SAY_1_MIN');
INSERT INTO `script_texts` VALUES ('-1129012', 'That\'s it -- we made it! The ritual is set in motion, and idol fires are about to go out for good! You truly are the heroes I thought you would be!', null, null, 'Das war\'s... wir haben es geschafft! Das Ritual ist in Gang gesetzt und die Götzenfeuer werden bald auf ewig verlöschen! Ihr seid wahrhaftig die Helden, für die ich Euch gehalten habe!', null, null, null, null, null, '0', '1', '0', '4', 'belnistrasz SAY_FINISH');
INSERT INTO `script_texts` VALUES ('-1189000', 'Ah, I have been waiting for a real challenge!', null, null, 'Ah, ich habe auf eine echte Herausforderung gewartet!', null, null, null, null, null, '5830', '1', '0', '0', 'herod SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189001', 'Blades of Light!', null, null, 'Klingen des Lichts!', null, null, null, null, null, '5832', '1', '0', '0', 'herod SAY_WHIRLWIND');
INSERT INTO `script_texts` VALUES ('-1189002', 'Light, give me strength!', null, null, 'Licht, gib mir Kraft!', null, null, null, null, null, '5833', '1', '0', '0', 'herod SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1189003', 'Hah, is that all?', null, null, 'Hah, ist das alles?', null, null, null, null, null, '5831', '1', '0', '0', 'herod SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1189004', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1189005', 'Infidels! They must be purified!', null, null, 'Ungläubige! Sie müssen geläutert werden!', null, null, null, null, null, '5835', '1', '0', '0', 'mograine SAY_MO_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189006', 'Unworthy!', null, null, 'Unwürdig!', null, null, null, null, null, '5836', '1', '0', '0', 'mograine SAY_MO_KILL');
INSERT INTO `script_texts` VALUES ('-1189007', 'At your side, milady!', null, null, 'Zu Diensten, Milady!', null, null, null, null, null, '5837', '1', '0', '0', 'mograine SAY_MO_RESSURECTED');
INSERT INTO `script_texts` VALUES ('-1189008', 'What, Mograine has fallen? You shall pay for this treachery!', null, null, 'Mograine ist gefallen? Für diesen Verrat sollt ihr büßen!', null, null, null, null, null, '5838', '1', '0', '0', 'whitemane SAY_WH_INTRO');
INSERT INTO `script_texts` VALUES ('-1189009', 'The Light has spoken!', null, null, 'Das Licht hat gesprochen!', null, null, null, null, null, '5839', '1', '0', '0', 'whitemane SAY_WH_KILL');
INSERT INTO `script_texts` VALUES ('-1189010', 'Arise, my champion!', null, null, 'Steht auf, mein Held!', null, null, null, null, null, '5840', '1', '0', '0', 'whitemane SAY_WH_RESSURECT');
INSERT INTO `script_texts` VALUES ('-1189011', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
