INSERT INTO `script_texts` VALUES ('-1189014', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1189015', 'The monster got what he deserved.', null, null, 'Das Monster hat bekommen was es verdient hat.', null, null, null, null, null, '0', '0', '1', '0', 'vishas SAY_TRIGGER_VORREL');
INSERT INTO `script_texts` VALUES ('-1189016', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
