INSERT INTO `script_texts` VALUES ('-1189019', 'You will not defile these mysteries!', null, null, 'Ihr werdet diese Mysterien nicht entweihen!', null, null, null, null, null, '5842', '1', '0', '0', 'doan SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1189020', 'Burn in righteous fire!', null, null, 'Brennt im Feuer der Rechtschaffenheit!', null, null, null, null, null, '5843', '1', '0', '0', 'doan SAY_SPECIALAE');
INSERT INTO `script_texts` VALUES ('-1189021', 'REUSE_ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE_ME');
INSERT INTO `script_texts` VALUES ('-1189035', 'The master has fallen! Avenge him my brethren!', null, null, 'Der Meister ist gefallen! Rächt ihn, meine Brüder!', null, null, null, null, null, '5834', '1', '0', '0', 'trainee SAY_TRAINEE_SPAWN');
INSERT INTO `script_texts` VALUES ('-1209000', 'How dare you enter my sanctum!', null, null, 'Wie könnt Ihr es wagen, mein Sanktum zu betreten!', null, null, null, null, null, '0', '0', '0', '0', 'zumrah SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1209001', 'Sands consume you!', null, null, 'Hol euch der Sand!', null, null, null, null, null, '5872', '1', '14', '0', 'zumrah SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1209002', 'Fall!', null, null, 'Fallt!', null, null, null, null, null, '5873', '1', '14', '0', 'zumrah SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1229000', '%s begins to regain its strength!', null, null, '%s beginnt seine Kraft zu regenerieren!', null, null, null, null, null, '0', '2', '0', '0', 'pyroguard EMOTE_BEGIN');
INSERT INTO `script_texts` VALUES ('-1229001', '%s is nearly at full strength!', null, null, '%s kommt allmählich wieder zu Kräften!', null, null, null, null, null, '0', '2', '0', '0', 'pyroguard EMOTE_NEAR');
INSERT INTO `script_texts` VALUES ('-1229002', '%s regains its power and breaks free of its bonds!', null, null, '%s erlangt die verlorene Macht zurück und wirft die Fesseln ab!', null, null, null, null, null, '0', '2', '0', '0', 'pyroguard EMOTE_FULL');
INSERT INTO `script_texts` VALUES ('-1229003', 'Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones.', null, null, 'Ha! Ha! Ha! Danke, dass Ihr mich befreit habt, ihr Dummköpfe. Ich werde es Euch vergelten, indem ich Euch das Fleisch von den Knochen brenne.', null, null, null, null, null, '0', '1', '0', '0', 'pyroguard SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1229004', 'Excellent... it would appear as if the meddlesome insects have arrived just in time to feed my legion. Welcome, mortals!', null, null, 'Vorzüglich... es scheint, als wären die lästigen Insekten gerade rechtzeitig eingetroffen, um meiner Legion als Nahrung zu dienen. Willkommen, sterbliche Wesen!', null, null, null, null, null, '0', '1', '0', '1', 'nefarius SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1229005', 'Let not even a drop of their blood remain upon the arena floor, my children. Feast on their souls!', null, null, 'Nicht einmal ein Tropfen ihres Blutes soll auf dem Arenaboden verbleiben, meine Kinder. Labt euch an ihren Seelen!', null, null, null, null, null, '0', '1', '0', '1', 'nefarius SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1229006', 'Foolsss...Kill the one in the dress!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_ATTACK_1');
INSERT INTO `script_texts` VALUES ('-1229007', 'Sire, let me join the fray! I shall tear their spines out with my bare hands!', null, null, 'Bitte lasst mich in den Kampf ziehen, Sire! Ich werde ihnen mit bloßen Händen den Rücken brechen!', null, null, null, null, null, '0', '1', '0', '1', 'rend SAY_REND_JOIN');
INSERT INTO `script_texts` VALUES ('-1229008', 'Concentrate your attacks upon the healer!', null, null, 'Konzentriert Eure Angriffe auf den Heiler!', null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_ATTACK_2');
INSERT INTO `script_texts` VALUES ('-1229009', 'Inconceivable!', null, null, 'Unfassbar!', null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_ATTACK_3');
INSERT INTO `script_texts` VALUES ('-1229010', 'Do not force my hand, children! I shall use your hides to line my boots.', null, null, 'Zwingt mich nicht, die Hand zu erheben, Kinder! Ich werde meine Stiefel mit Eurer Haut füttern.', null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_ATTACK_4');
INSERT INTO `script_texts` VALUES ('-1229011', 'Defilers!', null, null, 'Entweiher!', null, null, null, null, null, '0', '1', '0', '0', 'rend SAY_LOSE_1');
INSERT INTO `script_texts` VALUES ('-1229012', 'Impossible!', null, null, 'Unmöglich!', null, null, null, null, null, '0', '1', '0', '0', 'rend SAY_LOSE_2');
INSERT INTO `script_texts` VALUES ('-1229013', 'Your efforts will prove fruitless. None shall stand in our way!', null, null, 'Eure Bemühungen werden fruchtlos sein. Keiner wird uns den Weg verwehren!', null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_LOSE_3');
INSERT INTO `script_texts` VALUES ('-1229014', 'THIS CANNOT BE!!! Rend, deal with these insects.', null, null, 'DAS KANN NICHT SEIN!!! Rend, kümmert Euch um diese Insekten.', null, null, null, null, null, '0', '1', '0', '1', 'nefarius SAY_LOSE_4');
INSERT INTO `script_texts` VALUES ('-1229015', 'With pleasure...', null, null, 'Mit Vergnügen...', null, null, null, null, null, '0', '1', '0', '0', 'rend SAY_REND_ATTACK');
INSERT INTO `script_texts` VALUES ('-1229016', 'The Warchief shall make quick work of you, mortals. Prepare yourselves!', null, null, 'Der Kriegshäuptling macht kurzen Prozess mit Euch, sterbliche Wesen. Macht Euch bereit!', null, null, null, null, null, '0', '1', '0', '25', 'nefarius SAY_WARCHIEF');
INSERT INTO `script_texts` VALUES ('-1229017', 'Taste in my power!', null, null, 'Kostet meine Macht!', null, null, null, null, null, '0', '1', '0', '0', 'nefarius SAY_BUFF_GYTH');
INSERT INTO `script_texts` VALUES ('-1229018', 'Your victory shall be short lived. The days of both the Alliance and Horde are coming to an end. The next time we meet shall be the last.', null, null, null, null, null, null, null, null, '0', '1', '0', '1', 'nefarius SAY_VICTORY');
INSERT INTO `script_texts` VALUES ('-1229019', '%s is knocked off his drake!', null, null, '%s wird von seinem Drachen geworfen!', null, null, null, null, null, '0', '2', '0', '0', 'rend EMOTE_KNOCKED_OFF');
INSERT INTO `script_texts` VALUES ('-1229020', 'Intruders are destroying our eggs! Stop!!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'rookery hatcher - SAY_ROOKERY_EVENT_START');
INSERT INTO `script_texts` VALUES ('-1230000', 'Ah, hits the spot!', null, null, 'Ah, Perfekt!', null, null, null, null, null, '0', '0', '0', '0', 'rocknot SAY_GOT_BEER');
INSERT INTO `script_texts` VALUES ('-1230001', 'Come to aid the Throne!', null, null, 'Kommt und helft dem Thron!', null, null, null, null, null, '0', '1', '0', '0', 'dagran SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1230002', 'Hail to the king, baby!', null, null, 'Hoch lebe der König, mein Kleiner!', null, null, null, null, null, '0', '1', '0', '0', 'dagran SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1230003', 'You have challenged the Seven, and now you will die!', null, null, 'Ihr habt die Sieben herausgefordert; jetzt werdet Ihr sterben.', null, null, null, null, null, '0', '0', '0', '0', 'doomrel SAY_DOOMREL_START_EVENT');
INSERT INTO `script_texts` VALUES ('-1230004', 'The Sons of Thaurissan shall watch you perish in the Ring of the Law!', null, null, 'Die Söhne Thaurissans werden Euch im Ring des Gesetzes sterben sehen!', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_START_1');
INSERT INTO `script_texts` VALUES ('-1230005', 'You have been sentenced to death for crimes against the Dark Iron Nation!', null, null, 'Ihr wurdet wegen Verbrechen an der Dunkeleisennation zum Tode verurteilt.', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_START_2');
INSERT INTO `script_texts` VALUES ('-1230006', 'Unleash the fury and let it be done!', null, null, 'Entfesselt die Wut und lasst es geschehen!', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_OPEN_EAST_GATE');
INSERT INTO `script_texts` VALUES ('-1230007', 'But your real punishment lies ahead.', null, null, 'Doch Eure wahre Strafe liegt noch vor Euch.', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_SUMMON_BOSS_1');
INSERT INTO `script_texts` VALUES ('-1230008', 'Haha! I bet you thought you were done!', null, null, 'Haha! Ihr dachtet bestimmt, das wäre es gewesen!', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_SUMMON_BOSS_2');
INSERT INTO `script_texts` VALUES ('-1230009', 'Good Riddance!', null, null, 'Auf Nimmerwiedersehen!', null, null, null, null, null, '0', '1', '0', '0', 'grimstone SAY_OPEN_NORTH_GATE');
INSERT INTO `script_texts` VALUES ('-1230010', 'Thank you, $N! I\'m free!!!', null, null, 'Danke, $N! Ich bin frei!!!', null, null, null, null, null, '0', '0', '0', '0', 'dughal SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1230011', 'You locked up the wrong Marshal, $N. Prepare to be destroyed!', null, null, 'Ihr habt Euch den falschen Marschall ausgesucht, $N. Bereitet Euch auf Euer Ende vor!', null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1230012', 'I bet you\'re sorry now, aren\'t you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1230013', 'You better hold me back or $N is going to feel some prison house beatings.', null, null, 'Ihr haltet mich besser zurück, sonst wird $N schwere Prügel beziehen.', null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1230014', 'Let\'s get a move on. My gear should be in the storage area up this way...', null, null, 'Dann legen wir mal los. Meine Ausrüstung sollte im Lagerbereich da oben sein...', null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_START');
INSERT INTO `script_texts` VALUES ('-1230015', 'Check that cell, $N. If someone is alive in there, we need to get them out.', null, null, 'Untersucht diese Zelle, $N. Wenn jemand da drin am Leben ist, müssen wir ihn rausholen.', null, null, null, null, null, '0', '0', '0', '25', 'windsor SAY_CELL_DUGHAL_1');
INSERT INTO `script_texts` VALUES ('-1230016', 'Good work! We\'re almost there, $N. This way.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_CELL_DUGHAL_3');
INSERT INTO `script_texts` VALUES ('-1230017', 'This is it, $N. My stuff should be in that room. Cover me, I\'m going in!', null, null, 'Das ist es, $N. Mein Zeug müsste in dem Raum da sein. Gebt mir Deckung, ich geh rein!', null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_EQUIPMENT_1');
INSERT INTO `script_texts` VALUES ('-1230018', 'Ah, there it is!', null, null, 'Ah, da haben wir\'s!', null, null, null, null, null, '0', '0', '0', '0', 'windsor SAY_EQUIPMENT_2');
INSERT INTO `script_texts` VALUES ('-1230019', 'Can you feel the power, $N??? It\'s time to ROCK!', null, null, 'Spürt Ihr die Energie, $N??? Zeit für ROCK \'N\' ROLL!', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY__EQUIPMENT_3');
INSERT INTO `script_texts` VALUES ('-1230020', 'Now we just have to free Tobias and we can get out of here. This way!', null, null, 'Jetzt müssen wir nur noch Tobias befreien, dann können wir verschwinden. Hier geht\'s lang!', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY__EQUIPMENT_4');
INSERT INTO `script_texts` VALUES ('-1230021', 'Open it.', null, null, 'Öffnet es.', null, null, null, null, null, '0', '0', '0', '25', 'reginald_windsor SAY_CELL_JAZ_1');
INSERT INTO `script_texts` VALUES ('-1230022', 'I never did like those two. Let\'s get moving.', null, null, 'Ich konnte die beiden noch nie leiden. Gehen wir!', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY_CELL_JAZ_2');
INSERT INTO `script_texts` VALUES ('-1230023', 'Open it and be careful this time!', null, null, 'Macht das Ding auf, aber seid diesmal vorsichtig!', null, null, null, null, null, '0', '0', '0', '25', 'reginald_windsor SAY_CELL_SHILL_1');
INSERT INTO `script_texts` VALUES ('-1230024', 'That intolerant dirtbag finally got what was coming to him. Good riddance!', null, null, 'Dieser unerträgliche Schleimbeutel hat endlich bekommen, was er verdiente. Auf Nimmerwiedersehen!', null, null, null, null, null, '0', '0', '0', '66', 'reginald_windsor SAY_CELL_SHILL_2');
INSERT INTO `script_texts` VALUES ('-1230025', 'Alright, let\'s go.', null, null, 'Also dann mal los.', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY_CELL_SHILL_3');
INSERT INTO `script_texts` VALUES ('-1230026', 'Open it. We need to hurry up. I can smell those Dark Irons coming a mile away and I can tell you one thing, they\'re COMING!', null, null, 'Macht das Ding auf. Wir müssen uns beeilen. Ich kann diese Dunkeleisenzwerge auf eine Meile Entfernung riechen und ich sage Euch eines, sie KOMMEN!', null, null, null, null, null, '0', '0', '0', '25', 'reginald_windsor SAY_CELL_CREST_1');
INSERT INTO `script_texts` VALUES ('-1230027', 'He has to be in the last cell. Unless... they killed him.', null, null, 'Er muss in der letzten Zelle sein. Falls sie ihn nicht... getötet haben.', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY_CELL_CREST_2');
INSERT INTO `script_texts` VALUES ('-1230028', 'Get him out of there!', null, null, 'Frisches Brot zu verkaufen!', null, null, null, null, null, '0', '0', '0', '25', 'reginald_windsor SAY_CELL_TOBIAS_1');
INSERT INTO `script_texts` VALUES ('-1230029', 'Excellent work, $N. Let\'s find the exit. I think I know the way. Follow me!', null, null, 'Gut gemacht, $N. Suchen wir den Ausgang. Ich glaube, ich kenne den Weg. Folgt mir!', null, null, null, null, null, '0', '0', '0', '0', 'reginald_windsor SAY_CELL_TOBIAS_2');
INSERT INTO `script_texts` VALUES ('-1230030', 'We made it!', null, null, 'Wir haben es geschafft!', null, null, null, null, null, '0', '0', '0', '4', 'reginald_windsor SAY_FREE_1');
INSERT INTO `script_texts` VALUES ('-1230031', 'Meet me at Maxwell\'s encampment. We\'ll go over the next stages of the plan there and figure out a way to decode my tablets without the decryption ring.', null, null, 'Wir treffen uns in Maxwells Lager. Dort besprechen wir die nächsten Stufen des Plans und suchen nach einer Möglichkeit, meine Schrifttafeln ohne den Dechiffrierring zu dekodieren.', null, null, null, null, null, '0', '0', '0', '1', 'reginald_windsor SAY_FREE_2');
INSERT INTO `script_texts` VALUES ('-1230032', 'Thank you! I will run for safety immediately!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'tobias SAY_TOBIAS_FREE_1');
INSERT INTO `script_texts` VALUES ('-1230033', 'Finally!! I can leave this dump.', null, null, 'Endlich! Ich kann dieses elende Loch verlassen.', null, null, null, null, null, '0', '0', '0', '0', 'tobias SAY_TOBIAS_FREE_2');
INSERT INTO `script_texts` VALUES ('-1249000', 'How fortuitous. Usually, I must leave my lair to feed.', null, null, 'Was für ein Zufall. Normalerweise muss ich meinen Unterschlupf verlassen, um etwas zu essen.', null, null, null, null, null, '0', '1', '0', '0', 'onyxia SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1249001', 'Learn your place mortal!', null, null, 'Ihr müsst lernen, wo Euer Platz ist, sterbliches Wesen!', null, null, null, null, null, '0', '1', '0', '0', 'onyxia SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1249002', 'This meaningless exertion bores me. I\'ll incinerate you all from above!', null, null, 'Ich werde euch von oben verbrennen!', null, null, null, null, null, '0', '1', '0', '254', 'onyxia SAY_PHASE_2_TRANS');
INSERT INTO `script_texts` VALUES ('-1249003', 'It seems you\'ll need another lesson, mortals!', null, null, 'Es scheint, als wenn Ihr eine weitere Lektion braucht.', null, null, null, null, null, '0', '1', '0', '293', 'onyxia SAY_PHASE_3_TRANS');
INSERT INTO `script_texts` VALUES ('-1249004', '%s takes in a deep breath...', null, null, '%s atmet tief ein...', null, null, null, null, null, '0', '3', '0', '0', 'onyxia EMOTE_BREATH');
INSERT INTO `script_texts` VALUES ('-1289000', 'School is in session!', null, null, 'Der Unterricht hat begonnen!', null, null, null, null, null, '0', '1', '0', '0', 'gandling SAY_GANDLING_SPAWN');
INSERT INTO `script_texts` VALUES ('-1309000', 'Let the coils of hate unfurl!', null, null, 'Möge das Schlachten beginnen!', null, null, null, null, null, '8421', '1', '0', '0', 'venoxis SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1309001', 'Ssserenity..at lassst!', null, null, 'Klarheit... endlich!', null, null, null, null, null, '0', '1', '0', '0', 'venoxis SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309002', 'Lord Hir\'eek, grant me wings of vengance!', null, null, 'Lord Hireek, verleiht mir die Flügel der Rache!', null, null, null, null, null, '8417', '1', '0', '0', 'jeklik SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309003', 'I command you to rain fire down upon these invaders!', null, null, 'Ich befehle Euch, Feuer auf diese Eindringlinge herabregnen zu lassen!', null, null, null, null, null, '0', '1', '0', '0', 'jeklik SAY_RAIN_FIRE');
INSERT INTO `script_texts` VALUES ('-1309004', 'Finally ...death. Curse you Hakkar! Curse you!', null, null, 'Ahh... der Tod. Ich verfluche euch, Hakkar! Ich verfluche euch!', null, null, null, null, null, '8422', '1', '0', '0', 'jeklik SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309005', 'Draw me to your web mistress Shadra. Unleash your venom!', null, null, 'Hüllt mich in euer Netz, Herrin Shadra. Erfüllt mich mit eurem Gift!', null, null, null, null, null, '8418', '1', '0', '0', 'marli SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309006', 'Shadra, make of me your avatar!', null, null, 'Shadra, macht mich zu Eurem Avatar!', null, null, null, null, null, '0', '1', '0', '0', 'marli SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1309007', 'Aid me my brood!', null, null, 'Helft mir, meine Brut!', null, null, null, null, null, '0', '1', '0', '0', 'marli SAY_SPIDER_SPAWN');
INSERT INTO `script_texts` VALUES ('-1309008', 'Bless you mortal for this release. Hakkar controls me no longer...', null, null, 'Seid gesegnet für meine Befreiung, Sterbliche! Hakkar beherrscht mich nicht länger!', null, null, null, null, null, '8423', '1', '0', '0', 'marli SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309009', 'Shirvallah, fill me with your RAGE!', null, null, 'Shirvallah, erfülle mich mit deinem Zorn!', null, null, null, null, null, '8419', '1', '0', '0', 'thekal SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309010', 'Hakkar binds me no more! Peace at last!', null, null, 'Hakkar kontrolliert mich nicht länger. Endlich Frieden.', null, null, null, null, null, '8424', '1', '0', '0', 'thekal SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309011', 'Bethekk, your priestess calls upon your might!', null, null, 'Bethekk, eure Priesterin erfleht eure Macht!', null, null, null, null, null, '8416', '1', '0', '0', 'arlokk SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309012', 'Feast on $n, my pretties!', null, null, 'Weidet euch weiter an $N, meine Hübschen!', null, null, null, null, null, '0', '1', '0', '0', 'arlokk SAY_FEAST_PANTHER');
INSERT INTO `script_texts` VALUES ('-1309013', 'At last, I am free of the Soulflayer!', null, null, 'Die Macht des Seelenschinders fällt von mir!', null, null, null, null, null, '8412', '1', '0', '0', 'arlokk SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1309014', 'Welcome to da great show friends! Step right up to die!', null, null, 'Willkommen auf eurem Begräbnis! Tretet vor und sterbt!', null, null, null, null, null, '8425', '1', '0', '0', 'jindo SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309015', 'I\'ll feed your souls to Hakkar himself!', null, null, 'Ich werde Hakkar eure Seelen zum Fraß vorwerfen!', null, null, null, null, null, '8413', '1', '0', '0', 'mandokir SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309016', 'DING!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_DING_KILL');
INSERT INTO `script_texts` VALUES ('-1309017', 'GRATS!', null, null, 'GRATULATION!', null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_GRATS_JINDO');
INSERT INTO `script_texts` VALUES ('-1309018', '$N! I\'m watching you!', null, null, '$N! Ich behalte Euch im Auge!', null, null, null, null, null, '0', '1', '0', '0', 'mandokir SAY_WATCH');
INSERT INTO `script_texts` VALUES ('-1309019', 'Don\'t make me angry. You won\'t like it when I\'m angry.', null, null, 'Macht mich nicht wütend. Ihr werdet es gar nicht mögen wenn ich wütend bin.', null, null, null, null, null, '0', '4', '0', '0', 'mandokir SAY_WATCH_WHISPER');
INSERT INTO `script_texts` VALUES ('-1309020', 'PRIDE HERALDS THE END OF YOUR WORLD. COME, MORTALS! FACE THE WRATH OF THE SOULFLAYER!', null, null, 'Eure Überheblichkeit kündet bereits vom Ende dieser Welt! Kommt, sterbliche! Stellt euch dem Zorn des Seelenschinders!', null, null, null, null, null, '8414', '1', '0', '0', 'hakkar SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1309021', 'Fleeing will do you no good, mortals!', null, null, 'Fliehen wird euch nichts bringen, Sterbliche!', null, null, null, null, null, '0', '1', '0', '0', 'hakkar SAY_FLEEING');
INSERT INTO `script_texts` VALUES ('-1309022', 'You dare set foot upon Hakkari holy ground? Minions of Hakkar, destroy the infidels!', null, null, 'Ihr wagt es, Hakkari\'s heiligen Boden zu betreten? Diener Hakkar\'s zerstört die Ungläubigen!', null, null, null, null, null, '0', '6', '0', '0', 'hakkar SAY_MINION_DESTROY');
INSERT INTO `script_texts` VALUES ('-1309023', 'Minions of Hakkar, hear your God. The sanctity of this temple has been compromised. Invaders encroach upon holy ground! The Altar of Blood must be protected. Kill them all!', null, null, 'Diener Hakkar\'s, hört euren Gott. Die Heiligkeit dieses Tempels wurde gefährdet. Eindringlinge dringen in heiligen Grund ein! Der Altar des Blutes muss verteidigt werden. Tötet sie alle!', null, null, null, null, null, '0', '6', '0', '0', 'hakkar SAY_PROTECT_ALTAR');
INSERT INTO `script_texts` VALUES ('-1309024', '%s goes into a rage after seeing his raptor fall in battle!', null, null, '%s gerät in Rage, als er sieht, wie sein Raptor im Kampf stirbt!', null, null, null, null, null, '0', '2', '0', '0', 'mandokir EMOTE_RAGE');
INSERT INTO `script_texts` VALUES ('-1309025', 'The brood shall not fall!', null, null, 'Die Brut darf nicht versagen!', null, null, null, null, null, '0', '1', '0', '0', 'marli SAY_TRANSFORM_BACK');
INSERT INTO `script_texts` VALUES ('-1309026', '%s emits a deafening shriek!', null, null, '%s stößt einen betäubenden Schrei aus!', null, null, null, null, null, '0', '2', '0', '0', 'jeklik SAY_SHRIEK');
INSERT INTO `script_texts` VALUES ('-1309027', '%s begins to cast a Great Heal!', null, null, '%s beginnt, \'Großes Heilen\' zu wirken!', null, null, null, null, null, '0', '2', '0', '0', 'jeklik SAY_HEAL');
INSERT INTO `script_texts` VALUES ('-1329000', 'Thanks to Egan', null, null, 'Gelobt sei Egan!', null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED0');
INSERT INTO `script_texts` VALUES ('-1329001', 'Rivendare must die', null, null, 'Totenschwur muss vernichtet werden!', null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED1');
INSERT INTO `script_texts` VALUES ('-1329002', 'Who you gonna call?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED2');
INSERT INTO `script_texts` VALUES ('-1329003', 'Don\'t cross those beams!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'freed_soul SAY_ZAPPED3');
INSERT INTO `script_texts` VALUES ('-1329004', 'An Ash\'ari Crystal has fallen! Stay true to the Lich King, my brethren, and attempt to resummon it.', null, null, 'Ein Kristall von Ash\'ari ist gefallen! Steht treu zum Lichkönig, Brüder und Schwestern, und versucht, ihn erneut zu beschwören!', null, null, null, null, null, '0', '6', '0', '0', 'thuzadin acolyte SAY_ANNOUNCE_ZIGGURAT_1');
INSERT INTO `script_texts` VALUES ('-1329005', 'One of the Ash\'ari Crystals has been destroyed! Slay the intruders!', null, null, 'Einer der Kristalle von Ash\'ari wurde zerstört! Tötet die Eindringlinge!', null, null, null, null, null, '0', '6', '0', '0', 'thuzadin acolyte SAY_ANNOUNCE_ZIGGURAT_2');
INSERT INTO `script_texts` VALUES ('-1329006', 'An Ash\'ari Crystal has been toppled! Restore the ziggurat before the Necropolis is vulnerable!', null, null, 'Ein Kristall von Ash\'ari wurde umgestürzt! Stellt die Ziggurat wieder her, ehe die Nekropole verwundbar wird!', null, null, null, null, null, '0', '6', '0', '0', 'thuzadin acolyte SAY_ANNOUNCE_ZIGGURAT_3');
INSERT INTO `script_texts` VALUES ('-1329007', 'The Ash\'ari Crystals have been destroyed! The Slaughterhouse is vulnerable!', null, null, 'Die Kristalle von Ash\'ari wurden zerstört! Das Schlachthaus ist jetzt angreifbar!', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RIVENDARE');
INSERT INTO `script_texts` VALUES ('-1329008', 'Intruders at the Service Gate! Lord Rivendare must be warned!', null, null, 'Eindringlinge am Dienstbotentor! Baron Totenschwur muss gewarnt werden!', null, null, null, null, null, '0', '6', '0', '0', 'barthilas SAY_WARN_BARON');
INSERT INTO `script_texts` VALUES ('-1329009', 'Intruders! More pawns of the Argent Dawn, no doubt. I already count one of their number among my prisoners. Withdraw from my domain before she is executed!', null, null, 'Eindringlinge! Zweifellos mehr Handlanger der Argentumdämmerung. Es befindet sich bereits eine von ihnen in meiner Gefangenschaft. Zieht Euch aus meiner Domäne zurück, bevor ich sie hinrichten lasse!', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RUN_START');
INSERT INTO `script_texts` VALUES ('-1329010', 'You\'re still here? Your foolishness is amusing! The Argent Dawn wench needn\'t suffer in vain. Leave at once and she shall be spared!', null, null, 'Ihr seid immer noch hier? Eure Torheit ist wahrhaft amüsant! Das Mädchen der Argentumdämmerung muss jedoch nicht unnötig leiden. Verschwindet sofort, und ihr Leben wird verschont werden!', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RUN_10_MIN');
INSERT INTO `script_texts` VALUES ('-1329011', 'I shall take great pleasure in taking this poor wretch\'s life! It\'s not too late, she needn\'t suffer in vain. Turn back and her death shall be merciful!', null, null, 'Es wird mir großes Vergnügen bereiten, dieses armselige Mädchen ihres Lebens zu berauben! Aber es ist noch nicht zu spät, sie muss nicht unnötig leiden. Kehrt um und ich werde ihr einen gnadenvollen Tod bereiten.', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RUN_5_MIN');
INSERT INTO `script_texts` VALUES ('-1329012', 'May this prisoner\'s death serve as a warning. None shall defy the Scourge and live!', null, null, 'Möge der Tod dieses Gefangenen als ein Beispiel dienen. Niemand, der sich der Geißel in den Weg stellt, wird überleben!', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RUN_FAIL');
INSERT INTO `script_texts` VALUES ('-1329013', 'So you see fit to toy with the Lich King\'s creations? Ramstein, be sure to give the intruders a proper greeting.', null, null, 'Soso, Ihr haltet es also für angebracht, Euch mit den Schöpfungen des Lichkönigs anzulegen? Ramstein, die Eindringlinge sollten angemessen begrüßt werden.', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_ANNOUNCE_RAMSTEIN');
INSERT INTO `script_texts` VALUES ('-1329014', 'Time to take matters into my own hands. Come. Enter my domain and challenge the might of the Scourge!', null, null, 'Es ist an der Zeit, die Dinge selbst in die Hand zu nehmen. Kommt nur. Betretet mein Reich und fordert die Macht der Geißel heraus!', null, null, null, null, null, '0', '6', '0', '0', 'baron rivendare SAY_UNDEAD_DEFEAT');
INSERT INTO `script_texts` VALUES ('-1329015', 'You did it... you\'ve slain Baron Rivendare! The Argent Dawn shall hear of your valiant deeds!', null, null, 'Ihr habt es geschafft... Ihr habt Baron Totenschwur besiegt! Die Argentumdämmerung wird von Euren noblen Taten erfahren!', null, null, null, null, null, '0', '0', '0', '0', 'ysida SAY_EPILOGUE');
INSERT INTO `script_texts` VALUES ('-1329016', 'Today you have unmade what took me years to create! For this you shall all die by my hand!', null, null, 'Ihr habt heute an einem einzigen Tag ruiniert, was ich über Jahre hinweg erschaffen habe! Dafür werdet ihr alle von meiner Hand sterben!', null, null, null, null, null, '0', '1', '0', '0', 'dathrohan SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1329017', 'You fools think you can defeat me so easily? Face the true might of the Nathrezim!', null, null, 'Glaubt ihr Dummköpfe etwa, ihr könntet mich so leicht besiegen? Stellt euch der wahren Macht der Nathrezim!', null, null, null, null, null, '0', '1', '0', '0', 'dathrohan SAY_TRANSFORM');
INSERT INTO `script_texts` VALUES ('-1329018', 'Damn you mortals! All my plans of revenge, all my hate... all burned to ash...', null, null, 'Verdammt sollt ihr alle sein, sterbliche Wesen! All meine Rachepläne, all mein Hass... alles zu Asche verbrannt...', null, null, null, null, null, '0', '0', '0', '0', 'dathrohan SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1409000', '%s performs one last service for Ragnaros.', null, null, '%s leistet Ragnaros einen letzten Dienst...', null, null, null, null, null, '0', '2', '0', '0', 'geddon EMOTE_SERVICE');
INSERT INTO `script_texts` VALUES ('-1409001', 'REUSE ME', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'REUSE ME');
INSERT INTO `script_texts` VALUES ('-1409002', '%s refuses to die while its master is in trouble.', null, null, '%s weigert sich zu sterben so lange sein Meister in Schwierigkeiten steckt.', null, null, null, null, null, '0', '2', '0', '0', 'core rager EMOTE_LOWHP');
INSERT INTO `script_texts` VALUES ('-1409003', 'Reckless mortals, none may challenge the sons of the living flame!', null, null, 'Schwächliche Sterbliche! Niemand fordert die Söhne der Lebenden Flamme heraus!', null, null, null, null, null, '8035', '1', '0', '0', 'majordomo SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1409004', 'The runes of warding have been destroyed! Hunt down the infedels my bretheren.', null, null, 'Die Schutzrunen wurden zerstört! Vernichtet alle Ungläubigen, meine Brüder!', null, null, null, null, null, '8039', '6', '0', '0', 'majordomo SAY_SPAWN');
INSERT INTO `script_texts` VALUES ('-1409005', 'Ashes to Ashes!', null, null, 'Asche zu Asche!', null, null, null, null, null, '8037', '1', '0', '0', 'majordomo SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1409006', 'Burn mortals! Burn for this transgression!', null, null, 'Brennt, Sterbliche! Brennt für diesen Frevel!', null, null, null, null, null, '8036', '1', '0', '0', 'majordomo SAY_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1409007', 'Impossible! Stay your attack mortals! I submitt! I submitt!', null, null, 'Unmöglich! Haltet ein Sterbliche! Haltet ein! Ich gebe auf! Ich gebe auf! In eurer grenzenlosen Unverfrorenheit wolltet ihr die Geheimnisse der Lebenden Flamme an euch reißen! Schon bald werdet ihr die Leichtsinnigkeit eurer Suche bereuen! Ich werde euch nun verlassen, es ist an der Zeit den Herren dieses Hauses herbeizurufen. Wenn ihr eine Unterredung mit ihm wünscht werdet ihr diese mit eurem armseligen Leben bezahlen! Solltet ihr es dennoch wagen wollen, so sucht ruhig seine Ruhestätte auf!', null, null, null, null, null, '8038', '1', '0', '0', 'majordomo SAY_DEFEAT_1');
INSERT INTO `script_texts` VALUES ('-1409008', 'Behold Ragnaros, the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', null, null, 'Seht Ragnaros - den Feuerfürsten! Er, der schon uralt war, als eure Welt noch jung war. Verneigt euch vor ihm, Sterbliche! Verneigt euch vor eurem Untergang!', null, null, null, null, null, '8040', '1', '0', '0', 'ragnaros SAY_SUMMON_MAJ');
INSERT INTO `script_texts` VALUES ('-1409009', 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION?', null, null, 'ZU FRÜH! IHR HABT MICH ZU FRÜH ERWECKT, EXEKUTUS! WAS HAT DIESE STÖRUNG ZU BEDEUTEN???', null, null, null, null, null, '8043', '1', '0', '0', 'ragnaros SAY_ARRIVAL1_RAG');
INSERT INTO `script_texts` VALUES ('-1409010', 'These mortal infidels, my lord! They have invaded your sanctum, and seek to steal your secrets!', null, null, 'Diese ungläubigen Sterblichen, mein Fürst! Sie drangen in Euer Allerheiligstes ein und trachteten danach Eure Geheimnisse zu stehlen!', null, null, null, null, null, '8041', '1', '0', '0', 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT INTO `script_texts` VALUES ('-1409011', 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE, AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', null, null, 'NARR! DU LÄSST ES ZU, DAS SICH DIESE INSEKTEN IM GEHEILIGTEN KERN HERUMTREIBEN? UND NUN FÜHRST DU SIE AUCH NOCH IN MEINE HEIMSTÄTTE? DU ENTTÄUSCHT MICH, EXECUTUS! IN DER TAT WERDE ICH GERECHTIGKRILT WALTEN LASSEN!', null, null, null, null, null, '8044', '1', '0', '0', 'ragnaros SAY_ARRIVAL3_RAG');
INSERT INTO `script_texts` VALUES ('-1409012', 'NOW FOR YOU, INSECTS. BOLDLY YOU SAUGHT THE POWER OF RAGNAROS NOW YOU SHALL SEE IT FIRST HAND.', null, null, 'NUN ZU EUCH, INSEKTEN! MUTIG HABT IHR NACH DER MACHT VON RAGNAROS GESUCHT. NUN SOLLT IHR SIE AUS ERSTER HAND ERFAHREN!', null, null, null, null, null, '8045', '1', '0', '0', 'ragnaros SAY_ARRIVAL5_RAG');
INSERT INTO `script_texts` VALUES ('-1409013', 'COME FORTH, MY SERVANTS! DEFEND YOUR MASTER!', null, null, 'Kommt herbei, meine Diener! Verteidigt euren Herren!', null, null, null, null, null, '8049', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS1');
INSERT INTO `script_texts` VALUES ('-1409014', 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH YOU CREATURES OF HATE! YOUR MASTER CALLS!', null, null, 'NIEMAND BESIEGT DIE LEBENDE FLAMME! KOMMT IHR DIENER DES FEUERS! VORWÄRTS IHR KREATUREN DES HASSES! EUER MEISTER RUFT EUCH!!', null, null, null, null, null, '8050', '1', '0', '0', 'ragnaros SAY_REINFORCEMENTS2');
INSERT INTO `script_texts` VALUES ('-1409015', 'BY FIRE BE PURGED!', null, null, 'DAS FEUER WIRD EUCH LÄUTERN!', null, null, null, null, null, '8046', '1', '0', '0', 'ragnaros SAY_HAND');
INSERT INTO `script_texts` VALUES ('-1409016', 'TASTE THE FLAMES OF SULFURON!', null, null, 'SPÜRT DIE FLAMMEN VON SULFURON!', null, null, null, null, null, '8047', '1', '0', '0', 'ragnaros SAY_WRATH');
INSERT INTO `script_texts` VALUES ('-1409017', 'DIE INSECT!', null, null, 'STIRB, INSEKT!', null, null, null, null, null, '8051', '1', '0', '0', 'ragnaros SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1409018', 'MY PATIENCE IS DWINDILING! COME NATS TO YOUR DEATH!', null, null, 'MEINE GEDULD SCHWINDET! KOMMT SCHMEISSFLIEGEN, DER TOD WARTET!', null, null, null, null, null, '8048', '1', '0', '0', 'ragnaros SAY_MAGMABURST');
INSERT INTO `script_texts` VALUES ('-1409019', 'You think you\'ve won already? Perhaps you\'ll need another lesson in pain!', null, null, 'Ihr glaubt also, Ihr hättet schon gewonnen? Mir scheint, dass Ihr noch eine Lektion in Sachen Schmerz braucht!', null, null, null, null, null, '0', '1', '0', '0', 'majordomo SAY_LAST_ADD');
INSERT INTO `script_texts` VALUES ('-1409020', 'Brashly you have come to rest the secrets of the living flame. You will soon regret the recklessness of your quest.', null, null, 'In eurer grenzenlosen Unverfrorenheit wolltet ihr die Geheimnisse der Lebenden Flamme an euch reißen! Schon bald werdet ihr die Leichtsinnigkeit eurer Suche bereuen!', null, null, null, null, null, '0', '1', '0', '0', 'majordomo SAY_DEFEAT_2');
INSERT INTO `script_texts` VALUES ('-1409021', 'I go now to summon the lord whos house this is. Should you seek an audiance with him your paltry lives will surly be forfit. Nevertheless seek out his lair if you dare!', null, null, 'meine Flamme! Bitte nehmt nicht meine Flamme weg...', null, null, null, null, null, '0', '1', '0', '0', 'majordomo SAY_DEFEAT_3');
INSERT INTO `script_texts` VALUES ('-1409022', 'My flame! Please don\'t take away my flame... ', null, null, 'Ich werde euch nun verlassen. Es ist an der Zeit den Herrn dieses Hauses herbeizurufen. Wenn ihr eine Unterredung mit ihm wünscht, werdet ihr diese mit euren armseligen Leben bezahlen! Solltet ihr es dennoch wagen wollen, so sucht ruhig seine Ruhestätte auf!', null, null, null, null, null, '8042', '1', '0', '0', 'ragnaros SAY_ARRIVAL4_MAJ');
INSERT INTO `script_texts` VALUES ('-1409023', 'Very well, $N.', null, null, 'Nun gut, $N.', null, null, null, null, null, '0', '0', '0', '0', 'majordomo SAY_SUMMON_0');
INSERT INTO `script_texts` VALUES ('-1409024', 'Impudent whelps! You\'ve rushed headlong to your own deaths! See now, the master stirs!', null, null, 'Unverschämte Welpen! Ihr seid sehenden Auges dem Tod in die Arme gelaufen! Seht her, der Meister regt sich!', null, null, null, null, null, '0', '1', '0', '0', 'majordomo SAY_SUMMON_1');
INSERT INTO `script_texts` VALUES ('-1429000', 'The demon is loose! Quickly we must restrain him!', null, null, 'Der Dämon wurde freigesetzt! Wir müssen ihn wieder bannen, schnell!', null, null, null, null, null, '0', '6', '0', '0', 'highborne summoner SAY_FREE_IMMOLTHAR');
INSERT INTO `script_texts` VALUES ('-1429001', 'Who dares disrupt the sanctity of Eldre\'Thalas? Face me, cowards!', null, null, 'Wer wagt es die Grenzen von Eldre\'Thalas zu verletzten? Stellt Euch, Feiglinge!', null, null, null, null, null, '0', '6', '0', '0', 'prince tortheldrin SAY_KILL_IMMOLTHAR');
INSERT INTO `script_texts` VALUES ('-1429002', 'At last... Freed from his cursed grasp!', null, null, 'Endlich... Befreit von seiner Umklammerung der Verfluchung!', null, null, null, null, null, '0', '6', '0', '0', 'old ironbark SAY_IRONBARK_REDEEM');
INSERT INTO `script_texts` VALUES ('-1469000', 'None of your kind should be here! You\'ve doomed only yourselves!', null, null, 'Wie seid Ihr hier hereingekommen? Euresgleichen sollte nicht hier sein! Ich werde euch vernichten!', null, null, null, null, null, '8286', '1', '0', '0', 'broodlord SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1469001', 'Clever Mortals but I am not so easily lured away from my sanctum!', null, null, 'Ah! Sehr raffiniert, Sterbliche. Aber ich bin nicht so einfach von meinem Allerheiligsten weglocken!', null, null, null, null, null, '8287', '1', '0', '0', 'broodlord SAY_LEASH');
INSERT INTO `script_texts` VALUES ('-1469002', 'Run! They are coming!', null, null, 'Lauft! Sie kommen!', null, null, null, null, null, '0', '1', '0', '0', 'vaelastrasz blackwing tech SAY_INTRO_TECH');
INSERT INTO `script_texts` VALUES ('-1469003', '%s flinches as its skin shimmers.', null, null, '%s schreckt zurück, als seine Haut zu schimmern beginnt.', null, null, null, null, null, '0', '2', '0', '0', 'chromaggus EMOTE_SHIMMER');
INSERT INTO `script_texts` VALUES ('-1469004', 'In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say...', null, null, 'In dieser Welt in der die Zeit Euer Feind ist, ist sie mein größter Verbündeter. Dieses große Spiel des Lebens, von dem ihr denkt, dass Ihr es spielt, spielt in der Tat mit Euch. Dazu sage ich...', null, null, null, null, null, '0', '1', '0', '1', 'victor_nefarius SAY_GAMESBEGIN_1');
INSERT INTO `script_texts` VALUES ('-1469005', 'Let the games begin!', null, null, 'Lasst die Spiele beginnen!', null, null, null, null, null, '8280', '1', '0', '22', 'victor_nefarius SAY_GAMESBEGIN_2');
INSERT INTO `script_texts` VALUES ('-1469006', 'Ah...the heroes. You are persistent, aren\'t you? Your ally here attempted to match his power against mine - and paid the price. Now he shall serve me...by slaughtering you.', null, null, 'Ah... die Helden. Ziemlich beharrlich, nicht wahr? Euer Gefährte hier, hat versucht seine Kräfte mit den meinen zu messen - und musste dafür bezahlen. Jetzt wird er mir dienen... indem er Euch vernichtet..', null, null, null, null, null, '8279', '1', '0', '23', 'victor_nefarius SAY_NEFARIUS_CORRUPT');
INSERT INTO `script_texts` VALUES ('-1469007', 'Well done, my minions. The mortals\' courage begins to wane! Now, let\'s see how they contend with the true Lord of Blackrock Spire!', null, null, 'Sehr gut, meine Diener. Der Mut der Sterblichen scheint zu schwinden! Nun lasst uns sehen, wie sie sich gegen den wahren Herrscher des Schwarzfels behaupten werden!', null, null, null, null, null, '8288', '1', '0', '0', 'nefarian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1469008', 'Enough! Now you vermin shall feel the force of my birthright, the fury of the earth itself.', null, null, 'Genug! Nun sollt ihr Ungeziefer meine wahre Macht erfahren, die Macht der Erde selbst.', null, null, null, null, null, '8289', '1', '0', '0', 'nefarian SAY_XHEALTH');
INSERT INTO `script_texts` VALUES ('-1469009', 'BURN! You wretches! BURN!', null, null, 'Brennt, Elende! Brennt!', null, null, null, null, null, '8290', '1', '0', '0', 'nefarian SAY_SHADOWFLAME');
INSERT INTO `script_texts` VALUES ('-1469010', 'Impossible! Rise my minions! Serve your master once more!', null, null, 'Unmöglich! Erhebt Euch, meine Diener! Kämpft erneut für Euren Meister!', null, null, null, null, null, '8291', '1', '0', '0', 'nefarian SAY_RAISE_SKELETONS');
INSERT INTO `script_texts` VALUES ('-1469011', 'Worthless $N! Your friends will join you soon enough!', null, null, 'Wertloses Geschöpf $N! Deine Freunde werden dir schon bald folgen!', null, null, null, null, null, '8293', '1', '0', '0', 'nefarian SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1469012', 'This cannot be!  I am the master here!  You mortals are nothing to my kind!  Do you hear me? Nothing!', null, null, 'Das kann nicht sein! Niemand besiegt mich! Ihr Sterblichen seid nichts im Vergleich zu Meinesgleichen! Hört Ihr mich? Nichts!', null, null, null, null, null, '8292', '1', '0', '0', 'nefarian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1469013', 'Mages too? You should be more careful when you play with magic...', null, null, 'Auch Magier? Ihr solltet vorsichtiger sein, wenn Ihr mit Magie spielt...', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_MAGE');
INSERT INTO `script_texts` VALUES ('-1469014', 'Warriors, I know you can hit harder than that! Let\'s see it!', null, null, 'Krieger, Ich bin mir sicher, dass ihr kräftiger als das zuschlagen könnt!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_WARRIOR');
INSERT INTO `script_texts` VALUES ('-1469015', 'Druids and your silly shapeshifting. Let\'s see it in action!', null, null, 'Druiden und ihre lächerliche Gestaltwandlung. Zeigt mal was Ihr könnt!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_DRUID');
INSERT INTO `script_texts` VALUES ('-1469016', 'Priests! If you\'re going to keep healing like that, we might as well make it a little more interesting!', null, null, 'Priester! Wenn Ihr weiterhin so heilt, können wir es auch gerne etwas interessanter gestalten!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_PRIEST');
INSERT INTO `script_texts` VALUES ('-1469017', 'Paladins, I\'ve heard you have many lives. Show me.', null, null, 'Paladine... ich habe gehört, dass Ihr viele Leben habt. Zeigt es mir.', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_PALADIN');
INSERT INTO `script_texts` VALUES ('-1469018', 'Shamans, show me what your totems can do!', null, null, 'Schamane, zeigt mir was eure Totems können!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_SHAMAN');
INSERT INTO `script_texts` VALUES ('-1469019', 'Warlocks, you shouldn\'t be playing with magic you don\'t understand. See what happens?', null, null, 'Hexenmeister, Ihr solltet nicht mit Magie spielen, die Ihr nicht versteht. Seht Ihr was ich meine?', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_WARLOCK');
INSERT INTO `script_texts` VALUES ('-1469020', 'Hunters and your annoying pea-shooters!', null, null, 'Jäger und ihre lästigen Knallbüchsen!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_HUNTER');
INSERT INTO `script_texts` VALUES ('-1469021', 'Rogues? Stop hiding and face me!', null, null, 'Schurken? Kommt aus den Schatten und zeigt Euch!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_ROGUE');
INSERT INTO `script_texts` VALUES ('-1469022', 'You\'ll pay for forcing me to do this.', null, null, 'Ihr werdet dafür bezahlen, dass ihr mich zu so etwas gezwungen habt!', null, null, null, null, null, '8275', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN1');
INSERT INTO `script_texts` VALUES ('-1469023', 'Fools! These eggs are more precious than you know!', null, null, 'Narren! Diese Eier sind weit wertvoller als ihr glaubt.', null, null, null, null, null, '8276', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN2');
INSERT INTO `script_texts` VALUES ('-1469024', 'No - not another one! I\'ll have your heads for this atrocity!', null, null, 'Nein! Nicht noch eines! Für diese Gräueltat werde ich mir eure Köpfe holen.', null, null, null, null, null, '8277', '1', '0', '0', 'razorgore SAY_EGGS_BROKEN3');
INSERT INTO `script_texts` VALUES ('-1469025', 'If I fall into the abyss I\'ll take all of you mortals with me...', null, null, 'Wenn ich in den Abgrund stürze werde ich euch Sterbliche mit ins Verderben reißen...', null, null, null, null, null, '8278', '1', '0', '0', 'razorgore SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1469026', 'Too late, friends! Nefarius\' corruption has taken hold...I cannot...control myself.', null, null, 'Zu spät... Freunde. Nefarius\' üble Macht wirkt bereits. Ich habe mich nicht... nicht mehr unter Kontrolle.', null, null, null, null, null, '8281', '1', '0', '1', 'vaelastrasz SAY_LINE1');
INSERT INTO `script_texts` VALUES ('-1469027', 'I beg you, mortals - FLEE! Flee before I lose all sense of control! The black fire rages within my heart! I MUST- release it!', null, null, 'Ich bitte euch, Sterbliche: Flieht! Flieht, bevor ich endgültig die Kontrolle verliere. Das schwarze Feuer wütet in meinem Herzen. Ich muss es freigeben!', null, null, null, null, null, '8282', '1', '0', '1', 'vaelastrasz SAY_LINE2');
INSERT INTO `script_texts` VALUES ('-1469028', 'FLAME! DEATH! DESTRUCTION! Cower, mortals before the wrath of Lord...NO - I MUST fight this! Alexstrasza help me, I MUST fight it!', null, null, 'FLAMMEN! TOD! ZERSTÖRUNG! KNIET NIEDER STERBLICHE ODER DER ZORN VON LORD... NEIN! ICH MUSS ES BEKÄMPFEN!', null, null, null, null, null, '8283', '1', '0', '1', 'vaelastrasz SAY_LINE3');
INSERT INTO `script_texts` VALUES ('-1469029', 'Nefarius\' hate has made me stronger than ever before! You should have fled while you could, mortals! The fury of Blackrock courses through my veins!', null, null, 'Nefarius\' Hass hat mich stärker als jemals zuvor werden lassen. Ihr hättet fliehen sollen, solange Ihr noch konntet, Sterbliche! Die Wut von Schwarzfels pulsiert durch meine Adern!', null, null, null, null, null, '8285', '1', '0', '0', 'vaelastrasz SAY_HALFLIFE');
INSERT INTO `script_texts` VALUES ('-1469030', 'Forgive me, $N! Your death only adds to my failure!', null, null, 'Vergebt mir $N, euer Tod trägt nur noch mehr zu meinem Versagen bei.', null, null, null, null, null, '8284', '1', '0', '0', 'vaelastrasz SAY_KILLTARGET');
INSERT INTO `script_texts` VALUES ('-1469031', 'Death Knights, get over here!', null, null, 'Todesritter... kommt hierher!', null, null, null, null, null, '0', '1', '0', '0', 'nefarian SAY_DEATH_KNIGHT');
INSERT INTO `script_texts` VALUES ('-1469032', 'Get up, little red wyrm...and destroy them!', null, null, null, null, null, null, null, null, '0', '1', '0', '1', 'victor_nefarius SAY_NEFARIUS_CORRUPT_2');
INSERT INTO `script_texts` VALUES ('-1469033', '%s flee as the controlling power of the orb is drained.', null, null, '%s flieht während die kontrollierenden Kräfte der Kugel schwinden.', null, null, null, null, null, '0', '2', '0', '0', 'razorgore EMOTE_TROOPS_FLEE');
INSERT INTO `script_texts` VALUES ('-1469034', 'Run! They are coming.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'blackwing technician SAY_TECHNICIAN_RUN');
INSERT INTO `script_texts` VALUES ('-1469035', 'Orb of Domination loses power and shuts off!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', 'razorgore EMOTE_ORB_SHUT_OFF');
INSERT INTO `script_texts` VALUES ('-1509000', '%s senses your fear.', null, null, '%s fühlt Eure Angst.', null, null, null, null, null, '0', '2', '0', '0', 'moam EMOTE_AGGRO');
INSERT INTO `script_texts` VALUES ('-1509001', '%s bristles with energy!', null, null, '%s strotzt vor Energie!', null, null, null, null, null, '0', '2', '0', '0', 'moan EMOTE_MANA_FULL');
INSERT INTO `script_texts` VALUES ('-1509028', '%s drains your mana and turns to stone.', null, null, '%s entzieht Euch Euer Mana und versteinert Euch.', null, null, null, null, null, '0', '2', '0', '0', 'moam EMOTE_ENERGIZING');
INSERT INTO `script_texts` VALUES ('-1509002', '%s sets eyes on $N!', null, null, '%s behält $N im Blickfeld!', null, null, null, null, null, '0', '2', '0', '0', 'buru EMOTE_TARGET');
INSERT INTO `script_texts` VALUES ('-1509003', 'They come now. Try not to get yourself killed, young blood.', null, null, 'Nun kommen sie. Lasst Euch nicht töten, junges Blut.', null, null, null, null, null, '0', '1', '0', '22', 'andorov SAY_ANDOROV_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1509004', 'Remember, Rajaxx, when I said I\'d kill you last?', null, null, 'Erinnerst du dich, Rajaxx? Ich habe dir doch versprochen, dich zuletzt zu töten.', null, null, null, null, null, '0', '1', '0', '0', 'andorov SAY_ANDOROV_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1509005', 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', null, null, 'Die Zeit der Vergeltung ist gekommen! Lasst uns die Herzen unserer Feinde mit Dunkelheit erfüllen!', null, null, null, null, null, '8612', '1', '0', '0', 'rajaxx SAY_WAVE3');
INSERT INTO `script_texts` VALUES ('-1509006', 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!', null, null, 'Wir werden nicht länger hinter verbarrikadierten Toren und Mauern aus Stein verharren! Die Rache wird unser sein! Selbst die Drachen werden im Angesicht unseres Zornes erzittern!', null, null, null, null, null, '8610', '1', '0', '0', 'rajaxx SAY_WAVE4');
INSERT INTO `script_texts` VALUES ('-1509007', 'Fear is for the enemy! Fear and death!', null, null, 'Wir kennen keine Furcht! Und wir werden unseren Feinden den Tod bringen!', null, null, null, null, null, '8608', '1', '0', '0', 'rajaxx SAY_WAVE5');
INSERT INTO `script_texts` VALUES ('-1509008', 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!', null, null, 'Hirschhaupt wird winseln und um sein Leben betteln, genau wie sein räudiger Sohn! Eintausend Jahre der Ungerechtigkeit werden heute enden!', null, null, null, null, null, '8611', '1', '0', '0', 'rajaxx SAY_WAVE6');
INSERT INTO `script_texts` VALUES ('-1509009', 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!', null, null, 'Fandral! Deine Zeit ist gekommen! Geh und verstecke dich im Smaragdgrünen Traum und bete, dass wir dich nie finden werden!', null, null, null, null, null, '8607', '1', '0', '0', 'rajaxx SAY_WAVE7');
INSERT INTO `script_texts` VALUES ('-1509010', 'Impudent fool! I will kill you myself!', null, null, 'Unverschämter Narr! Ich werde euch höchstpersönlich töten!', null, null, null, null, null, '8609', '1', '0', '0', 'rajaxx SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1509011', 'Attack and make them pay dearly!', null, null, 'Angriff! Lasst sie bitter bezahlen!', null, null, null, null, null, '8603', '1', '0', '0', 'rajaxx SAY_UNK1');
INSERT INTO `script_texts` VALUES ('-1509012', 'Crush them! Drive them out!', null, null, 'Vernichtet sie! Treibt sie hinaus!', null, null, null, null, null, '8605', '1', '0', '0', 'rajaxx SAY_UNK2');
INSERT INTO `script_texts` VALUES ('-1509013', 'Do not hesitate! Destroy them!', null, null, 'Zögert nicht! Vernichtet sie alle!', null, null, null, null, null, '8606', '1', '0', '0', 'rajaxx SAY_UNK3');
INSERT INTO `script_texts` VALUES ('-1509014', 'Warriors! Captains! Continue the fight!', null, null, 'Soldaten! Hauptmänner! Gebt den Kampf nicht auf!', null, null, null, null, null, '8613', '1', '0', '0', 'rajaxx SAY_UNK4');
INSERT INTO `script_texts` VALUES ('-1509015', 'You are not worth my time $N!', null, null, 'Ihr seid es nicht wert, meine Zeit zu verschwenden!', null, null, null, null, null, '8614', '1', '0', '0', 'rajaxx SAY_DEAGGRO');
INSERT INTO `script_texts` VALUES ('-1509016', 'Breath your last!', null, null, 'Haucht euren letzten Atemzug aus!', null, null, null, null, null, '8604', '1', '0', '0', 'rajaxx SAY_KILLS_ANDOROV');
INSERT INTO `script_texts` VALUES ('-1509017', 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will be cease!', null, null, 'Bald werdet Ihr den Preis eures Einmischens kennen, Sterbliche... Der Meister ist fast vollständig... Und wenn er aufsteht wird eure Welt enden!', null, null, null, null, null, '0', '1', '0', '0', 'rajaxx SAY_COMPLETE_QUEST');
INSERT INTO `script_texts` VALUES ('-1509018', 'I am rejuvinated!', null, null, 'Ich bin wieder im Vollbesitz meiner Kräfte!', null, null, null, null, null, '8593', '1', '0', '0', 'ossirian SAY_SURPREME1');
INSERT INTO `script_texts` VALUES ('-1509019', 'My powers are renewed!', null, null, 'Meine Kräfte haben sich erneuert!', null, null, null, null, null, '8595', '1', '0', '0', 'ossirian SAY_SURPREME2');
INSERT INTO `script_texts` VALUES ('-1509020', 'My powers return!', null, null, 'Meine Kräfte sind zurückgekehrt!', null, null, null, null, null, '8596', '1', '0', '0', 'ossirian SAY_SURPREME3');
INSERT INTO `script_texts` VALUES ('-1509021', 'Protect the city at all costs!', null, null, 'Beschützt die Stadt um jeden Preis!', null, null, null, null, null, '8597', '1', '0', '0', 'ossirian SAY_RAND_INTRO1');
INSERT INTO `script_texts` VALUES ('-1509022', 'The walls have been breached!', null, null, 'Die Mauern wurden gestürmt!', null, null, null, null, null, '8599', '6', '0', '0', 'ossirian SAY_RAND_INTRO2');
INSERT INTO `script_texts` VALUES ('-1509023', 'To your posts. Defend the city.', null, null, 'Alle auf ihre Posten. Beschützt die Stadt.', null, null, null, null, null, '8600', '1', '0', '0', 'ossirian SAY_RAND_INTRO3');
INSERT INTO `script_texts` VALUES ('-1509024', 'Tresspassers will be terminated.', null, null, 'Eindringlinge werden vernichtet.', null, null, null, null, null, '8601', '1', '0', '0', 'ossirian SAY_RAND_INTRO4');
INSERT INTO `script_texts` VALUES ('-1509025', 'Sands of the desert rise and block out the sun!', null, null, 'Sande der Wüste, erhebt euch und verdunkelt die Sonne!', null, null, null, null, null, '8598', '1', '0', '0', 'ossirian SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1509026', 'You are terminated.', null, null, 'Ihr könnt gehen!', null, null, null, null, null, '8602', '1', '0', '0', 'ossirian SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1509027', 'I...have...failed.', null, null, 'Ich habe versagt.', null, null, null, null, null, '8594', '1', '0', '0', 'ossirian SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1509029', 'Come get some!', null, null, 'Jetzt gibt\'s Prügel!', null, null, null, null, null, '0', '0', '0', '0', 'andorov SAY_ANDOROV_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1509030', 'Kill first, ask questions later... Incoming!', null, null, 'Zuerst töten, dann Fragen stellen... Hier kommen sie!', null, null, null, null, null, '0', '1', '0', '0', 'andorov SAY_ANDOROV_ATTACK_START');
INSERT INTO `script_texts` VALUES ('-1509031', 'I lied...', null, null, 'Ich habe gelogen...', null, null, null, null, null, '0', '1', '0', '0', 'andorov SAY_ANDOROV_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1531000', 'Are you so eager to die? I would be happy to accomodate you.', null, null, 'Seid ihr so begierig darauf zu sterben? Ich werde euch mit Freuden dabei behilflich sein.', null, null, null, null, null, '8615', '1', '0', '0', 'skeram SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1531001', 'Cower mortals! The age of darkness is at hand.', null, null, 'Verneigt euch in Furcht, Sterbliche! Ein Zeitalter der Dunkelheit naht.', null, null, null, null, null, '8616', '1', '0', '0', 'skeram SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1531002', 'Tremble! The end is upon you.', null, null, 'Ihr Narren! Euer Ende ist nah.', null, null, null, null, null, '8621', '1', '0', '0', 'skeram SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1531003', 'Let your death serve as an example!', null, null, 'Möge euer Tod als ein Beispiel dienen!', null, null, null, null, null, '8617', '1', '0', '0', 'skeram SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1531004', 'Spineless wretches! You will drown in rivers of blood!', null, null, 'Kümmerliche Versager! Ihr werdet in Strömen von Blut ertrinken!', null, null, null, null, null, '8619', '1', '0', '0', 'skeram SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1531005', 'The screams of the dying will fill the air. A symphony of terror is about to begin!', null, null, 'Todesschreie sollen die Luft erfüllen. Schon bald wird eine Symphonie des Terrors erklingen!', null, null, null, null, null, '8620', '1', '0', '0', 'skeram SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1531006', 'Prepare for the return of the ancient ones!', null, null, 'Bereitet euch auf die Rückkehr der uralten Götter vor!', null, null, null, null, null, '8618', '1', '0', '0', 'skeram SAY_SPLIT');
INSERT INTO `script_texts` VALUES ('-1531007', 'You only delay... the inevetable.', null, null, 'Ihr zögert... das Unvermeidliche... nur... hinaus.', null, null, null, null, null, '8622', '1', '0', '0', 'skeram SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531008', 'You will be judged for defiling these sacred grounds! The laws of the Ancients will not be challenged! Trespassers will be annihilated!', null, null, 'Ihr habt heiligen Boden entweiht! Seid gewiss, dass ihr dafür gerichtet werdet!', null, null, null, null, null, '8646', '1', '0', '0', 'sartura SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1531009', 'I sentence you to death!', null, null, 'Ich verurteile euch zum Tode!', null, null, null, null, null, '8647', '1', '0', '0', 'sartura SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1531010', 'I serve to the last!', null, null, 'Ich diene... bis zum letzten Atemzug!', null, null, null, null, null, '8648', '1', '0', '0', 'sartura SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531011', '%s is weakened!', null, null, '%s ist geschwächt!', null, null, null, null, null, '0', '2', '0', '0', 'cthun EMOTE_WEAKENED');
INSERT INTO `script_texts` VALUES ('-1531012', 'The massive floating eyeball in the center of the chamber turns its gaze upon you. You stand before a god.', null, null, 'Der riesige, fliegende Augapfel im Zentrum des Raumes dreht sich in Eure Richtung und starrt Euch an. Ihr steht im Angesicht eines Gottes.', null, null, null, null, null, '0', '2', '0', '0', 'eye cthun EMOTE_INTRO');
INSERT INTO `script_texts` VALUES ('-1531013', 'Only flesh and bone. Mortals are such easy prey...', null, null, 'Nur Fleisch und Knochen. Sterbliche sind solch eine leichte Beute...', null, null, null, null, null, '0', '1', '0', '0', 'veklor SAY_INTRO_1');
INSERT INTO `script_texts` VALUES ('-1531014', 'Where are your manners, brother. Let us properly welcome our guests.', null, null, 'Wo sind nur deine Manieren, Bruder. Wir sollten unsere Gäste angemessen willkommen heißen!', null, null, null, null, null, '0', '1', '0', '0', 'veknilash SAY_INTRO_2');
INSERT INTO `script_texts` VALUES ('-1531015', 'There will be pain...', null, null, 'Euch erwartet Schmerz...', null, null, null, null, null, '0', '1', '0', '0', 'veklor SAY_INTRO_3');
INSERT INTO `script_texts` VALUES ('-1531016', 'Oh so much pain...', null, null, 'So viel Schmerz...', null, null, null, null, null, '0', '1', '0', '0', 'veknilash SAY_INTRO_4');
INSERT INTO `script_texts` VALUES ('-1531017', 'Come, little ones.', null, null, 'Kommt, ihr Wichtel.', null, null, null, null, null, '0', '1', '0', '0', 'veklor SAY_INTRO_5');
INSERT INTO `script_texts` VALUES ('-1531018', 'The feast of souls begin now...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'veknilash SAY_INTRO_6');
INSERT INTO `script_texts` VALUES ('-1531019', 'It\'s too late to turn away.', null, null, null, null, null, null, null, null, '8623', '1', '0', '0', 'veklor SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1531020', 'Prepare to embrace oblivion!', null, null, null, null, null, null, null, null, '8626', '1', '0', '0', 'veklor SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1531021', 'Like a fly in a web.', null, null, null, null, null, null, null, null, '8624', '1', '0', '0', 'veklor SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1531022', 'Your brash arrogance!', null, null, null, null, null, null, null, null, '8628', '1', '0', '0', 'veklor SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES ('-1531023', 'You will not escape death!', null, null, 'Vor dem Tod gibt es kein Entrinnen.', null, null, null, null, null, '8629', '1', '0', '0', 'veklor SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1531024', 'My brother...NO!', null, null, null, null, null, null, null, null, '8625', '1', '0', '0', 'veklor SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531025', 'To decorate our halls!', null, null, null, null, null, null, null, null, '8627', '1', '0', '0', 'veklor SAY_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1531026', 'Ah, lambs to the slaughter!', null, null, null, null, null, null, null, null, '8630', '1', '0', '0', 'veknilash SAY_AGGRO_1');
INSERT INTO `script_texts` VALUES ('-1531027', 'Let none survive!', null, null, null, null, null, null, null, null, '8632', '1', '0', '0', 'veknilash SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1531028', 'Join me brother, there is blood to be shed!', null, null, null, null, null, null, null, null, '8631', '1', '0', '0', 'veknilash SAY_AGGRO_3');
INSERT INTO `script_texts` VALUES ('-1531029', 'Look brother, fresh blood!', null, null, null, null, null, null, null, null, '8633', '1', '0', '0', 'veknilash SAY_AGGRO_4');
INSERT INTO `script_texts` VALUES ('-1531030', 'Your fate is sealed!', null, null, 'Euer Schicksal ist besiegelt!', null, null, null, null, null, '8635', '1', '0', '0', 'veknilash SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1531031', 'Vek\'lor, I feel your pain!', null, null, 'Vek\'lor, ich spüre deinen Schmerz!', null, null, null, null, null, '8636', '1', '0', '0', 'veknilash SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1531032', 'Shall be your undoing!', null, null, null, null, null, null, null, null, '8634', '1', '0', '0', 'veknilash SAY_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1531033', 'Death is close...', null, null, null, null, null, null, null, null, '8580', '4', '0', '0', 'cthun SAY_WHISPER_1');
INSERT INTO `script_texts` VALUES ('-1531034', 'You are already dead.', null, null, null, null, null, null, null, null, '8581', '4', '0', '0', 'cthun SAY_WHISPER_2');
INSERT INTO `script_texts` VALUES ('-1531035', 'Your courage will fail.', null, null, null, null, null, null, null, null, '8582', '4', '0', '0', 'cthun SAY_WHISPER_3');
INSERT INTO `script_texts` VALUES ('-1531036', 'Your friends will abandon you.', null, null, null, null, null, null, null, null, '8583', '4', '0', '0', 'cthun SAY_WHISPER_4');
INSERT INTO `script_texts` VALUES ('-1531037', 'You will betray your friends.', null, null, null, null, null, null, null, null, '8584', '4', '0', '0', 'cthun SAY_WHISPER_5');
INSERT INTO `script_texts` VALUES ('-1531038', 'You will die.', null, null, null, null, null, null, null, null, '8585', '4', '0', '0', 'cthun SAY_WHISPER_6');
INSERT INTO `script_texts` VALUES ('-1531039', 'You are weak.', null, null, null, null, null, null, null, null, '8586', '4', '0', '0', 'cthun SAY_WHISPER_7');
INSERT INTO `script_texts` VALUES ('-1531040', 'Your heart will explode.', null, null, null, null, null, null, null, null, '8587', '4', '0', '0', 'cthun SAY_WHISPER_8');
INSERT INTO `script_texts` VALUES ('-1533000', 'Ahh... welcome to my parlor.', null, null, 'Ahh... willkommen in meinem Reich.', null, null, null, null, null, '8788', '1', '0', '0', 'anubrekhan SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533001', 'Just a little taste...', null, null, 'Nur einmal kosten...', null, null, null, null, null, '8785', '1', '0', '0', 'anubrekhan SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533002', 'There is no way out.', null, null, 'Es gibt kein Entkommen.', null, null, null, null, null, '8786', '1', '0', '0', 'anubrekhan SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533003', 'Yes, Run! It makes the blood pump faster!', null, null, 'Rennt! Das bringt das Blut in Wallung!', null, null, null, null, null, '8787', '1', '0', '0', 'anubrekhan SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533004', 'I hear little hearts beating. Yesss... beating faster now. Soon the beating will stop.', null, null, 'Ich höre das Klopfen kleiner Herzen. Ja... sie schlagen schneller. Bald schon werden sie verstummen.', null, null, null, null, null, '8790', '1', '0', '0', 'anubrekhan SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533005', 'Where to go? What to do? So many choices that all end in pain, end in death.', null, null, 'Wohin gehen? Was tun? So viele Möglichkeiten und am Ende wartet doch nur der Tod.', null, null, null, null, null, '8791', '1', '0', '0', 'anubrekhan SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533006', 'Which one shall I eat first? So difficult to choose... the all smell so delicious.', null, null, 'Welchen soll ich nur zu erst verspeisen? Keine leichte Wahl... sie alle duften so köstlich.', null, null, null, null, null, '8792', '1', '0', '0', 'anubrekhan SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533007', 'Closer now... tasty morsels. I\'ve been too long without food. Without blood to drink.', null, null, 'Kommt näher... Ihr kleinen Leckerbissen. Ich habe mich so lange nach Nahrung gesehnt... Nach Blut, um meinen Durst zu stillen.', null, null, null, null, null, '8793', '1', '0', '0', 'anubrekhan SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533008', 'Shh... it will all be over soon.', null, null, 'Schhh... bald ist alles vorbei.', null, null, null, null, null, '8789', '1', '0', '0', 'anubrekhan SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533009', 'Your old lives, your mortal desires, mean nothing. You are acolytes of the master now, and you will serve the cause without question! The greatest glory is to die in the master\'s service!', null, null, 'Euer altes Leben, Eure irdischen Sehnsüchte sind bedeutungslos... Ihr seid jetzt Akolyten des Meisters, und Ihr werdet ihm bedingungslos gehorchen! Die höchste Ehre ist es, im Dienste des Meisters zu sterben!', null, null, null, null, null, '8799', '1', '0', '0', 'faerlina SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533010', 'Slay them in the master\'s name!', null, null, 'Tötet sie im Namen des Meisters!', null, null, null, null, null, '8794', '1', '0', '0', 'faerlina SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533011', 'You cannot hide from me!', null, null, 'Ihr könnt euch nicht vor mir verstecken!', null, null, null, null, null, '8795', '1', '0', '0', 'faerlina SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533012', 'Kneel before me, worm!', null, null, 'Kniet nieder, Wurm!', null, null, null, null, null, '8796', '1', '0', '0', 'faerlina SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533013', 'Run while you still can!', null, null, 'Flieht, solange ihr noch könnt!', null, null, null, null, null, '8797', '1', '0', '0', 'faerlina SAY_AGGRO4');
INSERT INTO `script_texts` VALUES ('-1533014', 'You have failed!', null, null, 'Ihr habt versagt!', null, null, null, null, null, '8800', '1', '0', '0', 'faerlina SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533015', 'Pathetic wretch!', null, null, 'Erbärmliche Kreatur!', null, null, null, null, null, '8801', '1', '0', '0', 'faerlina SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533016', 'The master... will avenge me!', null, null, 'Mein Meister wird mich rächen!!', null, null, null, null, null, '8798', '1', '0', '0', 'faerlina SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533017', 'Patchwerk want to play!', null, null, 'Flickwerk spielen möchte!', null, null, null, null, null, '8909', '1', '0', '0', 'patchwerk SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533018', 'Kel\'Thuzad make Patchwerk his Avatar of War!', null, null, 'Kel\'Thuzad macht Flickwerk zu seinem Abgesandten des Kriegs!', null, null, null, null, null, '8910', '1', '0', '0', 'patchwerk SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533019', 'No more play?', null, null, 'Nicht mehr spielen?', null, null, null, null, null, '8912', '1', '0', '0', 'patchwerk SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533020', 'What happened to... Patch...', null, null, 'Was... sein geschehen...', null, null, null, null, null, '8911', '1', '0', '0', 'patchwerk SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533021', '%s sprays slime across the room!', null, null, '%s sprüht Schleim durch den Raum!', null, null, null, null, null, '0', '3', '0', '0', 'grobbulus EMOTE_SPRAY_SLIME');
INSERT INTO `script_texts` VALUES ('-1533022', '%s lifts off into the air!', null, null, '%s erhebt sich in die Lüfte!', null, null, null, null, null, '0', '3', '0', '0', 'sapphiron EMOTE_FLY');
INSERT INTO `script_texts` VALUES ('-1533023', 'Stalagg crush you!', null, null, 'Stalagg zerquetschen!', null, null, null, null, null, '8864', '1', '0', '0', 'stalagg SAY_STAL_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533024', 'Stalagg kill!', null, null, 'Stalagg töten!', null, null, null, null, null, '8866', '1', '0', '0', 'stalagg SAY_STAL_SLAY');
INSERT INTO `script_texts` VALUES ('-1533025', 'Master save me...', null, null, 'Meister mich retten...', null, null, null, null, null, '8865', '1', '0', '0', 'stalagg SAY_STAL_DEATH');
INSERT INTO `script_texts` VALUES ('-1533026', 'Feed you to master!', null, null, 'Verfüttere euch an Meister!', null, null, null, null, null, '8802', '1', '0', '0', 'feugen SAY_FEUG_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533027', 'Feugen make master happy!', null, null, 'Feugen macht Meister glücklich!', null, null, null, null, null, '8804', '1', '0', '0', 'feugen SAY_FEUG_SLAY');
INSERT INTO `script_texts` VALUES ('-1533028', 'No... more... Feugen...', null, null, 'Kein... Feugen... mehr...', null, null, null, null, null, '8803', '1', '0', '0', 'feugen SAY_FEUG_DEATH');
INSERT INTO `script_texts` VALUES ('-1533029', 'You are too late... I... must... OBEY!', null, null, 'Ihr seid zu spät... Ich... muss.. gehorchen!', null, null, null, null, null, '8872', '1', '0', '0', 'thaddius SAY_GREET');
INSERT INTO `script_texts` VALUES ('-1533030', 'KILL!', null, null, 'TÖTEN!', null, null, null, null, null, '8867', '1', '0', '0', 'thaddius SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533031', 'EAT YOUR BONES!', null, null, 'Eure... Knochen... zermalmen...', null, null, null, null, null, '8868', '1', '0', '0', 'thaddius SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533032', 'BREAK YOU!', null, null, 'EUCH ZERQUETSCHEN!', null, null, null, null, null, '8869', '1', '0', '0', 'thaddius SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533033', 'You die now!', null, null, 'Ihr werdet jetzt... sterben!', null, null, null, null, null, '8877', '1', '0', '0', 'thaddius SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533034', 'Now YOU feel pain!', null, null, 'Jetzt spürt ihr den Schmerz...', null, null, null, null, null, '8871', '1', '0', '0', 'thaddius SAY_ELECT');
INSERT INTO `script_texts` VALUES ('-1533035', 'Thank... you...', null, null, 'Euch... dankbar sein!', null, null, null, null, null, '8870', '1', '0', '0', 'thaddius SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533036', 'Pleeease!', null, null, 'Bitteee!', null, null, null, null, null, '8873', '1', '0', '0', 'thaddius SAY_SCREAM1');
INSERT INTO `script_texts` VALUES ('-1533037', 'Stop, make it stop!', null, null, 'Aufhören, bitte aufhören!', null, null, null, null, null, '8874', '1', '0', '0', 'thaddius SAY_SCREAM2');
INSERT INTO `script_texts` VALUES ('-1533038', 'Help me! Save me!', null, null, 'Hilfe! So helft mir doch!', null, null, null, null, null, '8875', '1', '0', '0', 'thaddius SAY_SCREAM3');
INSERT INTO `script_texts` VALUES ('-1533039', 'Please, nooo!', null, null, 'Bitte, neeein!', null, null, null, null, null, '8876', '1', '0', '0', 'thaddius SAY_SCREAM4');
INSERT INTO `script_texts` VALUES ('-1533040', 'Foolishly you have sought your own demise.', null, null, 'Ihr Narren habt euren eigenen Untergang heraufbeschworen. Voller Leichtsinn habt ihr euch mit Kräften angelegt, die weit über euren Verstand hinausgehen. Tapfer seid ihr in das Reich des Seelenjägers vorgedrungen. Doch jetzt gibt es nur noch einen Ausweg - den einsamen Pfad der Verdammten.', null, null, null, null, null, '8807', '1', '0', '0', 'gothik SAY_SPEECH_1');
INSERT INTO `script_texts` VALUES ('-1533041', 'Death is the only escape.', null, null, 'Tod ist der einzige Ausweg!', null, null, null, null, null, '8806', '1', '0', '0', 'gothik SAY_KILL');
INSERT INTO `script_texts` VALUES ('-1533042', 'I... am... undone!', null, null, 'Das... ist... mein... Ende.', null, null, null, null, null, '8805', '1', '0', '0', 'gothik SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533043', 'I have waited long enough! Now, you face the harvester of souls!', null, null, 'Ich habe lange genug gewartet. Stellt euch dem Seelenjäger.', null, null, null, null, null, '8808', '1', '0', '0', 'gothik SAY_TELEPORT');
INSERT INTO `script_texts` VALUES ('-1533044', 'Defend youself!', null, null, 'Verteidigt euch!', null, null, null, null, null, '8892', '1', '0', '0', 'blaumeux SAY_BLAU_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533045', 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun.', null, null, 'Zeliek, tötet sie noch nicht. Erst haben wir noch unseren Spaß mit ihnen!', null, null, null, null, null, '8896', '6', '0', '0', 'blaumeux SAY_BLAU_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533046', 'I do hope they stay alive long enough for me to... introduce myself.', null, null, 'Ich hoffe sie bleiben lange genug am Leben... damit ich mich vorstellen kann.', null, null, null, null, null, '8897', '6', '0', '0', 'blaumeux SAY_BLAU_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533047', 'The first kill goes to me! Anyone care to wager?', null, null, 'Ich töte den Ersten! Wer wette dagegen?', null, null, null, null, null, '8898', '6', '0', '0', 'blaumeux SAY_BLAU_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533048', 'Your life is mine!', null, null, 'Euer Leben gehört mir!', null, null, null, null, null, '8895', '1', '0', '0', 'blaumeux SAY_BLAU_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533049', 'Who\'s next?', null, null, 'Wer ist der Nächste?', null, null, null, null, null, '8894', '1', '0', '0', 'blaumeux SAY_BLAU_SLAY');
INSERT INTO `script_texts` VALUES ('-1533050', 'Tou... che!', null, null, null, null, null, null, null, null, '8893', '1', '0', '0', 'blaumeux SAY_BLAU_DEATH');
INSERT INTO `script_texts` VALUES ('-1533051', 'Come out and fight, ye wee ninny!', null, null, 'Kommt und kämpft, ihr Wichte!', null, null, null, null, null, '8899', '1', '0', '0', 'korthazz SAY_KORT_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533052', 'To arms, ye roustabouts! We\'ve got company!', null, null, 'Zu den Waffen, Ihr Schmarotzer! Wir haben Gesellschaft!', null, null, null, null, null, '8903', '6', '0', '0', 'korthazz SAY_KORT_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533053', 'I heard about enough of yer sniveling. Shut yer fly trap \'afore I shut it for ye!', null, null, 'Ich hab\' genug von eurem Geflenne. Haltet die Klappe oder ich stopfe euch das Maul!', null, null, null, null, null, '8904', '6', '0', '0', 'korthazz SAY_KORT_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533054', 'I\'m gonna enjoy killin\' these slack-jawed daffodils!', null, null, 'Ich werde es genießen diese Waschlappen aus dem Weg zu räumen!', null, null, null, null, null, '8905', '6', '0', '0', 'korthazz SAY_KORT_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533055', 'I like my meat extra crispy!', null, null, 'Ich mag mein Fleisch besonders knusprig!', null, null, null, null, null, '8902', '1', '0', '0', 'korthazz SAY_KORT_SPECIAl');
INSERT INTO `script_texts` VALUES ('-1533056', 'Next time, bring more friends!', null, null, 'Bringt das nächste Mal mehr Freunde mit!', null, null, null, null, null, '8901', '1', '0', '0', 'korthazz SAY_KORT_SLAY');
INSERT INTO `script_texts` VALUES ('-1533057', 'What a bloody waste this is!', null, null, 'Was für eine verdammte Verschwendung!', null, null, null, null, null, '8900', '1', '0', '0', 'korthazz SAY_KORT_DEATH');
INSERT INTO `script_texts` VALUES ('-1533058', 'Flee, before it\'s too late!', null, null, 'Flieht, bevor es zu spät ist!', null, null, null, null, null, '8913', '1', '0', '0', 'zeliek SAY_ZELI_AGGRO');
INSERT INTO `script_texts` VALUES ('-1533059', 'Invaders, cease this foolish venture at once! Turn away while you still can!', null, null, 'Eindringlinge, macht diesem törichten Unterfangen ein für alle Mal ein Ende! Kehrt um!', null, null, null, null, null, '8917', '6', '0', '0', 'zeliek SAY_ZELI_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533060', 'Perhaps they will come to their senses, and run away as fast as they can!', null, null, 'Vielleicht kommen sie ja noch zur Vernunft... und fliehen so schnell sie können.', null, null, null, null, null, '8918', '6', '0', '0', 'zeliek SAY_ZELI_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533061', 'Do not continue! Turn back while there\'s still time!', null, null, 'Hört auf! Kehrt zurück! Kehrt zurück solange ihr noch könnt!', null, null, null, null, null, '8919', '6', '0', '0', 'zeliek SAY_ZELI_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533062', 'I- I have no choice but to obey!', null, null, 'Ich habe keine Wahl!', null, null, null, null, null, '8916', '1', '0', '0', 'zeliek SAY_ZELI_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533063', 'Forgive me!', null, null, 'Vergebt mir!', null, null, null, null, null, '8915', '1', '0', '0', 'zeliek SAY_ZELI_SLAY');
INSERT INTO `script_texts` VALUES ('-1533064', 'It is... as it should be.', null, null, 'Es verläuft... ganz nach Plan.', null, null, null, null, null, '8914', '1', '0', '0', 'zeliek SAY_ZELI_DEATH');
INSERT INTO `script_texts` VALUES ('-1533065', 'You seek death?', null, null, 'Sucht Ihr den Tod?', null, null, null, null, null, '8835', '1', '0', '0', 'mograine SAY_MORG_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533066', 'None shall pass!', null, null, 'Keiner darf passieren!', null, null, null, null, null, '8836', '1', '0', '0', 'mograine SAY_MORG_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533067', 'Be still!', null, null, 'Schweigt!', null, null, null, null, null, '8837', '1', '0', '0', 'mograine SAY_MORG_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533068', 'You will find no peace in death.', null, null, 'Im Tod werdet Ihr keinen Frieden finden!', null, null, null, null, null, '8839', '1', '0', '0', 'mograine SAY_MORG_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533069', 'The master\'s will is done.', null, null, 'Des Meisters Wille ist geschehen!', null, null, null, null, null, '8840', '1', '0', '0', 'mograine SAY_MORG_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533070', 'Bow to the might of the Highlord!', null, null, 'Verbeugt Euch vor der Macht der Geißel!', null, null, null, null, null, '8841', '1', '0', '0', 'mograine SAY_MORG_SPECIAL');
INSERT INTO `script_texts` VALUES ('-1533071', 'Enough prattling. Let them come! We shall grind their bones to dust.', null, null, 'Genug mit dem Geschwätz. Lasst sie nur kommen. Wir werden ihre Knochen zu Staub zermalmen.', null, null, null, null, null, '8842', '6', '0', '0', 'mograine SAY_MORG_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533072', 'Conserve your anger! Harness your rage! You will all have outlets for your frustration soon enough.', null, null, 'Bewahrt Euch Euren Zorn! Doch haltet Eure Wut in Zaum! Ihr alle werdet Eure Missmut schon noch früh genug freien Lauf lassen können!', null, null, null, null, null, '8843', '6', '0', '0', 'mograine SAY_MORG_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533073', 'Life is meaningless. It is in death that we are truly tested.', null, null, 'Das Leben ist bedeutungslos. Der Tod ist die wahre Prüfung.', null, null, null, null, null, '8844', '6', '0', '0', 'mograine SAY_MORG_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533074', 'I...am...released! Perhaps it\'s not too late to ', null, null, 'Der Tod... hält mich nicht auf...', null, null, null, null, null, '8838', '1', '0', '0', 'mograine SAY_MORG_DEATH');
INSERT INTO `script_texts` VALUES ('-1533075', 'Glory to the master!', null, null, 'Ehre unserem Meister!', null, null, null, null, null, '8845', '1', '0', '0', 'noth SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533076', 'Your life is forfeit!', null, null, 'Euer Leben ist verwirkt!', null, null, null, null, null, '8846', '1', '0', '0', 'noth SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533077', 'Die, trespasser!', null, null, 'Sterbt, Eindringling!', null, null, null, null, null, '8847', '1', '0', '0', 'noth SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533078', 'Rise, my soldiers! Rise and fight once more!', null, null, 'Erhebt Euch, Soldaten! Erhebt euch und kämpft erneut!', null, null, null, null, null, '8851', '1', '0', '0', 'noth SAY_SUMMON');
INSERT INTO `script_texts` VALUES ('-1533079', 'My task is done!', null, null, 'Meine Pflicht ist getan!', null, null, null, null, null, '8849', '1', '0', '0', 'noth SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533080', 'Breathe no more!', null, null, 'Sterbt!', null, null, null, null, null, '8850', '1', '0', '0', 'noth SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533081', 'I will serve the master... in... death!', null, null, 'Ich werde dem Meister über den Tod hinaus dienen!', null, null, null, null, null, '8848', '1', '0', '0', 'noth SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533082', '%s takes in a deep breath...', null, null, '%s atmet tief ein...', null, null, null, null, null, '0', '3', '0', '0', 'sapphiron EMOTE_BREATH');
INSERT INTO `script_texts` VALUES ('-1533083', '%s resumes his attacks!', null, null, '%s nimmt seine Angriffe wieder auf!', null, null, null, null, null, '0', '3', '0', '0', 'sapphiron EMOTE_GROUND');
INSERT INTO `script_texts` VALUES ('-1533084', 'Our preparations continue as planned master.', null, null, 'Eure Streitkräfte sind bald bereit, gegen Eure Feinde vorzugehen, mein Gebieter.', null, null, null, null, null, '8878', '6', '0', '0', 'kelthuzad SAY_SAPP_DIALOG1');
INSERT INTO `script_texts` VALUES ('-1533085', 'It is good that you serve me so faithfully. Soon... all will serve the Lich King and in the end, you shall be rewarded. So long as you do not falter.', null, null, 'Schon bald löschen wir Allianz und Horde aus. Danach wird der Rest Azeroths sich der Macht meiner Armee beugen', null, null, null, null, null, '8881', '6', '0', '0', 'lich_king SAY_SAPP_DIALOG2_LICH');
INSERT INTO `script_texts` VALUES ('-1533086', 'I foresee no complications at this... wait! What is this!?', null, null, 'Ja, Meister. Die Zeit ihres endgültigen Untergangs rückt näher... Was, was ist das?', null, null, null, null, null, '8879', '6', '0', '0', 'kelthuzad SAY_SAPP_DIALOG3');
INSERT INTO `script_texts` VALUES ('-1533087', 'Your security measures have failed! See to this interruption immediately!', null, null, 'Eindringlinge... hier?! VERNICHTET sie, Kel\'Thuzad! Naxxramas darf nicht fallen!', null, null, null, null, null, '8882', '6', '0', '0', 'lich_king SAY_SAPP_DIALOG4_LICH');
INSERT INTO `script_texts` VALUES ('-1533088', 'Yes, master!', null, null, 'Wie Ihr befehlt, Meister!', null, null, null, null, null, '8880', '6', '0', '0', 'kelthuzad SAY_SAPP_DIALOG5');
INSERT INTO `script_texts` VALUES ('-1533089', 'No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate...', null, null, 'Nein!!! Ich verfluche euch, Eindringlinge! Die Armeen des Lichkönigs werden euch jagen und vernichten. Ihr werdet eurem Schicksal nicht entrinnen...', null, null, null, null, null, '0', '6', '0', '0', 'kelthuzad SAY_CAT_DIED');
INSERT INTO `script_texts` VALUES ('-1533090', 'Who dares violate the sanctity of my domain? Be warned, all who trespass here are doomed.', null, null, 'Wer wagt es, die Heiligkeit meiner Hallen zu entweihen? Seid gewarnt: Jeder Eindringling ist verdammt!', null, null, null, null, null, '8820', '6', '0', '0', 'kelthuzad SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533091', 'Fools, you think yourselves triumphant? You have only taken one step closer to the abyss! ', null, null, 'Narren, Ihr nennt Euch siegreich? Ihr seit dem Abgrund jeglich einen Schritt näher gekommen!', null, null, null, null, null, '8821', '6', '0', '0', 'kelthuzad SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533092', 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', null, null, 'Eure Spielchen langweilen mich. Fahrt fort, und ich werde eure Seelen der Vergessenheit überantworten.', null, null, null, null, null, '8822', '6', '0', '0', 'kelthuzad SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533093', 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', null, null, 'Ihr ahnt ja nicht, welche Schrecken noch vor Euch liegen. Das Erlebte war erst der Anfang! Das eisige Herz Naxxramas erwartet Euch!', null, null, null, null, null, '8823', '6', '0', '0', 'kelthuzad SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533094', 'Pray for mercy!', null, null, 'Betet um Gnade!', null, null, null, null, null, '8809', '1', '0', '0', 'kelthuzad SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533095', 'Scream your dying breath!', null, null, 'Schreiend werdet Ihr diese Welt verlassen!', null, null, null, null, null, '8810', '1', '0', '0', 'kelthuzad SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533096', 'The end is upon you!', null, null, 'Euer Ende ist gekommen!', null, null, null, null, null, '8811', '1', '0', '0', 'kelthuzad SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533097', 'The dark void awaits you!', null, null, 'Die dunkle Leere wartet!', null, null, null, null, null, '8817', '1', '0', '0', 'kelthuzad SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533098', '<Kel\'Thuzad cackles maniacally!>', null, null, '<Kel\'Thuzad gackert wie verrückt!>', null, null, null, null, null, '8818', '1', '0', '0', 'kelthuzad SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533099', 'AAAAGHHH!... Do not rejoice... your victory is a hollow one... for I shall return with powers beyond your imagining!', null, null, 'Argh! Freut euch... nicht... zu früh! Euer Sieg ist bedeutungslos, denn ich werde mit Kräften weit jenseits eurer Vorstellungskraft zurückkehren!', null, null, null, null, null, '8814', '1', '0', '0', 'kelthuzad SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533100', 'Your soul, is bound to me now!', null, null, 'Eure Seele gehört jetzt mir!', null, null, null, null, null, '8812', '1', '0', '0', 'kelthuzad SAY_CHAIN1');
INSERT INTO `script_texts` VALUES ('-1533101', 'There will be no escape!', null, null, 'Es gibt kein Entrinnen!', null, null, null, null, null, '8813', '1', '0', '0', 'kelthuzad SAY_CHAIN2');
INSERT INTO `script_texts` VALUES ('-1533102', 'I will freeze the blood in your veins!', null, null, 'Ich werde das Blut in Euren Adern gefrieren lassen!', null, null, null, null, null, '8815', '1', '0', '0', 'kelthuzad SAY_FROST_BLAST');
INSERT INTO `script_texts` VALUES ('-1533103', 'Master! I require aid! ', null, null, 'Meister, ich benötige Beistand.', null, null, null, null, null, '8816', '1', '0', '0', 'kelthuzad SAY_REQUEST_AID');
INSERT INTO `script_texts` VALUES ('-1533104', 'Very well... warriors of the frozen wastes, rise up! I command you to fight, kill, and die for your master. Let none survive...', null, null, 'Wohlan, Krieger der Eisigen Weiten, erhebt euch! Ich befehle euch für euren Meister zu kämpfen, zu töten und zu sterben! Keiner darf überleben!', null, null, null, null, null, '8824', '1', '0', '0', 'kelthuzad SAY_ANSWER_REQUEST');
INSERT INTO `script_texts` VALUES ('-1533105', 'Minions, servants, soldiers of the cold dark, obey the call of Kel\'Thuzad!', null, null, 'Lakaien, Diener, Soldaten der eisigen Finsternis! Folg dem Ruf von Kel\'Thuzad!', null, null, null, null, null, '8819', '1', '0', '0', 'kelthuzad SAY_SUMMON_MINIONS');
INSERT INTO `script_texts` VALUES ('-1533106', 'Your petty magics are no challenge to the might of the Scourge! ', null, null, 'Eure armselige Magie kann der Macht der Geißel niemals kontollieren!', null, null, null, null, null, '9088', '1', '0', '0', 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT INTO `script_texts` VALUES ('-1533107', 'Enough! I grow tired of these distractions! ', null, null, 'Genug! Diese Ablenkungen ermüden mich!', null, null, null, null, null, '9090', '1', '0', '0', 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT INTO `script_texts` VALUES ('-1533108', 'Fools, you have spread your powers too thin. Be free, my minions!', null, null, 'Narren, Ihr habt Euch zu sehr verausgabt. Befreit euch, meine Diener!', null, null, null, null, null, '9089', '1', '0', '0', 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT INTO `script_texts` VALUES ('-1533109', 'You are mine now!', null, null, 'Ihr gehört mir!', null, null, null, null, null, '8825', '1', '0', '0', 'heigan SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533110', 'I see you!', null, null, 'Ihr entgeht mir nicht...', null, null, null, null, null, '8826', '1', '0', '0', 'heigan SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533111', 'You...are next!', null, null, 'Ihr seid... als nächstes dran.', null, null, null, null, null, '8827', '1', '0', '0', 'heigan SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533112', 'Close your eyes... sleep!', null, null, 'Schließt eure Augen. Schlaft.', null, null, null, null, null, '8829', '1', '0', '0', 'heigan SAY_SLAY');
INSERT INTO `script_texts` VALUES ('-1533113', 'The races of the world will perish. It is only a matter of time.', null, null, 'Die Völker dieser Welt werden zu grunde gehen. Es ist nur eine Frage der Zeit.', null, null, null, null, null, '8830', '1', '0', '0', 'heigan SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1533114', 'I see endless suffering, I see torment, I see rage. I see... everything!', null, null, 'Ich sehe endloses Leiden. Ich sehe Qualen. Ich sehe Zorn. Ich sehe... alles!', null, null, null, null, null, '8831', '1', '0', '0', 'heigan SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1533115', 'Soon... the world will tremble!', null, null, 'Schon bald... wird die Welt erzittern!', null, null, null, null, null, '8832', '1', '0', '0', 'heigan SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1533116', 'The end is upon you.', null, null, 'Euer Ende naht.', null, null, null, null, null, '8833', '1', '0', '0', 'heigan SAY_CHANNELING');
INSERT INTO `script_texts` VALUES ('-1533117', 'Hungry worms will feast on your rotten flesh!', null, null, 'Maden werden sich Eurem faulenden Fleisch laben!', null, null, null, null, null, '8834', '1', '0', '0', 'heigan SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1533118', 'Noo... o...', null, null, 'Arghh... h...', null, null, null, null, null, '8828', '1', '0', '0', 'heigan SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533119', '%s spots a nearby Zombie to devour!', null, null, '%s erspäht einen Zombie zum Verschlingen!', null, null, null, null, null, '0', '3', '0', '0', 'gluth EMOTE_ZOMBIE');
INSERT INTO `script_texts` VALUES ('-1533120', 'Hah hah, I\'m just getting warmed up!', null, null, 'Ha ha, ich fange gerade erst an!', null, null, null, null, null, '8852', '1', '0', '0', 'razuvious SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1533121', 'Stand and fight!', null, null, 'Stellt euch und kämpft!', null, null, null, null, null, '8853', '1', '0', '0', 'razuvious SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1533122', 'Show me what you\'ve got!', null, null, 'Zeigt mir, was ihr könnt!', null, null, null, null, null, '8854', '1', '0', '0', 'razuvious SAY_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1533123', 'Sweep the leg! Do you have a problem with that?', null, null, 'Ihr hättet daheim bleiben sollen!', null, null, null, null, null, '8861', '1', '0', '0', 'razuvious SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1533124', 'You should have stayed home!', null, null, 'Jaaa...', null, null, null, null, null, '8862', '1', '0', '0', 'razuvious SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1533125', 'Do as I taught you!', null, null, 'Befolgt meine Befehle!', null, null, null, null, null, '8855', '1', '0', '0', 'razuvious SAY_COMMAND1');
INSERT INTO `script_texts` VALUES ('-1533126', 'Show them no mercy!', null, null, 'Lasst kein Gnade walten!', null, null, null, null, null, '8856', '1', '0', '0', 'razuvious SAY_COMMAND2');
INSERT INTO `script_texts` VALUES ('-1533127', 'You disappoint me, students!', null, null, 'Ihr enttäuscht mich, meine Schüler!', null, null, null, null, null, '8858', '1', '0', '0', 'razuvious SAY_COMMAND3');
INSERT INTO `script_texts` VALUES ('-1533128', 'The time for practice is over! Show me what you\'ve learned!', null, null, 'Die Zeit des Übens ist vorbei! Zeigt mir was Ihr gelernt habt!', null, null, null, null, null, '8859', '1', '0', '0', 'razuvious SAY_COMMAND4');
INSERT INTO `script_texts` VALUES ('-1533129', 'An honorable... death...', null, null, 'Ein ehrenhafter... Tod.', null, null, null, null, null, '8860', '1', '0', '0', 'razuvious SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1533130', '%s summons forth Skeletal Warriors!', null, null, '%s beschwört Skelettkrieger!', null, null, null, null, null, '0', '3', '0', '0', 'noth EMOTE_WARRIOR');
INSERT INTO `script_texts` VALUES ('-1533131', '%s raises more skeletons!', null, null, '%s belebt mehr Skelette!', null, null, null, null, null, '0', '3', '0', '0', 'noth EMOTE_SKELETON');
INSERT INTO `script_texts` VALUES ('-1533132', '%s teleports to the balcony above!', null, null, '%s teleportiert sich nach oben auf den Balkon!', null, null, null, null, null, '0', '3', '0', '0', 'noth EMOTE_TELEPORT');
INSERT INTO `script_texts` VALUES ('-1533133', '%s teleports back into the battle!', null, null, '%s teleportiert sich zurück in den Kampf!', null, null, null, null, null, '0', '3', '0', '0', 'noth EMOTE_TELEPORT_RETURN');
INSERT INTO `script_texts` VALUES ('-1533134', 'A Guardian of Icecrown enters the fight!', null, null, 'Ein Wächter der Eiskrone beteiligt sich am Kampf!', null, null, null, null, null, '0', '3', '0', '0', 'kelthuzad EMOTE_GUARDIAN');
INSERT INTO `script_texts` VALUES ('-1533135', '%s strikes!', null, null, '%s schlägt zu!', null, null, null, null, null, '0', '3', '0', '0', 'kelthuzad EMOTE_PHASE2');
